@-moz-document domain("www.duolingo.com") {
/* Variables */
:root {
    --elevation-2dp: 0px 3px 1px -2px rgba(0, 0, 0, 0.1), 
        0px 2px 2px 0px rgba(0, 0, 0, 0.07),
        0px 1px 5px 0px rgba(0, 0, 0, 0.06) !important;
    --elevation-4dp: 0px 2px 4px -1px rgba(0, 0, 0, 0.1),
        0px 4px 5px 0px rgba(0, 0, 0, 0.07),
        0px 1px 10px 0px rgba(0, 0, 0, 0.06) !important;
    --elevation-8dp: 0px 5px 5px -3px rgba(0, 0, 0, 0.1), 
        0px 8px 10px 1px rgba(0, 0, 0, 0.07), 
        0px 3px 14px 2px rgba(0, 0, 0, 0.06) !important;

    --transition-params: 280ms cubic-bezier(0.4, 0, 0.2, 1);
}

/* Card */
._1E3L7, 
.page-main, 
.box-colored, 
.ReactModal__Content {
    border-radius: 2px !important;
    box-shadow: var(--elevation-2dp) !important;
}

.zmu4B {
    border-radius: 2px !important;
    box-shadow: var(--elevation-4dp) !important;
}

._1sdh6 {
    border-radius: 2px 2px 0 0 !important;
}

.OtLta {
    border-radius: 0 0 2px 2px !important;
}

.box-gray, 
.a5SW0 {
    border-radius: 3px !important;
}

/* Toolbar */
.topbar, 
._6t5Uh {
    box-shadow: var(--elevation-4dp) !important;
}

.topbar-nav, 
.zDDkq {
    margin-top: 0 !important;
}

.topbar-nav-main > li a,
._2QyU5 {
    border-radius: 0 !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    height: 70px !important;
    line-height: 70px !important;
    transition: background var(--transition-params) !important;
}

.topbar-nav-main > li a:hover,
._2QyU5:hover {
    background: rgba(0, 0, 0, 0.04) !important;
}

.topbar-nav-main > li.active a,
.uWoNt {
    background: none !important;
    border-bottom: 4px solid #fff !important;
}

/* Dropdown */
.dropdown-menu, 
._1ZY-H {
    border-radius: 2px !important;
    box-shadow: var(--elevation-8dp) !important;
}

/* Buttons */
.btn-outline, 
._1XnsG, 
.o-DI4 {
    border-radius: 3px !important;
}

.btn:not(.btn-outline):not(.btn-custom):not(.clear-button),
._3IS_q:not(._1XnsG),
._3cu46:not(.o-DI4):not(._2etd0) {
    border-radius: 2px !important;
    transition: box-shadow var(--transition-params), 
        background-color var(--transition-params),
        color var(--transition-params) !important;
    box-shadow: var(--elevation-2dp) !important;
}

.btn:not(.btn-outline):not(.btn-custom):not(.clear-button):hover,
._3IS_q:not(._1XnsG):hover,
._3cu46:not(.o-DI4):not(._2etd0):hover {
    box-shadow: var(--elevation-4dp) !important;
}

.btn:not(.btn-outline):not(.btn-custom):not(.clear-button):active,
._3IS_q:not(._1XnsG):active,
._3cu46:not(.o-DI4):not(._2etd0):active {
    box-shadow: var(--elevation-8dp) !important;
}

