css:
:root{
  
  /* ----- BEGIN OF COLORS ---- */
  
  /* Brand orange colors. */
  --BrandOrangeL: #F96A4B;
  --BrandOrange: #f95c39;
  --BrandOrangeD: #CC4C2F;

  /* Brand blue colors. */
  --BrandBlueL: #4B93B1;
  --BrandBlue: #247BA0;
  --BrandBlueD: #1B5A75;

  /* Grey palette. */
  --GreyDD: #313233;
  --GreyD: #929699;
  --Grey: #C4C9CC;
  --GreyL: #DCE1E5;
  --GreyLL: #E8ECED;
  --GreyLLL: #EDF2F4;
  --White: #FFFFFF;
  
  
  /* Secondary palette. */
  --Red: #C75146;
  --RedL: #EFCFCC;
  --RedLL: #F9EFEE;
  
  --Green: #44AF69;
  --GreenL: #CCE9D6;
  --GreenLL: #EEF7F1;
  
  --Yellow: #FCAB10;
  --YellowL: #FEE7C0;
  --YellowLL: #FEF7E9;
  
  --Blue: #39A9DB;
  --BlueL: #C9E7F5;
  --BlueLL: #EDF7FB;
  
  --Purple: #834555;
  --PurpleL: #DDCCD0;
  --PurpleLL: #F3EEEF;
  
  --Pink: #EF7674;
  --PinkL: #FAD9D9;
  --PinkLL: #FDF2F2;
  
  /* ---- END OF COLORS ---- */
  
  --root: 16;
  --fontFamily:  'Open Sans', Helvetica, Arial, sans-serif;
  
  --?: 4.3125rem / 5.5rem var(--fontFamily);
  --?: 2.875rem / 3.5rem var(--fontFamily);
	--?: 2.125rem / 3rem var(--fontFamily);
  --?: 1.6875rem / 2.5rem var(--fontFamily);
  --?: 1.3125rem / 2rem var(--fontFamily); 
	--?: 1.188rem / 1.5rem var(--fontFamily);
	--?: 600 1.0625rem / 1.5rem var(--fontFamily);
	--?: 600 0.9375rem / 1.5rem var(--fontFamily);
  
  --dino: var(--?);                                        
  --elephant: var(--?);
  --camel: var(--?);
  --panda: var(--?);
  --chimp: var(--?);
  --koala: var(--?);
  --cat: var(--?);
  --duck: var(--?);
}

/* ---- Small & Medium breakpoint ---- */
@media all and (max-width: 768px) {
  :root{
    --?: 1.6875rem / 2.5rem  var(--fontFamily);
    --?: 2.125rem / 3rem  var(--fontFamily);
    --?: 1.6875rem / 2.5rem  var(--fontFamily);
    --?: 1.3125rem / 2rem  var(--fontFamily);
    --?: 1.188rem / 1.5rem  var(--fontFamily);
    --?: 600 1.0625rem / 1.5rem  var(--fontFamily);
    --?: 600 0.9375rem / 1.5rem  var(--fontFamily);
  }
}
