css:
@-moz-document regexp("https?://www.google.([a-z]{2,3})(.[a-z][a-z]?)?/imghp(.*)"),
regexp("https?://www.google.([a-z]{2,3})(.[a-z][a-z]?)?/?(\\?gws_rd=ssl)?") {
    html body {
        background-image: url(https://i.imgur.com/jm5k0ok.png);
        background-repeat: no-repeat;
        background-position: center top;
        background-size: cover;
        background-attachment: fixed;
        font-family: "Microsoft YaHei";
    }
    #hdtbSum {
        background: transparent;
    }
    #hdtb {
        background: rgba(0, 0, 0, 0);
        border-bottom: 1px solid rgba(0, 0, 0, 0);
    }
    .sfbgx {
              border-bottom: none;
    }
    .sfbgg,
    #appbar {
        background: rgba(0, 0, 0, 0);
    }
    #fbar {
        background: rgba(0, 0, 0, 0);
        border-top: none;
    }
    .sbib_a {
        background: rgba(0, 0, 0, 0);
    }
    .sbibod {
        background: rgba(0, 0, 0, 0);
        
    }
    .sbibod:focus .sbib_a {
        background: rgba(0, 0, 0, 0);
    }
    #rhs .kp-blk {
        background: rgba(0, 0, 0, 0);
    }
    .sbico {
        color: #EE82EE;
    }
    .sbico-c {
        background: rgba(0, 0, 0, 0);
        top: 1px;
    }
    #hplogo,
    body:not(.hp) #lga {
        display: none !important;
    }
    #lga {
        background: url("https://steamuserimages-a.akamaihd.net/ugc/223319733433847338/C1C69FD0A766F67F66CCC0CB13E3F7B89C04B225/") no-repeat center 109px !important;
        background-size: 282px 102px !important;
    }
    #logo>*,
    #logocont>* {
        opacity: 0 !important;
    }
}
