css:
@-moz-document url-prefix("https://avenoel.org/")
{
html
  {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;
    background-image: url(https://image.noelshack.com/fichiers/2018/07/3/1518625907-1257.png);
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
  }

  body
  {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;
    background: rgba(255, 255, 255, 0);
  }

  main.container .container-content section
  {
    background-color: rgba(26, 26, 26, 0.8);
  }

  tbody
  {
    background-color: rgb(26, 26, 26);
  }

  td
  {
    background-color: rgba(26, 26, 26, 0.92);
  }

  tr
  {
    background-color: rgb(26, 26, 26);
  }

  .breadcrumb
  {
    background-color: rgb(26, 26, 26);
  }

  tr.active, tr.active td
  {
    background-color: rgb(128, 0, 0) !important;
  }

  .btn.btn-primary
  {
    background-color: rgb(128, 0, 0) !important;
  }

  .navbar li.active a
  {
    background-color: rgb(128, 0, 0) !important;
  }

  .bloc-title
  {
    color: white;
  }

  label
  {
    color: white;
  }

  a, th, .topics-author, .topics-amount, .topics-date, .topic-title
  {
    color: white;
  }

  .breadcrumb li.active
  {
    color: white !important;
  }

  .row.topic-message.odd
  {
    background-color: rgba(26, 26, 26, 0.9);
  }

  .row.topic-message
  {
    background-color: rgba(45, 45, 45, 0.9);
  }

  .message-content
  {
    color: white;
  }

  .message-username a
  {
    color: white !important;
  }

  .message-infos
  {
    color: white;
  }

  .message-content-quote
  {
    background-color: rgb(64, 64, 64) !important;
  }

  .message-content-quote a, .message-content-quote i
  {
    color: white;
  }

  .profile-wrapper, .profile-infos tr, .profile-title, profile-stats
  {
    background-color: rgb(26, 26, 26) !important;
    color: white;
  }

  .profile-title
  {
    color: white !important;
  }

  section
  {
    color: white;
  }

  .pagination.pagination-topic a
  {
    color: white !important;
  }

  .chat-message, .chat-message-username a
  {
    color: white !important;
  }

  .aside a
  {
    color: white !important;
  }

  .spoiler-content
  {
    background-color: rgb(64, 64, 64) !important;
  }

  .grey-btn.btn
  {
    background-color: rgb(128, 0, 0);
  }

  .glyphicon .glyphicon-refresh
  {
  }

  .form-control.post-content, .form-control
  {
    opacity: 0.85;
    color: black;
  }

  a:hover
  {
    color: white;
  }

  .form-group.preview
  {
    background-color: rgba(26, 26, 26, 0.9);
    color: white;
  }

  .profile-biography
  {
    background-color: rgb(26, 26, 26) !important;
    color: white;
  }

  a:focus
  {
    color: white;
  }

  .admin a
  {
    color: red !important;
  }

  .mod a
  {
    color: orange !important;
  }

  .glyphicon-envelope
  {
    color: white;
  }

  .active .topics-author a
  {
    color: white;
  }

  .chat-form
  {
    background-color: #1a1a1a;
  }


  .btn-danger
  {
    background-color: rgb(128, 0, 0);
    border-color: rgb(128, 0, 0);
  }

  .special a
  {
    color: #446CB3 !important;
  }


  .topics-author .special
  {
    color: #256cdd;
  }

  .post-content
  {
    background-image: url(https://image.noelshack.com/fichiers/2018/07/3/1518630764-741.png);
    background-position: center;
    background-repeat: no-repeat;
    background-size: 75px 75px;
  }

  .topics-author a
  {
    color: white;
  }

  .btn.btn-primary:hover
  {
    background-color: rgb(26, 26, 26) !important;
  }

  .btn.grey-btn:hover
  {
    background-color: rgb(26, 26, 26) !important;
  }

  .navbar li a:hover
  {
    background-color: rgb(128, 0, 0) !important;
  }
  
    .glyphicon-off
  {
    background-image: url(https://i.imgur.com/w52cZC1.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }

    .glyphicon-cog
  {
    background-image: url(https://i.imgur.com/1bE4fm0.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }

      .glyphicon-envelope
  {
    background-image: url(https://i.imgur.com/WL3nhhd.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }
  
   .spoiler-btn
        {
        color: white !important;
        }

        .glyphicon-envelope.active
  {
    background-image: url(https://i.imgur.com/rt43oV9.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
  }
             .navbar-logo img
        {
        background-image: url(https://image.noelshack.com/fichiers/2018/07/5/1518796568-log3.png)!important;
        max-width: 200px;
        max-height: 41px;
        width: 100%;
        height: auto;
        padding-bottom: 41px!important;
        }
  
}

@-moz-document url-prefix("https://avenoel.org/favoris")
{
  body
  {
    color: white;
  }
    main.container .col-xs-12.col-sm-12.col-md-9 section
		{
		background-color: rgba(26, 26, 26, 0);
		}
}

@-moz-document url-prefix("https://avenoel.org/messagerie")
  {
  main.container .col-xs-12.col-sm-12.col-md-9 section
		{
		background-color: rgba(26, 26, 26, 0);
		}
  }

@-moz-document url-prefix("https://avenoel.org/topic")
  {
  main.container .col-xs-12.col-sm-12.col-md-9 section
		{
		background-color: rgba(26, 26, 26, 0);
		}
  }

@-moz-document url-prefix("https://avenoel.org/forum")
  {
  main.container .col-xs-12.col-sm-12.col-md-9 section
		{
		background-color: rgba(26, 26, 26, 0);
		}
  }
