css:
@-moz-document domain("eldarya.fr"), domain("fa1.eldarya.com"), domain("www.eldarya.ru"), domain("eldarya.com"), domain("eldarya.com.br"), domain("eldarya.de"), domain("eldarya.es"), domain("eldarya.fi"), domain("eldarya.hu"), domain("eldarya.it"), domain("eldarya.pl")
{

/*Флаги, бимув*/


#bgames { left: -1100px; opacity: .3; filter: alpha(opacity=30); }    
#bgames:hover:not(.off) { opacity: 1; filter: alpha(opacity=1); }

.bbimage { display: none!important; } .bbfleche { display: none!important; }

div#beemoov, #bbflags { position: fixed; top: 0%; left: 3%;  }
#bgames { position: fixed; top: 0%; left: 0%; }
#bbflags { position: fixed; top: 0%; left: 93%;  }

#bbflags { left: 115px; opacity: .3; filter: alpha(opacity=30); }
#bbflags:hover:not(.off) { opacity: 1; filter: alpha(opacity=1); }

div#beemoov, #bbflags { position: fixed; top: 0%; left: 3%;  }
#bgames { position: fixed; top: 0%; left: 0%; }
#bbflags { position: fixed; top: 0%; left: 93%;  }

/*Фон*/
body
{
  background-image: url("https://pp.userapi.com/c840720/v840720484/591fc/6-J6xgFtANs.jpg")  
}

body.night
  {
   background-image: url("https://pp.userapi.com/c840720/v840720484/591fc/6-J6xgFtANs.jpg")  
  }
/*Страница фон*/
  #container .page-main-container
  {
    background-color: rgba(52, 56, 111, 0);
  }
/*- рамка*/
   #container .page-main-container:after
  {
    background-size: 4px 0%, 4px 0%, 0% 4px, 0% 4px;
    box-shadow: 1px 2px 3px 0 rgba(52, 56, 111, 0);
  }

/*- тень рамки*/
  #container .page-main-container
  {
    box-shadow: inset 1px 1px 3px 1px rgba(52, 56, 111, 0); 
  }

  /*- Фон менюшки*/
 #main-menu ul
  {
   background-color: rgba(52, 56, 111, 0);
   box-shadow: inset 1px 1px 3px 1px rgba(52, 56, 111, 0);
  }

  /*- Рамка менюшки*/
 #main-menu ul:after
  {
    background-size: 4px 0%, 4px 0%, 0% 4px, 0% 4px;
    box-shadow: 1px 2px 3px 0 rgba(52, 56, 111, 0);
  }

/*Полоса сверху*/
 #header #header-top
  {
    position: absolute;
    top: 0;
    background-color: #110e1f;
    height: 30px;
    width: 100%;
  }

/*Полоса низ*/
  #footer
  {
    font-family: Banda bold, sans-serif;
    background-color: #110e1f;
  }

/*Имя: фон*/
#player-infos .player-name
  {
    color: #fff;
    border: solid 1px #333244;
    background-color: #7d5f6b;
  }

/*Уровень: фон, цвет*/
#player-info-level
  {
    color: #fff;
    background: #7d5f6b;
     border: solid 1px #333244;
  }

/*Мана и зм*/
  #player-infos .player-currencies
  {
    background-color: #7d5f6b;
    border: solid 1px #333244;
    border-radius: 13px;
    font-size: 15px;
  }


/*Инфа про уровень когда наводишь*/
  .tooltip-content
   {
    color: #fff;
    background-color: #7d5f6b;
    border: 1px solid #7d5f6b;
    padding: 10px;
   }

/*Кнопки в объявлении и эп*/
.flavr-button, .button.button
  {
    color: #fff;
    background-color: #7d5f6b;
    border: 1px solid #7d5f6b;
  }

/*Кнопочка в объявлении и эп актив*/
.flavr-button, .button.button:hover
  {
    color: #e3c1c0;
    background: #fff;
    border-color: #e3c1c0;
    cursor: default;
  }

/*Кнопочка*/
 .flavr-button.disabled, .button.button.button-disabled
  {
    background: #7d5f6b;
    border-color: #7d5f6b;
    color: #fff;
  }

/*Кнопочка актив*/
 .flavr-button.disabled, .button.button.button-disabled:hover
  {
    color: #e3c1c0;
    background: #fff;
    border-color:#e3c1c0;
    cursor: default;
  }

/*Кнопочка*/
  .flavr-button.gray, .button.button.button-gray
  {
    color: #fff;
    background: #7d5f6b;
    border-color: #7d5f6b;
  }

 /*Кнопочка кв*/
.flavr-button.purple, .button.button.button-purple
  {
    color: #fff;
    background: #e3c1c0;
    border-color: #e3c1c0;
  } 

/*Кнопочка кв актив*/
  .flavr-button.purple, .button.button.button-purple:hover
  {
    color: #e3c1c0;
    background: #fff;
    border-color: #e3c1c0;
    cursor: default;
  }
/*Менюшка при выборе*/
 #main-menu .menu-label span
  {
    background: #7d5f6b;
  }

/*Заголовки*/
 .flavr-container .flavr-fixer .flavr-outer .flavr-content h1
  {
    border-bottom: solid 1px  #e3c1c0;
    color: #fff;
    background:  #e3c1c0;
  /*  -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;*/
  }

#container h1
  {
    color: #333244;
  }

/*- Название раздела*/
 #container h1
  {
    background-image: url("https://avatanplus.com/files/resources/mid/5831d5ebe351215882aba19b.png");
  }  

/*- Конец названия раздела*/
   #container h1 .title-end
  {
    background-image: url("https://avatanplus.com/files/resources/mid/5831d5ebe351215882aba19b.png");
  }

/*Края моей рожи на главном экране*/
  #index-player
  {
    border: solid 1px  #333244;
  }

 
/*Новости*/
  #news-list .news
  {
    background: linear-gradient( to top, #9986de, #cab9f8);
   }
  #index-news .text a
  {
    color: #000;
  }
#index-news
  {
    background-color: #4e3374;
    border-radius: 7px;
    border: 2px solid #9585ad;
    height: 344px;
    width: 232px;
    margin: 20px -12px 0;
  }

  #news-list .news + .news
  {
    border-top: 2px solid #917391;
    ;
  }
    #index-news .date
  {
    background-color: #7d5f6b;
    color: #fff;
  }


/*Рамка инфы о фаме*/
  #pet-info-main
  {
    padding: 15px;
    background: #333244;
    border-radius: 10px;
    position: relative;
    border: 1px solid #333244 !important;
  }


/*Рамка еда и удача*/
  #pet-info-food, #pet-info-levels
  {
    background: #333244;
    border: 1px solid #333244 !important;
  }


/*Инфа о энергии и времени в разведке */
  #pet-info-energy-level,
#event-bonus-progressbar-fill
  {
    background-color: #333244;
  }


/*Фам активность*/
  #pet-info-racegender, #pet-info-age
  {
    font-size: 14px;
    color: #fff;
    margin: 6px 0px 0px;
  }

  /*Привязанность удача*/
  #pet-info-food, #pet-info-levels
  {
    color: #fff;
   }

/*Карта рамка*/
  #treasurehunt-fold
  {
    position: absolute;
    top: 0px;
    z-index: 3;
    width: 100%;
    border-radius: 8px;
    background: #333244;
  }


/*- Фон сундуков*/
  #main-bonus-progressbar
  {
    width: 100%;
    text-align: center;
    position: relative;
    background-color: rgba(52, 56, 111, 0);
    border-radius: 10px;
    padding: 15px 0px;
  }
/*Инфа в профиле фон*/
  #player-left-tab
  {
    background-color: #333244;
    border: 1px solid #333244;
  }
/*На этой странице ты можешь...*/
.marketplace-assistance
  {
    color: #fff;
    background: #333244;
  }

/*Приз: фон*/
#ranking-prize-container
  {
    background: #333244;
    border: 1px solid #333244;
  }

/*Лучшие игроки месяца*/
#ranking-top-players
  {
    background: #333244;
    border: 2px solid #333244;
    box-shadow: 1px 2px 3px 0 #333244;
  }
/*Инвентарь*/
#inventory-container{
  background-color: rgba(255, 228, 225, 0);
    box-shadow: 1px 2px 3px 0 rgba(255, 228, 225, 0);
}
}
