css:
@-moz-document regexp("https?:\\/\\/.*\\/.*(summa|finrisk|precis).*"), regexp("https?:\\/\\/v?summa.*") {
body {
    font-family: "Open Sans";
}

.readOnly {
    cursor: default;
}

.disabled {
    cursor: default;
}

.pane {
    background-color: #fff;
}

.pane > div.widgets {
    background-color: #fff;
}

.footContent .gotoPage input[type="text"] {
    background-color: #fff;
    border: 1px solid #ddd;
}

.dashboard-body .tab-widgets > .widget > .widget-content {
    background: #fff;
    border: none;
}

.widget.dashboarded {
    border-radius: 2px;
    border: none;
    box-shadow: none;
}

.dashboard-body .tab-widgets > .widget > .widget-header > .widget-label {
    color: #fff;
    background: #fff;
}
.widget.dashboarded.ValuationDate .widget-content .valuationDate {
    font-family: 'Orbitron', 'Ubuntu Mono', monospace;
    font-size: 22px;
    letter-spacing: 2px;
    padding: 9px 0 0 10px;
}

h2.widget-label {
    text-transform: uppercase;
    font-weight: 400!important;
    letter-spacing: .1em;
}

.widget.fill > .widget-header .widget-label,
.widget.filler > .widget-header .widget-label,
.widget.fullscreen > .widget-header .widget-label,
.widget.container > .widget-header .widget-label,
.widget.spacer > .widget-header .widget-label,
.widget.spacer > .widget-header h2.widget-label {
    color: #ba0000;
    background-color: transparent;
    box-shadow: none;
    text-shadow: 0 0 2px #ba00006b;
}

.widget.content-header > .widget-header {
    background-color: transparent;
}

.datepicker {
    border-bottom: none;
}

.ui-datepicker .ui-datepicker-header {
    background: #ba0000;
}

select.ui-datepicker-year,
select.ui-datepicker-month {
    color: #fff;
    background: #ba0000;
}

select.ui-datepicker-month option,
select.ui-datepicker-year option {
    background: #fff;
    color: #000;
}

.connect-left.disabled {
    border-bottom: none;
}

.widget.dashboarded:hover {
    box-shadow: 0 0 2px #ddd;
}

.table2 tbody tr.readOnly td {
    color: #555;
    font-style: italic;
    background-color: #fdfdfd;
    text-shadow: 0 0 1px #fff;
}

textarea.readOnly,
input.readOnly,
input.readOnly:hover {
    border: none;
    box-shadow: none;
    color: #aaa;
}

span.tablabel,
label.forinput {
    font-family: "Open Sans";
}

span.orderIndex {
    color: #f7f7f7;
    background-color: #999999b8;
    display: inline-block;
    border-radius: 3px;
    font-size: 10px;
    font-weight: 600;
    padding: 0 3px 1px 2px;
}

pre,
xmp,
plaintext,
listing {
    display: block;
    font-family: monospace;
    white-space: pre;
    margin: 1em 0;
}

fieldset legend {
    font-size: .9em;
    letter-spacing: 2px;
    font-weight: 400;
    color: #333;
    text-transform: uppercase;
    padding: 2px 3px 3px 2px;
    border: none;
    border-bottom: 2px solid #ba000069;
    text-shadow: #fff 0 0 2px;
    width: auto;
    box-shadow: none;
}

form fieldset {
    border: none;
    margin: 30px 5px 5px;
    background-color: transparent;
}

form fieldset legend {
    background-color: #fff;
    padding: 2px 6px 2px 6px;
    margin: 0px;
    border: 0px;
    text-shadow: none;
    color: #ba0000;
    width: 100%;
}

form div.description {
    padding: 6px;
    line-height: 1.5em;
    color: #333;
    text-transform: uppercase;
    background-image: url(data:image/png;base64,);
    background-repeat: no-repeat;
    background-position: 7px 6px;
    border: 1px solid #f39c1280;
    background-color: #f39c1220;
    border-radius: 4px;
    padding-left: 32px;
    margin: 8px;
}

form .myTemplates.shortcutContainer {
    display: block;
    margin: 0px;
    padding: 3px 3px 3px 25px;
    background-color: #f0f0f0;
    border-bottom: 1px solid #fff;
    background-image: url(data:image/png;base64,);
    background-repeat: no-repeat;
    background-position: 8px 8px;
}

form .shortcutContainer {
    display: inline-block;
    vertical-align: top;
}

div.row div.description {
    padding: 6px;
    line-height: 1.5em;
    color: #333;
    text-transform: uppercase;
    background-image: url(data:image/png;base64,);
    background-repeat: no-repeat;
    background-position: 7px 6px;
    border: 1px solid #f39c1280;
    background-color: #f39c1220;
    border-radius: 4px;
    padding-left: 32px;
    margin: 8px;
}

fieldset.collapsible > legend {
    padding: 2px 21px 1px 6px;
    cursor: pointer;
    color: #444;
    background: #ffffff88 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAMAAABFNRROAAAAOVBMVEUAAABERERERERERERERERERERERERERERERERERERERERERERERERERERERERERERERERERERERESE+eGDAAAAEnRSTlMABBIWGR8gZGdpfoCCg6bx+fsm1dRYAAAAQUlEQVQIW4XHORKAIBTA0Lih3w3M/Q9rwTBaaao8+GqZ2iRYLQmAzWtktnLX3ENoSRyaB6g8GyD0AcQbEG9Ax283GKUCxxU2fqIAAAAASUVORK5CYII=) no-repeat right 4px top 4px;
    transition: background-color 1000ms, color 1000ms;
}

fieldset.collapsible.open > legend {
    padding: 2px 21px 1px 6px;
    color: #ba0000;
    background: #ffffff url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANBAMAAACAxflPAAAAMFBMVEUAAAC6AAC6AAC6AAC6AAC6AAC6AAC6AAC6AAC6AAC6AAC6AAC6AAC6AAC6AAC6AACIarMFAAAAD3RSTlMABBIWGB4fZGZpe3+i8flqBvUFAAAAPklEQVQIW2NgwAQCECHOyxB6/l8DMPf/f7DA/H/nQQKc/59ygwTq/yUw7P+rwLD+GQMD9z8HBo4AoJo2DEMBuUkUa5alI5wAAAAASUVORK5CYII=) no-repeat right 4px top 2px;
}

fieldset.collapsible {
    min-width: 440px;
    border: 1px solid #f7f7f7;
    transition: border 1000ms;
}

fieldset.collapsible.open {
    border: 1px solid #ddd;
}

label {
    font-size: 12px;
}

label.forInput.hasHelp.disabled {
    color: #a0a0a0;
    text-shadow: #fff 1px 0 0px, #fff -1px 0 0;
}

.form > .label {
    color: #fff;
    text-shadow: 1px 1px 2px #000;
    background: #999;
    border-bottom: none;
    text-transform: uppercase;
    letter-spacing: .8px;
}

.dialog .form > .label {
    color: #333;
    text-shadow: 1px 1px 0 #fff;
    background: #fff;
    border-bottom: none;
}

.form > .label.readonly {
    background: #999;
    color: #fff;
    text-shadow: 1px 1px 0 #777;
    font-style: italic;
}

.ms2-parent .choices {
    border: none;
    border-left: 1px dashed #e0e0e0;
    background-color: transparent;
}

.tag.ok,
.tag.passed {
    border: none;
    padding: 0 4px;
    font-size: 10px;
    margin-right: 2px;
    box-shadow: none;
    color: #fff;
    background-color: #36a24c;
    border-radius: 2px;
    text-transform: uppercase;
}

.tag.failed {
    border: none;
    padding: 0 7px;
    font-size: 10px;
    margin-right: 2px;
    box-shadow: none;
    color: #fff;
    background-color: #f2543c;
    border-radius: 2px;
    text-transform: uppercase;
}

.tag.role {
    border: none;
    padding: 0 2px;
    font-size: 10px;
    margin-right: 2px;
    background: none;
    border-radius: 0;
    box-shadow: none;
}

.tag.role.owner {
    border: none;
    border-bottom: 1px solid #36a24c;
    padding: 0 2px;
    font-size: 10px;
    margin-right: 2px;
    background: none;
    border-radius: 0;
    color: #36a24c;
    box-shadow: none;
}

.checked .tag.role.owner,
.selected .tag.role.owner {
    color: #fff;
    background-color: #36a24c;
    border-radius: 4px;
}

.tag.role.counterparty {
    border-bottom: 1px solid #f2543c;
    padding: 0 2px;
    font-size: 10px;
    margin-right: 2px;
    color: #f2543c;
}

.checked .tag.role.counterparty,
.selected .tag.role.counterparty {
    color: #fff;
    background-color: #f2543c;
    border-radius: 4px;
}

.tag.role.bank {
    border-bottom: 1px solid #3e9cef;
    padding: 0 2px;
    font-size: 10px;
    margin-right: 2px;
    color: #3e9cef;
}

.checked .tag.role.bank,
.selected .tag.role.bank {
    background-color: #3e9cef;
    color: #fff;
    border-radius: 4px;
}

.tag.role.issuer {
    border: none;
    border-bottom: 1px solid #a28336;
    padding: 0 2px;
    font-size: 10px;
    margin-right: 2px;
    background: none;
    border-radius: 0;
    color: #a28336;
    box-shadow: none;
}

.checked .tag.role.issuer,
.selected .tag.role.issuer {
    color: #fff;
    background-color: #a28336;
    border-radius: 4px;
}

.tag.role.exchange {
    border: none;
    border-bottom: 1px solid #000;
    padding: 0 2px;
    font-size: 10px;
    margin-right: 2px;
    background: none;
    border-radius: 0;
    color: #000;
    box-shadow: none;
}

.checked .tag.role.exchange,
.selected .tag.role.exchange {
    color: #ddd;
    background-color: #444;
    border-radius: 4px;
}


.tag.tstate.final,
.tag.tstate.limit-violation,
.tag.tstate.to-confirm,
.tag.tstate.selling-rejected {
    border: none;
    padding: 1px 3px;
    font-size: 10px;
    margin-right: 2px;
    background: #fff;
    color: #000;
    border-radius: 3px;
    box-shadow: none;
    letter-spacing: 1px;
    text-shadow: none;
}

.tag.tstate.final {
    padding: 2px 3px 2px 18px;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAMAAABFNRROAAAAM1BMVEUAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACjBUbJAAAAEXRSTlMACg8cHiguVWdoaWtsbZj5/dOH3uwAAAA4SURBVAhbY2DABXjZkDg8/Cw4ONwQDhOYwwXhMAuwAklOmDJ2IJcDoYddgA/ZAHZkDgMDIwMRAAB+wgFNpGGPRgAAAABJRU5ErkJggg==) no-repeat 3px 2px;
    background-color: #37a60340;
}
.checked .tag.tstate.final,
.selected .tag.tstate.final {
    color: #fff;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAMAAABFNRROAAAAM1BMVEUAAAD///////////////////////////////////////////////////////////////+3leKCAAAAEXRSTlMACg8cHiguVWdoaWtsbZj5/dOH3uwAAAA4SURBVAhbY2DABXjZkDg8/Cw4ONwQDhOYwwXhMAuwAklOmDJ2IJcDoYddgA/ZAHZkDgMDIwMRAAB+wgFNpGGPRgAAAABJRU5ErkJggg==) no-repeat 3px 2px;
    background-color: #37a60380;
}

.tag.tstate.limit-violation,
.tag.tstate.selling-rejected {
    padding: 2px 3px 2px 18px;
    background: url(data:image/png;base64,) no-repeat 3px 2px;
    background-color: #ff260080;
}
.checked .tag.tstate.limit-violation,
.selected .tag.tstate.limit-violation,
.checked .tag.tstate.selling-rejected,
.selected .tag.tstate.selling-rejected {
    color: #fff;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAQAAADY4iz3AAAAnklEQVQYV2NggIP/sf9P/LdgQAf/Hf6//r/+P9v/n/8f/XdGlXr2n+l/HJBO/8/8//9/NmSpR0DsCcX//9/5r4JdKuU/6/9TIEEBoB07UaQ8IUoZ/u/7z/Ff+P9NFKuhUuf/swBJNWxSuv97GDDA/4sQqv6/CZoEN1Q50EdH/3OgSGn894ExRf6v/c+IJFX8Xw7B0QLqfASF9/5XgMQAsthoof81CBAAAAAASUVORK5CYII=) no-repeat 3px 2px;
    background-color: #ff260080;
}

.tag.tstate.to-confirm {
    padding: 2px 3px 2px 18px;
    background: url(data:image/png;base64,) no-repeat 3px 2px;
    background-color: #5fc7f99c;
}
.checked .tag.tstate.to-confirm,
.selected .tag.tstate.to-confirm {
    color: #fff;
    background: url(data:image/png;base64,) no-repeat 3px 2px;
    background-color: #5fc7f99c;
}



.row.required .inputBoxRequired,
.input-wrapper.required .inputBoxRequired,
.row.required .ms2-parent:after {
    border: none;
    background: transparent;
}



.row.required .ms2 -parent:after,
.input-wrapper.required .ms2-parent:after {
    border: none;
    background: transparent;
}


.row.requiredWithIgnore .inputBoxRequired,
.input-wrapper.requiredWithIgnore .inputBoxRequired,
.row.requiredWithIgnore .ms2-parent:after {
    border: none;
    background: transparent;
}

.row.requiredWithIgnore .ms2 -parent:after,
.input-wrapper.requiredWithIgnore .ms2-parent:after {
    border: none;
    background: transparent;
}


label {
    color: #000;
    font-size: .75em;
    letter-spacing: 1px;
    text-transform: uppercase;
}

.row.required > label.forInput {
    font-weight: 600;
    text-shadow: 0 0 0 #9d01016b;
    color: #9d0101;
}

.row.required > label.forInput::after {
    font-size: .95em;
    color: #e90049;
}

.row.requiredWithIgnore > label.forInput {
    font-weight: 600;
    text-shadow: 0 0 0 #9f78006b;
    color: #9f7800;
}

.row.requiredWithIgnore > label.forInput::after {
    font-size: .95em;
    color: #9f7800;
}



.table-actions-main.clicked {
    padding-top: 10px;
    height: 40px;
    background: #fff;
    box-shadow: -3px 3px 6px #888;
    border-radius: 3px 0 0 3px;
    border: 1px solid #aaa;
    overflow: hidden;
    text-overflow: ellipsis;
}

div.actionButtons > span > button {
    font-weight: 400;
    box-shadow: none;
    text-shadow: none;
}

div.actionButtons > span > button:hover {
    text-shadow: none;
    background-color: #f0f0f0;
}

.btn,
button,
input[type=button],
input[type=submit],
input[type=reset],
.btn.disabled:hover,
button.disabled:hover,
input[type=button].disabled:hover,
input[type=submit].disabled:hover,
input[type=reset].disabled:hover,
.btn.disabled:active,
button.disabled:active,
input[type=button].disabled:active,
input[type=submit].disabled:active,
input[type=reset].disabled:active {
    font-weight: 400;
}

.btn.submit,
button.submit,
input[type=submit],
.btn.submit.disabled:hover,
button.submit.disabled:hover,
input[type=submit].disabled:hover .btn.submit.disabled:active,
button.submit.disabled:active,
input[type=submit].disabled:active {
    background-color: #d80000;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.8);
}

div.actionButtons > span > button.submit:hover {
    background-color: #fe1818;
    box-shadow: none;
}

textarea,
input[type=text],
input[type=number],
input[type=password],
select {
    background-color: #fff;
    border-bottom: none;
}

.ms2-parent .ms2-choice {
    background-color: #fff;
    border-bottom: none;
}

.row.modified textarea,
.row.modified input,
.row.modified select,
.row.modified .ms2-parent .ms2-choice {
    color: #000;
    background-color: #fff5ba;
    font-weight: 600;
    border-bottom: 1px solid transparent;
}

.notify.success {
    background: #fff;
    border: 1px solid #74a900;
    border-left: 15px solid #74a900;
    color: #000;
    text-shadow: 0 0 3px #fff;
}

.notify.warning {
    color: #000;
    background: #fff;
    border: 1px solid #ffa700;
    border-left: 15px solid #ffa700;
    text-shadow: 0 0 3px #fff;
}

.notify.error {
    color: #000;
    background: #fbe5e5;
    border: 1px solid #d80000;
    border-left: 15px solid #d80000;
    text-shadow: 0 0 3px #fff;
}

.notify {
    border: 1px solid #333;
    text-shadow: none;
    background: #fff;
    color: #333;
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.2), 0 2px 25px rgba(0, 0, 0, 0.3);
    border-radius: 3px;
    padding: 8px 8px 9px 18px;
}

.notify > .content {
    font-weight: 500;
}

.notify > .content .showMore {
    padding-left: 5px;
    color: #333;
    text-shadow: 1px 1px 1px #fff;
    font-weight: 300;
}

.notify.toast a.showMore {
    color: #fff;
    text-shadow: 1px 1px 1px #000;
}

.table2 tbody tr.negative td {
    color: #da0101;
    background-color: #fffcf4;
}

.table2.table-hover tbody tr.error.selected td:first-child {
    border-left: 4px solid red;
}

.table2.table-hover tbody tr.warning.selected td:first-child {
    border-left: 4px solid #ff0;
}

ul.widget-tabs-nav.ui-tabs-nav.ui-corner-all.ui-helper-reset.ui-helper-clearfix.ui-widget-header {
    background-color: #f7f7f7;
    border-bottom: 0 solid #a0a0a0;
    padding-bottom: 4px;
}

.table2 tbody tr th.positive,
.table2 tbody tr td.positive {
    color: #0013ea;
    background-color: #fff;
    font-weight: 500;
}

.table2 tbody tr th.negative,
.table2 tbody tr td.negative,
.table2 tbody tr th.error,
.table2 tbody tr td.error {
    color: red;
    background-color: #fff;
    font-weight: 500;
}

li.ui-tabs-tab.ui-corner-top.ui-state-default.ui-tab.ui-tabs-active.ui-state-active {
    border-bottom: 2px solid #ba0000;
}

.ui-datepicker-calendar .ui-state-active.weekend a.ui-state-default.ui-state-active,
.ui-datepicker-calendar td.weekend a.ui-state-default.ui-state-active,
.ui-datepicker-calendar .ui-state-active.holiday a.ui-state-default.ui-state-active,
.ui-datepicker-calendar td.holiday a.ui-state-default.ui-state-active,
.ui-datepicker-calendar .ui-state-active a.ui-state-default.ui-state-active,
.ui-datepicker-calendar td a.ui-state-default.ui-state-active {
    background: #ba0000;
    box-shadow: inset 0 0 1px 0 rgba(0, 0, 0, 0.5);
    color: #fff;
    text-shadow: 0 1px 0 #ba0000;
    position: relative;
}

.ui-datepicker-calendar .ui-state-active a.ui-state-default.ui-state-hover,
.ui-datepicker-calendar td a.ui-state-default.ui-state-hover {
    background: #ba00007d;
    color: #fff;
    text-shadow: 1px 1px 0 #000;
    box-shadow: none;
}

.ui-datepicker-calendar .ui-state-active a.ui-state-default.ui-state-active.ui-state-hover,
.ui-datepicker-calendar td a.ui-state-default.ui-state-active.ui-state-hover {
    background: #ba0000d4;
    color: #fff;
    text-shadow: 1px 1px 0 #000;
    box-shadow: none;
}

.widget.tabbed .widget-tabs,
.widget.tabbed .side-widget-tabs,
.widget.tabbed .widget-tabs-nav {
    background: #f7f7f7;
}

table.dialog .widget-header,
.widget > .widget-header-tabbed,
.widget > .widget-header {
    background: #ba0000;
}

.widget.tabbed .side-widget-tabs .widget-tabs-nav .ui-state-default a,
.widget.tabbed .widget-tabs .widget-tabs-nav .ui-state-default a {
    padding: 10px 10px 6px;
    color: #a53b3b;
    text-shadow: 0 0 2px #f7f7f7;
    text-transform: uppercase;
}

.widget.tabbed .side-widget-tabs .widget-tabs-nav .ui-state-default:hover a,
.widget.tabbed .widget-tabs .widget-tabs-nav .ui-state-default:hover a {
    color: #ba0000;
    text-shadow: 0 0 1px #f7f7f7;
}

.widget.tabbed .side-widget-tabs .widget-tabs-nav .ui-state-default.ui-state-active a,
.widget.tabbed .widget-tabs .widget-tabs-nav .ui-state-default.ui-state-active a {
    color: #ba0000;
    text-shadow: none;
}

.widget.tabbed.filler > .fix-tabbed {
    top: -5px;
}

.widget.dialog {
    background: #f7f7f7;
    border: 1px solid #ba0000;
    box-shadow: 0 0 25px 5px rgba(0, 0, 0, 0.8);
}

.table2.activeChecking tbody td.checkable {
    background-color: #0a123d45;
}

ul.main-menu li a span.label {
    text-transform: uppercase;
    padding-left: 7px;
    padding-right: 7px;
}

table.dialog .widget-label,
.widget .widget-label {
    color: #ba0000;
    cursor: default;
    font-weight: 700;
    line-height: 20px;
    font-family: "Open Sans";
}

.table-header tr.sort th .rendererLabel,
.table-header tr.sort th .label {
    text-transform: uppercase;
    font-size: .85em;
    color: #000;
    font-weight: 600;
}

.simpleTable th,
.transitionsTable th {
    font-size: 13px;
}

.simpleTable td,
.transitionsTable td,
.simpleTable th,
.transitionsTable th {
    vertical-align: middle;
    padding: 15px 10px;
    border-bottom: 1px solid #d1d1d1;
}

.simpleTable td.label,
.transitionsTable td.label {
    font-size: 13px;
    white-space: nowrap;
}

.simpleTable td.state,
.transitionsTable td.state {
    font-size: 11px;
}

.simpleTable .status,
.transitionsTable .status {
    font-size: 12px;
    padding: 4px 10px;
    line-height: 1.3em;
    display: inline-block;
    height: auto;
    width: 100%;
}

.widget.dialog .ui-widget-header .ui-icon-close {
