/* エリア全体
---------------------------------------- */


/* 番組情報欄
---------------------------------------- */
/* 開始時間 */
[class*=___on-air-time-skin___] { color: #f9f7f7; }

/* タイトル */
[class*=___program-title-skin___] { color: #d3ee0d; }

/* 本文｜依存有___descriptionより下に */
[class*=___description-skin___] { color: #f9f7f7; }

/* 視聴者お便り */
[class*=___audience-mail-widget-skin___] [class*=___body-skin___] { border-color: #222; background-color: inherit; }
[class*=___audience-mail-widget-skin___] [class*=___title-skin___] { background-color: #07f; }

/* コンテンツツリーを見るボタン */
[class*=___contents-tree-anchor-skin___] { color: #d9a300; border-color: #1a1a1a; background-color: inherit ; }
[class*=___contents-tree-anchor-skin___]:hover { color: #e9b300; border-color: #e9b300; }

/* コミュニティ情報
---------------------------------------- */

/* タイトル */
[class*=___title-skin___] { color: #d3ee0d; }

/* コミュ名・レベル */
[class*=___social-group-anchor-skin___] { color: #f9f7f7; }
[class*=___level-counter-skin___] { color: #f9f7f7; }

/* 本文-skin */
[class*=___description-skin___] { color: #f9f7f7; }
[class*=___description-skin___][aria-expanded=false] { color: #999; }

/* もっと見る */
[class*=___description-expand-toggle-button-skin___] { color: #0080ff; background: linear-gradient(90deg,hsla(0,0%,100%,0),#121212 16%); }


/* ========================================
  プレイヤー下エリア
======================================== */
[class*=___zapping-widget-skin___] { background: #1a1a1a; }
[class*=___paddle-button-skin___] { background-color: #333; }
[class*=___paddle-button-skin___]:hover { background-color: #444; }
[class*=___paddle-button-skin___]:disabled { background-color: #333; }
[class*=___paddle-button-skin___]:disabled>svg { fill: #555; }
[class*=___refresh-button-skin___] >svg { fill: #dadada; }
[class*=___refresh-button-skin___] { color: #dadada; }

/* コメント欄
---------------------------------------- */

/* 背景色・幅384px 303030 121212 */
[class^=___player-status] { border-color: #121212; background-color: #191919; min-width: 384px; }

/* コメント・来場者数・コメント数 */
[class*=___watch-page-skin___] { color: #f9f7f7; }

/* ルーム名 */
[class^=___room-information]>[class^=___room-name] { color: #f9f7f7; }

/* パネル・下線色 */
[class*=___program-panel-base___] { border-bottom: 1px solid #353535; }

/* ヘッダー・上線色 */
[class*=___header-cell-skin___] { color: #200ae7; background-color: #f9f7f7; }

/* ヘッダー・下線色 */
[class*=___header-skin___] { border-color: #252525; }

/* 上・下線色 */
[class*=___body-skin___] { border-top: 1px solid #171717; border-bottom: 1px solid #222; }

/* コメント・閲覧総数アイコン */
[class^=___comment-count-icon___],
[class^=___watch-count-icon___] { fill: #b2bac2; }

/* コメント番号（ニコ生） */
[class^=___comment-number___] { color: #f9f7f7; }

/* コメント番号（ニコキャス） */
[class^=___comment-number___3m0a7] { color: #f9f7f7; }

/* ホバー */
[class*=___table-row-skin___]:hover{background-color:#282828}

/* 右クリック */
[class*=___context-menu-skin___] { background-color: #4c4744; }

/* 右クリック｜罫線色 */
[class*=___context-menu-skin___]>hr { background-color: #444; }

/* 右クリック */
[class^=___list]>li>a { color: #fff; background-color: #555;}

/* 右クリック｜ホバー */
/*[class^=___list]>li>a:hover { background-color: #555; }*/

/* オペレーター */
[class^=___table-row][data-comment-type=operator] { color: #f13; }
/* ニコ生｜コメ欄｜Normal：通常｜文字色 */
[class^=___table-row][data-comment-type=normal] { color: #f9f7f7; }
/* ニコ生｜コメ欄｜BSP：バックステージパス｜文字色 */
[class^=___table-row][data-comment-type=bsp] {color: #0cc; }
/* ニコ生｜コメ欄｜トライアル｜文字色 */
[class^=___table-row][data-comment-type=trialWatch] {color:#68dc5e; }
/* ニコ生｜コメ欄｜ニコアド｜文字色da0・d90 */
[class^=___table-row][data-comment-type=nicoad] { color: #151414; background-color: #d90; }

/* NGリスト */
[class^=___ng-delete-form___] [class*=___table-row-skin___]:hover { background-color: #ccc; }

/* ヘッダー・セパレータ */
[class*=___header-cell-separator-base___] { background-color: #333; }
[class*=___header-cell-separator-base___][data-resizable=true] { width: 4px; }

/* セパレータ */
[class*=___table-row-skin___] { border-color: #222; border-right-width: 1px; }

/* 放送者 */
[class*=___provider-skin___],
[class^=___provider]>a,
[class*=___broadcaster-skin___],
[class^=___broadcaster]>a { color: #999; }

/* レベルカウンター */
[class*=___level-counter-skin___] { color: #999; }

/* タグ編集
---------------------------------------- */

/* 背景色 121212 */
[class*=___tag-widget-base___] { background-color: inherit; }


/* ナビゲーションバー */
#siteHeader { background: #232323; }
#siteHeader #siteHeaderInner { background: inherit; }

