css:
@-moz-document url-prefix("https://instabart.no/") {
@keyframes fade-in {
    0% {opacity:0;}
    40% {opacity:0;}
    100% {opacity:1;}
}

body {
    background-image: url("https://pvv.ntnu.no/~pederbs/redirect.php?url=http%3A%2F%2Fpvv%2Eorg%2F%7Epederbs%2Fbackgrounds%2Etxt");
    background-repeat: no-repeat;
    background-size: cover;
    animation-name: fade-in;
    animation-duration: 0.7s;
}

header {
    background-color: rgba(0,0,0,0.2);
    border-color: rgba(0,0,0,0.25);
    color: #fff;
    text-shadow: 1px 1px 2px black; 
}

.track-main {
    background-color: rgba(0,0,0,0.2);
}

footer {
    color: #ccc;
    text-shadow: 1px 1px 2px black; 
}
#track-mvn {
    color: #fff;
    text-shadow: 1px 1px 2px black; 
}
}
