css:
@-moz-document domain("habitica.com") {
.boss-section,.notification[data-v-5e125e86] {
    display: none !important;
}
/*LESLIE*/
.seasonal .background.broken {
    background: url(https://habitica.com/static/images/seasonal_shop_closed_background.aa753c7.png) !important;
}
.seasonal .broken .npc {
    background: url(data:image/png;base64,) !important;
    background-repeat: no-repeat !important;
}
.seasonal .broken .closed .text {
  text-indent: -9999px;
  line-height: 0; /* Collapse the original line */
	}
.seasonal .broken .closed .text::after {
  content: "The Seasonal Shop is currently closed!! It’s only open during Habitica’s four Grand Galas.";
	text-indent: 0;
  display: block;
  line-height: initial; /* New content takes up original line height */
}
/*ALEX*/
.market .background.broken {
    background: url(data:image/png;base64,) !important;
}
.market .broken .npc {
    background: url(https://habitica.com/static/images/market_banner_npc.f5d86b5.png) !important;
    background-repeat: no-repeat !important;
}
/*IAN*/
.quests .background.broken {
    background: url(https://habitica.com/static/images/quest_shop_background.17f2343.png) !important;
}
.quests .broken .npc {
    background: url(data:image/png;base64,) !important;
    background-repeat: no-repeat !important;
}
}
