@-moz-document domain(magister.net) {

* {
  -webkit-overflow-scrolling: touch;
}
.k-scheduler-weekview div.k-scheduler-header table.k-scheduler-table th.k-today,
.k-scheduler-weekview table.k-scheduler-table th.k-today div.weekHeader div,
.k-calendar .k-header .k-nav-fast, h3, .block h3, .details h3, aside .tabs li:hover a,
.aside-toggle aside .content-container .tabs li:hover a, .k-scheduler .k-scheduler-times .sm-now,
#afsprakenLijst .today {
  color: rgb(231,172,94);
}
.k-scheduler .k-event {
  box-sizing: border-box;
  min-width: 88px;
  font-size: 1.05em;
  border-color: white;
  border-radius: 6px;
  border-width: 2px !important;
  box-shadow: 2px 2px 8px rgba(0,0,0,.2);
  transition: .2s linear 0s background-color, .2s linear 0s box-shadow;
  color: black !important;
}
.block, .k-calendar {
  box-shadow: 1px 1px 4px rgba(0,0,0,.15);
}
.k-scheduler .k-event.k-state-selected {
  box-shadow: 2px 2px 8px rgba(0,0,0,.4), 0px 0px 4px rgba(0,0,0,.1) inset;
}
.icon-block {
  width: 100%;
  position: absolute;
  bottom: 0;
}
.icon-block .text-icon + .text-icon {
  margin-left: 0;
  margin-right: 4px;
}
.text-icon {
    background-color: #be2f37;
}
.text-icon[data-ng-if^="(dataItem.heeftOnafgerondHuiswerk"], .text-icon[data-ng-if^="dataItem.heeftInformatie"], .text-icon[data-ng-show^="!event.isAfgerond"] {
  background-color: rgb(231,172,94) !important;
}
.grades-table-overview * {
  cursor: default !important;
}
.cijferCell {
  font-size: 1.1em;
}
.dialog-overlay.privacy-overlay-blur:before {
  opacity: 0 !important;
}
div.dialog-overlay .dialog {
  box-shadow: 0px 10px 20px rgba(0,0,0,.5);
}
.head-bar, .menu li.active, .menu li.active > a:hover, .menu li.active > a:hover, aside .tabs li.active a, #vandaag-container #cijfers-leerling .last-grade, .login-dialog .title, .k-scheduler .k-current-time {
  background-color: rgb(231,172,94);
  color: white;
}
aside .tabs li.active:after {
  display: none;
}
aside .tabs li.active a:after {
  border-color: rgb(231,172,94) rgba(231,172,94,0) rgba(231,172,94,0);
}
.head-bar div > a, aside .trigger-btn, aside .icon-menu {
  border-color: transparent;
  height: 47px;
  color: white;
}
.head-bar div > a.grayed, aside .trigger-btn.grayed {
  color: rgba(255,255,255,.35) !important;
}
.head-bar div > a:hover {
  background: rgb(173,129,70);
}
.k-scheduler .k-current-time {
  box-shadow: 0px 1px 3px rgb(173,129,70);
}
.aside-toggle aside .head-bar .asideTrigger {
  border-top-left-radius: 8px;
}
.head-bar .foldout {
  border-color: rgb(243,213,174);
  background-color: rgb(231,172,94);
  color: white;
}
.head-bar .foldout li + li {
  border-color: rgb(243,213,174);
}
.head-bar .foldout li a:hover, aside .trigger-btn:hover {
  background-color: rgb(173,129,70);
  cursor: pointer;
}
.head-bar {
  border-top-left-radius: 8px;
  border-top-right-radius: 8px;
  padding: 0 12px;
}
header, .menu {
  position: fixed !important;
  -webkit-transform: translate(0,0);
}

/* 
 * Fixes for homework section in hour summary (strange use of padding and extraneous line breaks).
 */
.block .padding-bottom {
  padding-bottom: 0;
}
.block .content .title + p {
  padding-top: 0;
}
.block .content p {
  padding: 0 8px;
  margin: 1em 0;
}
.block .content p p {
  padding: 0;
}
.block .content p p br:last-child, .block .content p p br:nth-last-child(2) {
  display: none;
}
.block .content .title {
  line-height: .9em;
  height: 1em;
  margin: 1em 0;
}
/**/

/*
 * Light theme.
 */
.header, header, .menu, .header .version, .header .version strong {
  background-color: #E8E8E8;
  color: black;
}
.menu li, .menu .navTrigger, .menu ul ul, .menu li.highlight-menu a span, .header .logo, .header .logo > a, .header > div + div {
  background-color: #E8E8E8;
  color: #505050;
  border-color: #C0C0C0;
}
.header > div + div a {
  color: #505050;
}
.menu a {
  border-color: #C0C0C0;
  color: #505050;
}
.menu a:hover, .menu li:hover, .menu .navTrigger:hover, .header div.logoff:hover, .header > div + div:hover, .header .header-user:hover, .header .user:hover {
  background-color: #C0C0C0;
}
.menu .sub > a:after {
  border-color: transparent transparent transparent #606060;
}
.menu li.active > a, .menu li.highlight-menu.active, .menu li.active > a:hover {
  color: white;
}
/**/

  
/*
 * Integrated head-bar.
 */
.head-bar {
  position: fixed;
  top: 0px;
  left: 123px;
  right: 278px;
  height: 48px;
  padding-top: 1px;
  border-top-left-radius: 0px;
  border-top-right-radius: 0px;
  z-index: 1000;
}

em.ng-binding {
  min-width: 91px;
}
      
.sidecolumn .head-bar {
  right: 278px;
  /*padding-right: 38px;*/
  border-right: 26px solid #F0F0F0;
}
      
.sidecolumn.aside-toggle .head-bar {
  border: none;
}
      
.sidecolumn aside .head-bar {
  border-top-left-radius: 8px;
  border-top-right-radius: 8px;
  padding-right: 12px;
  border-right: none;
}
      
.aside-toggle.container .head-bar {
  right: 278px;
}
      
.main {
  padding-top: 48px;
}
/**/

.header {
  width: 100%;
}
aside div.content-container {
  border-top-left-radius: 16px;
  border-top-right-radius: 16px;
}
.menu > ul {
  border-top-color: rgb(231,172,94);
}
body {
  background-color: rgb(243,213,174);
}
body[contenteditable] {
  background-color: white;
}
.endlink a {
  color: rgb(231,172,94);
}
.endlink a:hover {
  color: rgb(243,213,174);
}
.k-scheduler-weekview table.k-scheduler-table .k-today,
.agenda-lesdashboard aside .agenda-list li a.current,
.agenda-lesdashboard aside .agenda-list li a:hover,
.widget .list li a:hover,
.messages li:hover,
.messages li.selected,
.sources li.selected > a {
  background-color: rgb(250,238,222);
}
.main > div {
  padding-left: 8px;
}
.widget .list li:hover, .widget .list li.active {
  background-color: rgb(250,238,222);
}
.widget, .widget * {
  user-select: none;
  -moz-user-select: none;
  -webkit-user-select: none;
  -webkit-tap-highlight-color: transparent;
}
.primary-button {
  border-color: rgb(231,172,94);
  background: rgb(231,172,94);
  color: white;
}
.primary-button:hover {
  box-shadow: inset 0 0 0 2px rgb(231,172,94), 1px 1px 2px -1px rgba(0,0,0,0.7);
}
#agenda-container > aside:nth-child(3) {
  overflow: hidden;
}
.view > .container > .head-bar, header > * {
  transform: skew(-10deg);
}
.view > .container > .head-bar > div > a > *, .view > .container > .head-bar > h1, header > * > * {
  transform: skew(10deg);
}
header > .logo {
  transform: none;
}
/*.icon-logo:before {
  color: white !important;
}*/
.header-user img {
    border-radius: 8px;
    transform: skew(-10deg);
    transition: transform ease .5s;
}
.header-user:hover img {
    transform: skew(-10deg) scale(-1) rotate(180deg);
}

}
