css:
@-moz-document domain('docs.godotengine.org'), domain('godot.readthedocs.io') {
/* GENERAL */
a:visited {
	color: #2980b9;
}

body {
	color: #ddd;
}

h1, h3 {
	color: #27ae60;
}

h2 {
	color: #e67e22;
}

h4 {
	color: #9b59b6;
}

hr.docutils {
	border-top: 1px dashed #2cc36b;
}
/* BACKGROUNDS & MENUS */

/* Main Content Background */
.wy-body-for-nav, .wy-nav-content {
	background: #101010;
	border-right: 5px solid #9b59b6;
}
/* Background Overflow */
.wy-nav-content-wrap {
	background: #091721;
}

.wy-nav-shift {
	background-color: #091721;
}

/* Sidemenu */
.wy-nav-side {
	background-color: #101010;
	border-right: 5px solid #9b59b6;
}

/* Fixed Position Search & Scrollbars (only visible in desktop sizes) */
@media screen and (min-width: 768px) {
	::-webkit-scrollbar {
		background-color: #222;
	}

	::-webkit-scrollbar-thumb {
		background-color: #9b59b6;
		border-radius: 5px;
	}

	.wy-side-scroll {
		width: 300px;
		overflow-x: hidden;
		overflow-y: scroll;
	}

	.wy-side-nav-search {
		background-color: #222;
		width: 283px;
	}

	.wy-side-nav-search input[type="text"] {
		border-radius: 2px;
		border-width: 0.25em;
		border-color: #9b59b6;
		background-color: #101010;
		color: #fcfcfc;
		box-shadow: none;
		font-size: 100%;
	}
    
    .wy-side-nav-search input[type="text"]:focus {
		border: 2px solid #2cc36b;
        border-width: 0.25em;
    }
}
/* Sidemenu Items */
.wy-menu-vertical a {
	font-size: 100%;
}
/* Sidemenu -> Captions */
.wy-menu-vertical p.caption {
	color: #e67e22;
}
/* Sidemenu -> Selected Item */
.wy-menu-vertical li.current>a {
	color: #fcfcfc !important;
	background-color: #27ae60;
	border: none !important;
}
/* Sidemenu -> 1st-Level Items */
.wy-menu-vertical li.current {
	background-color: #222;
}
/* Sidemenu -> 1st-Level Items (Indiv. Entries) */
.wy-menu-vertical li.current a {
	color: inherit;
	border: none !important;
}

.wy-menu-vertical li.current a:hover {
	color: #000000;
	background: #e67e22 !important;
}
/* Sidemenu -> 2nd-Level Items */
.wy-menu-vertical li.toctree-l2.current>a {
	background-color: #9b59b6;
}
/* Sidemenu -> 3rd-Level Items */
.wy-menu-vertical li.toctree-l2.current li.toctree-l3>a {
	background-color: #404040;
}
/* Sidemnu -> 4th-Level Items */
.wy-menu-vertical li.toctree-l3.current li.toctree-l4>a {
	background-color: #555;
}
/* Tips */
.admonition.tip {
	background-color: #073a30;
}
/* Notes */
.admonition.note {
	background-color: #0c1e2b;
}
/* Warnings */
.admonition.warning {
	background-color: #750622;
}
/* Attention */
.admonition.attention {
	background-color: #564628;
}

.rst-content .warning .admonition-title {
	background-color: #d14;
}
/* TABLES */

/* Odd Rows */
.wy-table-odd td, .wy-table-striped tr:nth-child(2n-1) td, .rst-content table.docutils:not(.field-list) tr:nth-child(2n-1) td {
	color: #FFF;
	background-color: #151515;
	border-color: #27ae6050;
}
/* Even Rows */
.wy-table td, .rst-content table.docutils td, .rst-content table.field-list td {
	color: #FFF;
	background-color: #00000080;
	border-color: #27ae6050;
}
/* Table Header */
.wy-table thead, .rst-content table.docutils thead, .rst-content table.field-list thead {
	color: #e67e22;
	border-color: #27ae6050;
}

.wy-table thead th, .rst-content table.docutils thead th, .rst-content table.field-list thead th {
	background-color: #00000080;
	border: none;
}

.wy-table-bordered-all, .rst-content table.docutils {
	border: 1px solid #00000000;
}
/* BUTTONS */
.btn-neutral {
	color: #27ae60 !important;
	background-color: #0000001a !important;
}

.btn-neutral:visited {
	color: #2cc36b !important;
}
/* CODE & SNIPPET DISPLAY */

/* Language Select Tab */
.ui.tabular.menu .active.item {
	background: #000;
}

.sphinx-tabs .sphinx-menu a.active.item {
	border-color: #9b59b6 !important;
}

.ui.tabular.menu .item {
	padding: 0em 0.75em;
}

.rst-content code {
	border-color: #27ae60;
	border-radius: 4px;
	background: #000;
}

.sphinx-tab {
	border-color: #9b59b6 !important;
}

.ui.segment {
	background: #000;
}

div[class^='highlight'] {
	color: #666;
	border-color: #27ae60;
	background: #000;
}
/* SYNTAX HIGHLIGHTING */

/* Comments */
.c1 {
	color: #555;
}
/* Identifiers & Names */
.n {
	color: #999;
}
/* Keywords */
.k {
	color: #2cc36b;
}
/* Strings */
.s2 {
	color: #e67e22;
}
/* Parentheses & Enclosing Brackets */
.p {
	color: #bbb;
}
/* Function Names */
.nf {
	color: #6ab0de;
}
/* Class Names */
.nc {
	color: #9b59b6;
}
/* Operators */
.o {
	color: #999;
}
/* Highlighted Line */
.hll {
	background-color: #101010;
	border: 1px dashed #27ae604f;
}
}
