css:
@-moz-document domain(eve-rave.ch) {
	body {
		background-color: #24333d   
	}
	body > div#wrap {
		background: #24333d;
	}
	div#page-header > div.headerbar {
		filter: invert(100%);   
	}
	div#site-description h1, div#site-description a, div#site-description p {
		color: #306e74;   
	}
	div.forumbg {
		background-color: #354f4f;
	}
	dt {
		color: #fcfcfc;   
	}
	.bg1 {
		/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#0b0e27+0,283940+100 */
		background: #0b0e27; /* Old browsers */
		background: -moz-linear-gradient(left, #0b0e27 0%, #283940 100%); /* FF3.6-15 */
		background: -webkit-linear-gradient(left, #0b0e27 0%,#283940 100%); /* Chrome10-25,Safari5.1-6 */
		background: linear-gradient(to right, #0b0e27 0%,#283940 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
	}
	.bg2 {
		/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#101438+0,39515b+100 */
		background: #101438; /* Old browsers */
		background: -moz-linear-gradient(left, #101438 0%, #39515b 100%); /* FF3.6-15 */
		background: -webkit-linear-gradient(left, #101438 0%,#39515b 100%); /* Chrome10-25,Safari5.1-6 */
		background: linear-gradient(to right, #101438 0%,#39515b 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
	}
	div.post.bg1, div.post.bg2 {
		/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#8f989d+0,b4b7b8+100 */
		background: #8f989d; /* Old browsers */
		background: -moz-linear-gradient(left, #8f989d 0%, #b4b7b8 100%); /* FF3.6-15 */
		background: -webkit-linear-gradient(left, #8f989d 0%,#b4b7b8 100%); /* Chrome10-25,Safari5.1-6 */
		background: linear-gradient(to right, #8f989d 0%,#b4b7b8 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
	}

	ul.topiclist div.list-inner a {
		color: #a5e2ff;
		text-decoration: underline;
	}
	ul.topiclist div.list-inner a.topictitle {
		color: #a5e2ff;
		text-decoration: none;
	}
	dd {
		color: #fff;
	}
	label {
		color: #f8f8f8;
	}
	h1 a, h2 a, h3 a, h4 a, p > a {
		color: #8dcbff;
	}
	h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, p > a:hover {
		color: #8dcbff;
	}
	div.pagination > ul > li > a {
		background: #393939;
	}
	.search-box .inputbox, .search-box .inputbox:hover, .search-box .inputbox:focus, .search-box .button:hover {
		background: #dbdbdb;
	}
	h1, h2, h3, h4, h5,
	div.pagination {
		color: #cfcfcf;
	}

	div.content, div.content * {
		color: #000;
	}
	div.content a {
		color: #002f74;
	}
	div.postbody h3 > a {
		color: #fff700;
	}
}
