css:
@-moz-document url-prefix("https://avenoel.org/") {
html {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;
    background-image: url(https://i.imgur.com/g2zjz89.jpg);
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}
body {
    font-family: Avantgarde, TeX Gyre Adventor, URW Gothic L, sans-serif;
    background: rgba(255, 255, 255, 0);
}
main.container .container-content section {
    background-color: rgba(26, 26, 26, 0.8);
}
tbody {
    background-color: rgb(26, 26, 26);
}
td {
    background-color: rgba(26, 26, 26, 0.92);
}
tr {
    background-color: rgb(247, 40, 40);
}
.breadcrumb {
    background-color: rgb(247, 40, 40);
}
tr.active,
tr.active td {
    background-color: rgb(128, 0, 0) !important;
}
.btn.btn-primary {
    background-color: rgb(128, 0, 0) !important;
}
.navbar li.active a {
    background-color: rgb(128, 0, 0) !important;
}
.bloc-title {
    color: white;
}
label {
    color: white;
}
a,
th,
.topics-author,
.topics-amount,
.topics-date,
.topic-title {
    color: white;
}
.breadcrumb li.active {
    color: white !important;
}
.glyphicon-envelope.active {
    content: "\2710";
    background-image: url(https://i.imgur.com/7nyjwH7.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
}
.glyphicon-envelope {
    content: "\2709";
    background-image: url(https://i.imgur.com/6DO4eDh.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
}
.navbar-logo img {
    background-image: url(https://i.imgur.com/XIVNZMl.png)!important;
    max-width: 188px;
    max-height: 41px;
    width: 100%;
    height: auto;
    padding-bottom: 41px!important;
}

.topic-message .message-header .message-username a {
    font-family: 'Segoe UI'!important;
    font-size: 16px;
    color: #000;
    font-weight: 700;
    border-radius: 0;
    background-image: url(https://i.imgur.com/2EC7iAv.png);
    background-repeat: no-repeat;
    background-position: left;
    padding: 3px 5px 1px 22px;
}

.row.topic-message.odd {
   background-image: url(https://i.imgur.com/4XlusXv.png);
    background-color: #212121;
    border: 1px solid #212121;
    border-radius: 0
}
.row.topic-message {
    background-image: url(https://i.imgur.com/d1IYV6a.png);
    background-color: #212121;
    border: 1px solid #212121;
    border-radius: 0
}
.message-content {
    color: white;
}
.message-username a {
    color: white !important;
}
.message-infos {
    color: white;
}
.message-content-quote {
    background-color: rgb(64, 64, 64) !important;
}
.message-content-quote a,
.message-content-quote i {
    color: white;
}
.profile-wrapper,
.profile-infos tr,
.profile-title,
profile-stats {
    background-color: rgb(26, 26, 26) !important;
    color: white;
}
.profile-title {
    color: White !important;
}
section {
    color: white;
}
.pagination.pagination-topic a {
    color: white !important;
}
.chat-message,
.chat-message-username a {
    color: white !important;
}
.aside a {
    color: white !important;
}
.spoiler-content {
    background-color: rgb(64, 64, 64) !important;
}
.grey-btn.btn {
    background-color: rgb(128, 0, 0);
}
.glyphicon .glyphicon-refresh {}
.form-control.post-content,
.form-control {
    opacity: 0.85;
    color: black;
}
.glyphicon-off {
    content: "\2709";
    background-image: url(https://i.imgur.com/saEjLSX.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
}
.glyphicon-cog {
    content: "\2709";
    background-image: url(https://i.imgur.com/G9jVM87.png);
    color: transparent!important;
    font-size: 17px;
    display: inline-block;
    top: 1px;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
}

a:hover {
    color: white;
}
.form-group.preview {
    background-color: rgba(26, 26, 26, 0.9);
    color: white;
}
.profile-biography {
    background-color: rgb(26, 26, 26) !important;
    color: white;
}
a:focus {
    color: white;
}
.admin a {
    color: red !important;
}
.mod a {
    color: orange !important;
}
.glyphicon-envelope {
    color: white;
}
.active .topics-author a {
    color: white;
}
.chat-form {
    background-color: #1a1a1a;
}
.btn-danger {
    background-color: rgb(128, 0, 0);
    border-color: rgb(128, 0, 0);
}
.special a {
    color: #446CB3 !important;
}
.topics-author .special {
    color: #256cdd;
}
.post-content {
    background-image: url(https://image.noelshack.com/fichiers/2018/07/3/1518630764-741.png);
    background-position: center;
    background-repeat: no-repeat;
    background-size: 75px 75px;
}
.breadcrumb {
    font-family: 'Open Sans'!important;
    font-size: 12px;
    background-color: #fff;
    padding-left: 10px;
    border: 0 solid transparent!important;
    text-transform: Capitalize;
    margin-top: 5px;
    background-image: url(https://i.imgur.com/lMeDIgl.png);
    background-position: right;
    background-repeat: no-repeat;
    border-radius: 10px;
    height: 25px
}
.profile h2,
.h2 {
    color: #92b0ce;
    padding-top: 20px;
    font-size: 29px!important;
    font-family: 'comfortaaregular'!important;
    background-image: url(https://i.imgur.com/Kgb5ZrO.png)!important;
    background-repeat: no-repeat;
    background-position: left
}
.profile .profile-title,
.profile .profile-wrapper,
.profile .profile-footer,
.profile .profile-biography {
    background-image: url(https://i.imgur.com/IDul5gl.png);
    background-repeat: repeat;
    background-color: #fff!important;
    font-family: 'Open Sans'!important;
    font-size: 10px;
    color: #777;
    border: 1px solid #92b0ce;
    border-radius: 5px;
    box-shadow: 0 3.5px 0 #92b0ce
}
.profile h3,
.h3 {
    font-size: 15px!important;
    font-family: 'comfortaaregular'!important;
    color: #92b0ce;
    text-transform: none!important;
    background-color: transparent!important;
    text-shadow: none!important;
    border-bottom: none!important;
    box-shadow: none!important;
    border-radius: 0!important;
    filter: none!important
}
.topics-author a {
    color: white;
}
.btn.btn-primary:hover {
    background-color: rgb(26, 26, 26) !important;
}
.btn.grey-btn:hover {
    background-color: rgb(26, 26, 26) !important;
}
.navbar li a:hover {
    background-color: rgb(128, 0, 0) !important;
}.message-content-quote {
    background-color: rgb(80, 81, 85) !important;
    /*fond des citations*/
}
tr.active,
tr.active td {
    background-color: rgb(270, 40, 40) !important;
    /*couleur de fond lorsque le sujet est suivi et qu'il y a une réponse*/
}

.breadcrumb {
    background-color: rgb(26, 26, 26);
    /*fond de la bande où il est écrit "avenoel.org / forum*/

}

.topic-message aside.message-aside {
    width: 151px;
    padding: 4px 4px;
    background-image : url(https://i.imgur.com/ZaRijFo.png);
    height: 180px;
    border-radius: 0;

}
.navbar {
    background-image : url(https://i.imgur.com/rXYxg5g.png);
    border-radius: 0!important;
    border-bottom: 4px solid #83c7ba!important;
    text-shadow: none;


                    }

  tr
  {
    background-color: rgb(24, 24, 24)  /*fond de l'en tête de la liste des sujets*/
  }

tr
 {
    background-color: rgb(24, 24, 24); /*fond bouton suivre*/
    border-color: rgb(128, 248, 255);
    font-family :conthraxsemibold;
    color : white !important;
  }

}

