css:
#pr_sel li {
    border-color: #920000 !important;
    background: linear-gradient(#F00, #FF3030) !important;
    color: #fff !important;
    text-shadow: 0px 0px 0px #ffffff;
}
.n_head .pr_hd_bar {
    border-radius: 8px !important;
    border: .5px solid #9c0000;
    border-top-width: 0.5px;
    border-top-style: solid;
    border-top-color: rgb(156, 0, 0);
    border-top: 0;
}
.pr_hd_bar {
    background: linear-gradient(#FF2B2B, #AE0000);
}
#pr_hd li:hover,
.n_head li:hover {
    color: #0040bf;
    border: 1px solid #fff;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(174, 172, 255);
    border-bottom: 0;
    background: -moz-linear-gradient(top, #f4fbff 0, #c9dbff 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #f4fbff), color-stop(100%, #cedeff));
    background: -webkit-linear-gradient(top, #f4fbff 0, #cedeff 100%);
    background: -o-linear-gradient(top, #f4fbff 0, #cedeff 100%);
    background: -ms-linear-gradient(top, #f4fbff 0, #cedeff 100%);
    background: linear-gradient(to bottom, #f4fbff 0, #bed1f9 100%);
}
#pr_hd li,
.n_head li {
    color: #1c55c9;
    display: inline-block;
    text-align: center;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
    padding: 6.5px 0;
    width: 139px;
    border: 1px solid #fff;
    border-bottom: 0;
    background: #f4fbff;
    background: -moz-linear-gradient(top, #f4fbff 0, #fff 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #f4fbff), color-stop(100%, #96d3f7));
    background: -webkit-linear-gradient(top, #f4fbff 0, #96d3f7 100%);
    background: -o-linear-gradient(top, #f4fbff 0, #96d3f7 100%);
    background: -ms-linear-gradient(top, #f4fbff 0, #96d3f7 100%);
    background: linear-gradient(to bottom, #f4fbff 0, #fff 100%);
}
#name_nav a,
#pr_hd li,
#sm_nav > a:first-of-type,
.a_btn,
.friend_activity > div:last-of-type > div:first-of-type a,
.mv_under > div,
.n_head li,
.th_t,
.w_lnks a,
.yel_btn,
dt {
    font-weight: 700;
}
header li {
    border-color: #FFF;
    background: linear-gradient(#FBFCFF, #FFF) !important;
    color: #000 !important;
    text-shadow: 0px 0px 0px #ffffff;
}
