css:
@-moz-document domain("binance.com") {
::-webkit-scrollbar-track {
	background: #5C5555;
}

 ::-webkit-scrollbar-thumb {
	background: #9f9999;
}

 ::-webkit-scrollbar-thumb:window-inactive {
	background: #8f8888;
}

 ::-webkit-scrollbar-thumb:hover {
	background: #cfcccc;
}

.container {
	width: 98%;
	margin: 0 auto;
}

input,
.ipt.fillemail,
.ipt.pwd,
.nc_scale,
.verify .ipt {
	background-color: #333!important;
	color: #CCC!important;
	border-color: #666!important;
}

.kline-para .productSymbol {
	color: #CCC!important;
}


/* Black style */

body,
.wrap,
#pageLoading,
.noData,
.table,
.table td,
.main,
.hotMarkets li,
.markets-table,
.markets-table .con,
.markets-table .con .table th,
.markets-table .con .table td,
.markets-table .type li,
.market-box .market-con ul,
.market-box .market-con ul li,
.orderforms,
.orderforms .orderform-main>.f-fl,
.orderforms .orderform-main>.f-fr,
#kline-con,
.kline-timeline,
#chart_container,
.item-con,
.overvies,
.panel,
.partner,
.detail-lb,
.orderform,
.header,
#klinecon,
.table.table-striped .btn,
.depthMerge>span,
.units,
.verifyLayout {
	background: #222!important;
	color: #CCC!important;
	border-color: #333!important;
}


/* Grey Style */

.table th,
.accountInfo-lists li.th,
.depths,
.depths .depthstitle,
.tab-box li,
.searchFilter,
.item-title,
.kline-timeline span,
.orderforms .orderform-type span,
.depths .newest,
.market-box .market-t,
.panel .panel-title,
.detail-rb .orderform-type,
.depthbg,
.detail-rb .orderform-type span.cur,
.orderforms-inputs .iptwrap .jiantou,
.orderforms-inputs .iptwrap .jiantou i:first-child,
.orderforms-inputs .field.percent .col,
.main-nav>li .subNav a,
.logined ul a,
.depths .depthstitle .select select,
.kline-timeline ul li,
.accountInfo-lists li .items>div .btn,
.SecurityCenter .item .btn,
.verifyBottom {
	background: #333!important;
	color: #CCC!important;
	border-color: #333!important;
}


/* Text color white only */

.hotMarkets li .volume,
.filed .lable,
.orderforms-inputs label,
.orderforms-hd span.f-fr,
.orderforms-hd label,
.main-con .fee,
.basicInfo .right .fee {
	color: #CCC!important;
}


/* Border change only */

.accountInfo-lists,
.accountInfo-lists li {
	border-color: #111!important;
}


/* Mini chart on homepage top boxes */

.hotMarkets .svg-container svg {
	color: #666!important;
	opacity: 0.5!important;
}


/* Books bar graphs */

.askTable .zhuzhuang,
.tradefive-inner.ask tr .zhuzhuang {
	background-color: #332828!important;
}

.bidTable .zhuzhuang,
.tradefive-inner.bid tr .zhuzhuang {
	background-color: #283328!important;
}


/* basic charts*/

#chart canvas:nth-of-type(0) {
	filter: invert(0);
}

#chart canvas:nth-of-type(1) {
	background-color: #222!important;
	color: #ddd!important;
	filter: invert(0);
}

#chart canvas:nth-of-type(3) {
    color: #ddd!important;
	filter: invert(0) hue-rotate(45deg);
}

#chart canvas:nth-of-type(4) {
	filter: invert(0) hue-rotate(45deg);
}

#depth {
	filter: invert(0.93) hue-rotate(180deg);
}


/* Invert element */

.basicInfo .left .usertx,
.depths img {
	filter: invert(1);
}

.depthctrl .buy,
.depthctrl .buy.cur,
.depthctrl .sell,
.depthctrl .sell.cur,
.depthctrl .sellbuy,
.depthctrl .sellbuy.cur {
	filter: contrast(150%);
}


/* Highlighted tabs */

.orderforms .orderform-type span.cur,
.detail-rb .orderform-type span.cur,
.tab-box li.active,
.main-nav>li .subNav a:hover,
.logined ul a:hover,
.table.table-striped .btn:hover {
	color: #f3ba2e!important;
}

.kline-timeline .cur {
	color: black!important;
}

.depths td:hover,
.depths td:hover span {
	color: #eee !important;
}


/* Red highlights */

.depths .table td.magenta:hover span,
.magenta,
.magenta .gray,
.ng-binding.magenta {
	color: #dd6666 !important;
}

.orderforms-btn .btn.btn-sell {
	border-color: red!important;
}


/* Green highlights */

.green,
.green .gray,
.ng-binding.green {
	color: #55bb55!important;
}

.orderforms-btn .btn.btn-buy {
	border-color: green!important;
}


/* Orange Highlights */

.btn-orange,
.SecurityCenter .item .btn.btn-orange {
	background: #e8b342 !important;
	border-color: #e8b342 !important;
	color: #ccc !important;
}

.pagination ul li a {
	color: #999 !important;
}

.jiantou,
.btn {
	background-color: #333339 !important;
}

.jiantou:hover,
.btn:hover {
	background-color: #555 !important;
}

.importantInfo p {
	color: #ccc;
}

.orderforms-inputs input.ipterror {
	border-color: #ef4b5a !important;
	background-color: #443333 !important;
}

.orderforms .f-fl,
.orderforms .f-fr,
.orders .f-fl,
.orders .f-fr {
	width: 43% !important;
}

.depths .depths-wrap {
	overflow-y: scroll !important;
}
}
