css:
@-moz-document domain('moodle.zhaw.ch') {
.list-group-item  
  {
  	padding: .05rem 0.25rem;
  }
  div.no-progress
  {
    background-image: url("https://image.flaticon.com/icons/png/128/477/477153.png");
    background-size: 45px 45px;
    background-repeat: no-repeat;
    background-position: center;
  }

.header-right {
    margin-top: 70px;
    width: 440px;
    float: right;
    z-index: 2;
}

#inst430406 {
    visibility:hidden;
}

.header-container {
    background-color: #933770;}

a
{
	color: #933770;
	text-decoration: none;
  }

  #header-inner
  {
    background-color: #933770;
    max-height: 60px;
  }

  #page-wrapper
  {
    margin: 0 0 0 0;
  }

  #header
  {
    margin: 0 0 0 0;
    padding: 0 0 0 0;
    max-height: 0px;
  }

  #page
  {
    margin-top: 30px;
  }

  #page-site-index #page-content
  {
    margin-right: 0px;
  }

  .row
  {
    margin-right: 0;
  }

  #nav-drawer
  {
    top: 55px;
    height: calc(100% - 60px);
    margin-left: 0;
  }

  #nav-drawer-button
  {
    margin-top: 15px;
    font-size: 26px;
    background-color: transparent;
    position: relative;
    z-index: 2;
  }

  #logo-zhaw
  {
    margin-top: 55px;
      visibility:hidden;
  }

  #logo-department
  {
    margin-top: 0;
    vertical-align: top;
  }

  #logo-text-home img
  {
    vertical-align: top;
    margin: 4px 0 0 30px;
  }

  img[alt="Logo ZHAW"]
  {
  	/* targets any div with the attribute alt = "Logo ZHAW" */
    visibility: hidden;
  }

  #home-link
  {
    position: absolute;
    width: 150px;
    height: 25px;
    background-color: transparent;
  }

.list-group-item.active {
    background-color: #933770;
    border-color: #933770;
}
  #service
  {
    top: -38px;
    right: -100px;
    
  }

  #page-site-index #page-header::after
  {
    background-image: url(https://thumbs.gfycat.com/ForcefulThoseEarthworm-size_restricted.gif);
    margin-top: -85px;
  }
/* original banner: https://moodle.zhaw.ch/theme/zhawboost/pix/frontpagebanner.jpg */}
