    .result__check {
      pointer-events: none;
        color: #292929 !important;
        position: absolute;
        right: 100%;
        top: 0.3em;
        margin-right: 1em;
        font-size: 1em;
        width: 1em;
        white-space: nowrap;
    }
    .result__check:before{
        display: inline-block;
        float: right;
    }

    .result__check:visited {
        color: #FFF!important;
    }
    .result__check__tt {
        visibility: hidden;
        opacity: 0;
        background-color: #a3a3a3;
        background-color: rgba(138,138,138,0.9);
        padding: 0 1em;
        font-size: 0.76em;
        line-height: 2;
        position: absolute;
        bottom: 2.5em;
        left: -0.95em;
        z-index: 200
    }
    .result__check__tt:before {
        content: "";
        display: block;
        position: absolute;
        margin-left: -0.5em;
        bottom: -0.5em;
        left: 1.5em;
        border: 0.5em solid transparent;
        border-bottom-width: 0;
        border-top-color: #a3a3a3;
        border-top-color: rgba(138,138,138,0.9)
    }
    .result__check:hover .result__check__tt {
        visibility: visible;
        opacity: 1
    }
    
    
    
    
  

