css:
html,
body {
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
    background-color: #404040;
    color: #b3b3b3;
}

/* i really want this to be global */

.col_c_icon {
    content: url(https://i.imgur.com/uhLg1p3.png);
}
.maintitle {
    background: #252525;
    color: #e2e2e2;
    padding: 10px 10px 11px;
    font-weight: 300;
}
.userpopup dl {
    border-bottom: 1px solid #27B062;
}
#folder_list .total {
    background-color: #252525;
}
.likepop.fixed_inner {
    background-color: #303030;
}
#community_app_menu>li.active>a {
    border-bottom: 3px solid #27B062;
    color: #fafafb;
    font-weight: bold;
    margin-top: 0;
}
.ipsHasNotifications {
    padding: 0px 4px;
    height: 12px;
    line-height: 12px;
    background: #27B062;
    color: #fff !important;
    text-align: center;
    -webkit-box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.3), 0px 1px 0px rgba(255, 255, 255, 0.1) inset;
    -moz-box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.3), 0px 1px 0px rgba(255, 255, 255, 0.1) inset;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.3), 0px 1px 0px rgba(255, 255, 255, 0.1) inset;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    position: absolute;
    top: 4px;
    left: 3px;
}
.topic_buttons li a:hover,
.ipsButton:hover {
    transition: color .25s cubic-bezier(.25, .08, .44, 1);
    color: #fff !important;
    font-weight: bold !important;
    cursor: pointer;
    filter: opacity(80%) !important;
    border-color: #27B062 !important;
}
.popupClose {
    position: absolute;
    right: 16px;
    top: 12px;
    content: url(https://s14.postimg.org/8bsyigv81/iconmonstr-x-mark-1-16.png);
}
.ipsSideMenu ul li.active a {
    background: #27B062 url(http://devgaming.pl/public/style_images/dev/icon_check_white.png) no-repeat 6px 8px;
    color: #fff;
    font-weight: bold;
}
.bar {
    background: #252525;
}
#branding {
    padding-top: 10px;
    min-height: 190px;
}
.input_submit.alt {
    background: #252525 !important;
    border-color: #252525 !important;
    color: #aaa !important;
}
#billboardImage {
    border: 1px solid #252525 !important;
    background: #252525 !important;
}
cite.ipb {
    border-left: 2px solid #989898;
    background: #f6f6f6;
    background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #252525), color-stop(100%, #303030));
    background-image: -o-linear-gradient(top, #f6f6f6, #e5e5e5);
    border-bottom: 2px solid #303030;
    border-right: 2px solid #e5e5e5;
}
.maintitle a {
    color: #ffffff;
}
.ipsType_pagedesc {
    color: #efefef !important;
    line-height: 1.5;
}
.groupStats {
    display: inline;
    background-color: #191919 !important;
    background-image: -moz-linear-gradient(center top, #FFFFFF 0%, #E5E5E5 100%);
    padding: 10px;
    border: 1px solid #333333 !important;
    color: #fff !important;
}
.ipsBox table_wrap {
    background-color: #292929 !important;
}
.ipsBadge_green {
    background: #27B062;
}
.cke_skin_ips .cke_dialog_title {
    background: #27B062 !important;
}
.cke_skin_ips textarea.cke_dialog_ui_input_textarea {
    background-color: #292929 !important;
}
.cke_skin_ips div.cke_dialog_ui_input_text,
.cke_skin_ips div.cke_dialog_ui_input_password,
.cke_skin_ips div.cke_dialog_ui_input_textarea {
    border: 1px solid #191919;
    background: #292929 !important;
}
.cke_skin_ips input.cke_dialog_ui_input_text,
.cke_skin_ips input.cke_dialog_ui_input_password {
    background-color: #292929 !important;
}
.cke_skin_ips .cke_dialog_contents {
    background-color: rgb(66, 66, 66);
    border-top: 1px solid #4e4e4e;
}
#bbcode-description {
    color: #b1b1b1 !important;
    white-space: normal !important;
    word-wrap: break-word;
}
.cke_skin_ips .cke_dialog_footer {
    background-color: #424242 !important;
    text-align: right;
}
.cke_skin_ips .cke_dialog_ui_input_select {
    background-color: #292929 !important;
    border: 1px solid #191919;
    padding: 3px;
}
.cke_skin_ips .cke_wrapper {
    padding: 0px 5px 0px 3px !important;
    border: 2px solid rgba(4, 4, 5, 0.29) !important;
    background-color: #303030 !important;
    background-image: none !important;
}
.cke_skin_ips .cke_toolbox {
    clear: both;
    background-color: #303030 !important;
    padding-bottom: 1px;
}
.cke_skin_ips *,
.cke_skin_ips a:hover,
.cke_skin_ips a:link,
.cke_skin_ips a:visited,
.cke_skin_ips a:active {
    color: #27B062 !important;
}
.cke_skin_ips .cke_contents iframe {
    background-color: rgba(0, 0, 0, 0.77) !important;
    border-color: rgba(0, 0, 0, 0.30) rgba(0, 0, 0, 0.30) rgba(0, 0, 0, 0.30) rgba(0, 0, 0, 0.30) !important;
}
.cke_skin_ips a.cke_toolbox_collapser,
.cke_skin_ips a:hover.cke_toolbox_collapser {
    background-color: #484848 !important;
    border: 1px outset rgba(0, 0, 0, 0.6) !important;
}
.cke_top {
    background: none !important;
}
.cke_show_borders {
    color: #fff !important;
    background: rgba(0, 0, 0, 0) !important;
}
.CSS1Compat {
    background: rgba(0, 0, 0, 0) !important;
}
select {
    color: #d8d8d8;
    background-color: #303030;
    outline: none;
    border-color: #000000;
}
.ipsType_subtitle {
    text-align: center;
    color: #fff !important;
    padding-top: 10px;
    padding-bottom: 10px;
    background-color: #252525;
}
.ipsSettings_sectiontitle {
    color: #ffffff;
}
.no_messages {
    background: rgb(23, 24, 29);
    color: #1c2837;
}
body#ipboard_body fieldset.submit,
body#ipboard_body p.submit {
    padding: 15px 6px 15px 6px;
    text-align: center;
    background-color: #303030;
}
a {
    color: #a9a9a9;
    text-decoration: none;
}
.ipsBox_containerDev {
    background: #303030;
}
.desc,
.desc.blend_links a,
p.posted_info {
    color: #8a8a8a !important;
}
.desc.lighter,
.desc.lighter.blend_links a {
    color: #6e6c77;
}
.ipsSideBlock {
    background: rgb(48, 48, 48);
    border: 1px solid rgb(9, 10, 12);
}
.general_box {
    background: rgba(25, 25, 25, 0.3);
}
body#ipboard_body fieldset.submit,
body#ipboard_body p.submit {
    background: rgb(64, 64, 64);
}
.poll_question ol {
    background-color: #191919;
}
.poll_question h4 {
    background-color: #141414;
    color: #fff;
}
.progress_bar span {
    background: #27B062 url(http://devgaming.pl/public/style_images/dev/gradient_bg.png) repeat-x left 50%;
    color: #fff;
}
.progress_bar span {
    background: #27B062 url(http://devgaming.pl/public/style_images/dev/gradient_bg.png) repeat-x left 50%;
}
.statistics {
    background: rgb(48, 48, 48);
    border: 1px solid rgb(9, 10, 12);
}
#community_app_menu>li.active>a {
    border-bottom: 3px solid #27B062;
}
#secondary_nav_app_info:hover {
    border-top: 3px solid #252525;
}
#secondary_nav_app_info {
    background: rgb(48, 48, 48);
}
#user_link.menu_active,
#notify_link.menu_active,
#inbox_link.menu_active {
    color: #fff;
    background-color: #252525;
}
.ipsHeaderMenu {
    background: #252525;
    padding: 10px;
    overflow: hidden;
    width: 350px;
}
#user_link_menucontent #links li {
    text-shadow: none;
}
.input_text,
.ipsTagBox_wrapper {
    color: #fff;
    outline: none;
    background: #303030;
    border: none;
}
.ipsButton_secondary {
    background: rgba(10, 10, 10, 0.68);
    color: #aaa;
    outline: none;
    transition: background .2s cubic-bezier(.56, .01, .73, .96);
    box-shadow: none !important;
    border: solid 1px #000;
    border-radius: 0 !important;
    opacity: 1 !important;
    filter: none !important;
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
    -webkit-box-shadow: 0px 0px 47px -1px rgba(0, 0, 0, 0.90);
    -moz-box-shadow: 0px 0px 47px -1px rgba(0, 0, 0, 0.90);
}
.topic_buttons li.important a,
.topic_buttons li.important span,
.ipsButton .important,
.topic_buttons li a,
.topic_buttons li span,
.ipsButton {
    background: #252525;
    color: #aaa;
    border: solid 1px #000 !important;
    outline: none;
    transition: background .2s cubic-bezier(.56, .01, .73, .96);
    box-shadow: none !important;
    border-radius: 0 !important;
    opacity: 1 !important;
    filter: none !important;
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
}
.ipsBox {
    background: #303030;
}
.message.unspecific {
    background-color: #252525;
    border-color: #252525;
    color: #d2d2d2;
}
.general_box h3 {
    background: #252525;
    color: #c5c5c5;
}
.ipsButton_secondary a {
    color: #bbbbbb;
}
.ipsVerticalTabbed_tabs>ul {
    border-top: 1px solid #252525;
    border-left: 1px solid #252525;
}
.ipsVerticalTabbed_tabs li.active a {
    border-right: 1px solid #252525;
    background: #303030;
    color: #fff;
}
.ipsVerticalTabbed_tabs li {
    background: rgb(9, 10, 12);
    color: #ffffff;
    border-bottom: 1px solid #252525;
}
.ipsVerticalTabbed_tabs li a:hover {
    background: #303030;
    color: #ffffff;
}
.ipsBox_container {
    background: #252525;
    border: 0;
}
.status_update {
    background: #252525;
}
.ipsFilterbar li.active a,
.ipsFilterbar.bar.altbar li.active a {
    background: #27B062;
}
.block {
    filter: blur(2px) !important;
}
.header {
    background: rgb(9, 10, 12);
    color: #a9a9a9;
}
.row1 {
    background-color: #252525 !important;
}
.row2 {
    background-color: #252525 !important;
}
td,
th {
    border-color: #404040;
}
.buyItem {
    border-top: none;
}
.rightBar {
    color: #fff;
    background: linear-gradient(#252525, #303030) !important;
}
.input_submit {
    color: #aaa;
    outline: none;
    transition: background .2s cubic-bezier(.56, .01, .73, .96);
    box-shadow: none !important;
    border: none !important;
    border-radius: 0 !important;
    opacity: 1 !important;
    filter: none !important;
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
}
input[type=text] {
    color: #aaa;
    outline: none;
    transition: background .2s cubic-bezier(.56, .01, .73, .96);
    box-shadow: none !important;
    border: none !important;
    border-radius: 0 !important;
    opacity: 1 !important;
    filter: none !important;
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
}
.upload {
    border: 1px solid #252525 !important;
    background: #303030 !important;
    color: #d0d0d0 !important;
}
.rules {
    border: 1px solid #252525 !important;
    background: #303030 !important;
    color: #ddd !important;
}
textarea {
    background-color: #252525;
    outline: none;
    color: #fff;
    border-color: #000;
}
.message.error {
    background-color: #1d0000;
    border-color: #da788e;
    color: #a50125;
}
.colorDev2 {
    color: #737373;
}
::-webkit-scrollbar-thumb {
    background: #27B062;
}
::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #040406;
    width: 14px;
    height: 14px;
}
::-webkit-scrollbar-button {
    display: none;
}
::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #040406;
    width: 14px;
    height: 14px;
}
.popupWrapper {
    background-color: #27B062;
    padding: 4px;
    -webkit-box-shadow: 0px 0px 30px rgb(39, 176, 98);
    -moz-box-shadow: 0px 0px 30px rgb(39, 176, 98);
    box-shadow: 0px 0px 30px rgb(39, 176, 98);
    -moz-border-radius: 4px;
}
.post_block {
    background: #303030;
    border-bottom: 1px solid #252525;
}
.post_body .post {
    color: #ececec !important;
}
ul.post_controls a:hover {
    color: #ffffff;
}
ul.post_controls a {
    color: #afafaf;
}
.cke_skin_ips .cke_contents iframe {
    background-color: #fff;
    border-width: 1px;
    border-style: solid;
    border-color: #000000;
}
.pagination .back a,
.pagination .forward a {
    background: #252525;
    color: #dadada;
    border: none;
}
.pagination .back a:hover,
.pagination .forward a:hover {
    background: #27B062;
    border: none;
    color: #fff;
}
.pagination .pages li.active {
    background: #27B062;
    color: #fff;
    font-weight: bold;
    border: none;
    padding: 1px 5px;
}
ul.mini_pagination li a {
    background: #303030;
    color: #fff;
    border: 1px solid #252525;
}
a:hover {
    color: #27B062;
}
.ipsVerticalTabbed_tabs li a {
    color: #8c8c8c;
}
.popupInner h3 {
    background: #404040;
    color: #fff;
}
#backtotop {
    background: #303030;
}
#backtotop:hover {
    background: #27B062;
    color: #fff;
    opacity: 1;
}
#ips_photoWrap {
    border: 1px solid #252525 !important;
}
.ips_photoPreview {
    border: 1px solid #252525 !important;
}
li.ips_option {
    border: 1px solid #252525 !important;
}
#billboardRules {
    border: 1px solid #252525 !important;
    background: #303030 !important;
    color: #e6e6e6 !important;
}
#header_bar {
    padding: 0;
    text-align: right;
    background: #252525;
}
#community_app_menu>li>a:hover,
#community_app_menu>li>a.menu_active {
    border-bottom: 3px solid #27B062;
    color: #fafafb;
}
.ipsButton_secondary:hover {
    transition: color .25s cubic-bezier(.25, .08, .44, 1);
    color: #fff !important;
    font-weight: bold !important;
    cursor: pointer;
    filter: opacity(80%) !important;
    background-color: #171717 !important;
    border-color: #27B062 !important;
}
.userpopup {
    background: #303030;
}
.popupInner h3 a {
    color: #ffffff;
}
.desc,
.desc.blend_links a,
p.posted_info {
    color: #ececec;
}
p.citation {
    padding: 8px 10px;
    border-left: 2px solid #252525;
    background: #f6f6f6;
    background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #252525), color-stop(100%, #303030));
    border-top: 2px solid #252525;
    border-right: 2px solid #252525;
    -moz-border-radius: 5px 5px 0 0;
    -webkit-border-radius: 5px 5px 0 0;
    border-radius: 5px 5px 0 0;
    font-weight: bold;
    overflow-x: auto;
}
blockquote.ipsBlockquote {
    padding: 10px;
    border: 2px solid #252525;
    border-left: 2px solid #0d0e10;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    background: #303030;
    margin: 0 0;
    overflow-x: auto;
}
