css:
@-moz-document domain("next.voz.vn") {
#header, body {
    background: #201f25;
}
.comments {
    background: white;
}
.content__sidebar--right {
    width: 100%;
    margin-bottom: 0;
}
.content {
    flex-direction: column-reverse;
}
.heading.heading--thread-detail {
    margin-bottom:20px;
}
.comments__item, .thread-section, .thread-list__anchor, #thread-heading {
    background: #F5F5FF;
}
.thread-section {
    padding: 0;
    margin-top: 0;
    border-top: none;
}
.comments__item, #section-thread-detail > div > div:nth-child(1) {
    padding: 1px;
}
.comments__item, #section-thread-detail > div > div:nth-child(1):not(.thread-section__comments), .thread-list, #thread-heading {
    border: 1px solid #B0BDC6;
}
.comments .scroll-item {
    border-color: #F2F2F2;
}
.user-profile-brief {
    background: #E1E4F2;
    padding: 6px 2px
}
.bound, .thread-section--detail .thread-section__detail {
    margin: 0 6px
}
.user-profile-brief__link-wrap {
    margin-left: 3px
}
.button-blue-blur[href$="comment"] {
    background-image: url(https://vozforums.com/images/buttons/quickreply.gif), url(https://i.imgur.com/moOhIvw.gif);
    width: 25px;
    height: 22px;
    color: transparent!important;
    box-shadow: none;
    padding: 0;
    border: none;
    float: right;
    margin: 8px;
}
.user-profile-brief__button {
    right: 10px;
}
blockquote {
    background: #E1E4F2;
    border: 1px inset;
    margin: 20px;
    margin-top:5px;
}
#wrapper #content .container, .boxes--feedback, .thread-sticky__title, .thread-sticky__main, .thread-sticky__meta {
    background: #F2F2F2;
}
#wrapper #content .container {
    box-shadow: 0 0 5px 0 #CCC;
}
.thread-section--detail .thread-section__user, .thread-section--detail .thread-section__detail {
    padding-left: 0;
    padding-right: 0;
}
#section-thread-detail > div > div:nth-child(1) {
    margin-bottom: 6px;
}
.avatar__img {
    border-radius: 0;
    margin-right: 3px;
}
.avatar.avatar--standard .avatar__img img {
    max-width: none;
    width: auto;
    height: auto;
}
.user-profile__username {
    color: #23497C;
    font-size: 18px;
    font-weight: normal;
}
.avatar__default, [id^="div-gpt-ad"], .button.button-gradient img {
    display: none;
}
.avatar.avatar--standard .avatar__img {
    width: auto;
    height: auto;
}
#section-thread-detail > div > div:nth-child(1) > div.thread-section__detail > a {
    margin-right: -3px
}
.heading--thread-detail .heading__main h1, .heading h1 {
    font-size: 14px;
    font-weight: 700
}
blockquote {
    padding: 7px
}
blockquote strong:nth-of-type(1):before {
    content:"Originally Posted by ";
    font-weight: normal;
}
* {
    font-family: verdana!important
}
.thread-list__header h2 {
    font-size: 13px;
    color: #23497C
}
p, .bound {
    font-size: 13px;
    line-height: 1.5
}
.user-profile-brief__info {
    padding-left: 0
}
.button.button-gradient {
    background-image: url(https://vozforums.com/images/buttons/newthread.gif), url(https://i.imgur.com/aln9ilG.gif);
    color: transparent;
    width: 110px;
    height: 26px
}
#header > div {
    padding-right: 0
}
.thread-list__item {
    border: 1px solid white
}
._number-comment span, .viewing-total-interval, .heading--thread-detail__sub-info ._column {
    color: black
}
.thread-sticky__meta ._viewing, .thread-sticky__meta ._number, .thread-sticky__comment ._date {
    color: #5066b9
}
.thread-sticky__progress-bar__current, .comments-form__has-to-login, .thread-list__item__indicator ._progress-bar {
    background: #738FBF
}
.content__main > .heading {
    background-image: url(https://vozforums.com/images/gradients/gradient_tcat.gif), url(https://i.imgur.com/Txzv8Er.gif);
    background-repeat:repeat-x;
    background-color: #869BBF;
    background-position: top left;
    padding: 3px 15px
}
.thread-section__first-comment .thread-section__detail {
    border-bottom:0
}
.comments__footer__right-column img, .thread-section__footer__right-column img {
    width: 26px;
    border-radius: 5px;
    padding: 2px 5px;
    background: #c9c9c9;
}
.comments__footer__right-column {
    margin-top:6px
}
.comments__footer__right-column ._button strong, .thread-section__footer__right-column ._button strong, .button-dotted.tooltip__trigger {
    display:none
}
/* ribbon - might not stable nor necessary */
 .comments__header, .thread-section__user {
    margin-bottom:10px;
    padding-top:25px;
    padding-bottom:0;
    background-color:#5C7097;
    background-image:url(https://vozforums.com/images/gradients/gradient_thead.gif), url(https://i.imgur.com/rKI3Xmp.gif);
    background-position: top left;
    background-repeat:repeat-x;
}
.tooltip.user-profile-brief__button {
    width:100%;
    margin-top:-37px;
}
.user-profile-brief__button ._number {
    width:100%;
    right:0;
    color:white!important;
    direction: rtl;
    top:30px;
}
.user-profile-brief__button ._number::before {
    content:"Đả đảo Voz tân";
    position:absolute;
    direction: ltr;
    left:17px;
}
}

@-moz-document url-prefix("https://next.voz.vn/f") {
.content__main > .heading h1 {
    color: white;
    line-height: 0;
    font-size: 0;
}
}

@-moz-document url("https://next.voz.vn/f/diembao") {
.content__main > .heading h1:after {
    content:"F33 : Điểm Báo";
    font-size: 15px
}
}

@-moz-document url("https://next.voz.vn/f/chuyentrolinhtinh") {
.content__main > .heading h1:after {
    content:"F17 : Chuyện trò linh tinh";
    font-size: 15px
}
}
