css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("angularjs.org") {

/*[[theme]]*/

::-moz-selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}
::selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}

body, #navbar-sub, .hero, .check-element, .modal, .modal-footer {
  background-color: var(--bg) }
#navbar-main .navbar-inner, .footer, .button.button-secondary, .button.button-secondary.has-shield, .announcement-bar, .well, .popover-content, .table-bordered > thead > tr > th, .variables-matrix th, .content-inner, .main-body-grid > .grid-left {
  background-color: var(--bg-2) }
oddrow, #navbar-notice .navbar-inner, .popover-title, .table-striped > tbody > tr:nth-child(2n+1) > td, .sup-header, .variables-matrix tr:nth-child(2n) td, .date-header span, .post-footer {
  background-color: var(--bg-odd) }
input, textarea, .navbar .navbar-search .search-query, .navbar .nav .dropdown-menu a, .docs-search, .dropdown-menu, .home-link, .blog-pager-older-link {
  background-color: var(--bg-input);
  color: var(--fg-input) }
input:focus, select:focus, textarea:focus, .navbar .navbar-search .search-query:focus, .navbar .navbar-search .search-query:hover, .navbar .nav .dropdown-toggle:hover, .navbar .nav .dropdown-toggle:focus, .navbar .nav .dropdown-menu a:hover, .navbar .nav .dropdown-menu a:focus, .home-link:hover, .blog-pager-older-link:hover {
  background-color: var(--bg-input-hover);
  color: var(--fg-input-hover) }
div[style*="background"], ul[style*="background"], span[style*="background"] {
  background-color: transparent !important }
nobg {
  background-image: none }

body, .navbar .navbar-search i, .navbar .navbar-search input.search-query, .button.button-plain, .button.button-plain i, .text-body {
  color: var(--fg) }
div[style*="color"], span[style*="color"] { color: var(--fg) !important }
#header, #header h1, h1, h2, h3, h4, h5, .text-display-1, .text-title, div.widget > h2 {
  color: var(--fg-2) }
.muted, .nav-breadcrumb-entry > .divider, .aside-nav a, .aside-nav a:link, label, .api-profile-header-structure, .navbar, .post-footer {
  color: var(--fg-muted) }

.label:link {
  color: #eee }
.label.type-hint-date, .label.type-hint-array, .label.type-hint-object {
  color: #000 }

a, a:link, .nav-index-group .nav-index-listing.current a {
  color: var(--fg-link) }
.nav-index-section a, a > span[style*="color"] { color: var(--fg-link) !important }
a:hover, a:visited:hover, .aside-nav a:hover {
  color: var(--fg-link-hover) }
a:visited, .aside-nav a:visited {
  color: var(--fg-link-visited) }

noborder, pre code, .alert, .table-bordered > thead > tr > th, .variables-matrix th + th {
  border: none }
custom-border, input, textarea, pre, code, .prettyprint, input[readonly] {
  border: var(--border) }
bordercolor, .footer, h1, h2, h3, h4, h5, .button.button-secondary.has-shield, .nav-tabs, .popover-title, .table-bordered > tbody > tr > td, .table-bordered, .variables-matrix, .variables-matrix td, .variables-matrix td + td, .check-element, .modal-footer, hr, .post-footer, .footer-outer {
  border-color: var(--border-color) }
.dropdown-menu .divider, hr {
  background-color: var(--border-color) }

.toc-close {
  box-shadow: none }
.modal-footer {
  box-shadow: 0 0 5px #000 }

success, success a {
  background-color: var(--bg-success);
  color: var(--fg-success) }
info, info a, .alert-info {
  background-color: var(--bg-info);
  color: var(--fg-info) }
warning, warning a, .alert-warning {
  background-color: var(--bg-warning);
  color: var(--fg-warning) }
error, error a, .alert-danger {
  background-color: var(--bg-error);
  color: var(--fg-error) }
disabled, input[readonly] {
  background-color: var(--bg-disabled);
  color: var(--fg-disabled) }

.picker select {
  color: var(--fg-input) }
.picker::after {
  border-top-color: var(--fg-input) }

.picker, .btn, .learn-link, .nav > li > a:hover, .toc-close {
  color: var(--fg-input);
  background-image: linear-gradient(var(--bg-input-hover), var(--bg-input));
  border-color: var(--border-color);
  text-shadow: 0.3px 0.3px 0.5px #000 }
.picker optgroup {
  background: var(--bg-odd) }
.picker option {
  background: var(--bg-2) }

.btn.active {
  background-image: linear-gradient(var(--bg-input), var(--bg-input-hover)) }

.nav-tabs > .active > a, .nav-tabs > .active > a:hover, .nav-tabs > .active > a:focus, .picker select:hover, .picker select:focus, .btn:hover, .btn:focus, .button.button-plain:hover, .button.button-plain:hover i {
  background-color: var(--bg-input) !important;
  border-color: var(--border-color) !important;
  color: var(--fg-input-hover) !important }

.prettyprint {
  background-color: var(--syntax-bg) !important }
pre, code, p > code, .nocode, ol.linenums li {
  background-color: var(--syntax-bg);
  text-shadow: 0.3px 0.3px 0.5px rgba(0, 0, 0, 0.5) }
pre, code, .prettyprint, .nocode, .pln { color: var(--syntax-fg) }
.kwd, .tag, .pun, .linenums .tag { color: var(--syntax-builtin) }
.atn { color: var(--syntax-keyword) }
.dec, .typ { color: var(--syntax-function) }
.atv, .str { color: var(--syntax-string) }
.num, .lit { color: var(--syntax-number) }
.com { color: var(--syntax-comment) }

.code-annotation, .runnable-example-frame {
  border-color: var(--border-color);
  background-color: var(--syntax-bg-2);
  box-shadow: 0 0 1px var(--fg-link) }

.prettyprint.linenums {
  box-shadow: 40px 0px 0px var(--bg-2) inset, 41px 0px 0px var(--border-color) inset }

img[src^="img/guide"] {
  background-color: rgba(255, 255, 255, 0.2) }

}
