@-moz-document domain("linux.org.ru") {
/* Заточена под использование с темой LOR "Waltz". На других темах могут вылезти несостыковки в цветовой гамме (с Black гарантированно лучше даже не пробовать, хотя если вы предпочитаете Black — зачем тогда юзерскрипт, вы же олдфаг? :). */

    /* Общие установки, дефолтные для каждого элемента на странице (т.е. если в отдельном элементе не переопределить указанные здесь свойства, то они такими и будут в этом элементе) */
    html,
    body {
        font: 1.05em normal;
        font-family: "Roboto", "Arial", sans-serif;
        background: #fff !important;
        color: #38000b !important;
        line-height: 1.05;
    }
    
    @font-face {
        font-family: "Roboto";
        font-style: normal;
        font-weight: 400;

        src: local("Roboto"),
             url("/fonts/roboto.woff2") format("woff2"),
             url("/fonts/roboto.woff") format("woff");
    }

    /* Виджеты на `Главной` (на десктопной версии у левого края страницы, на мобильной внизу страницы): `Опрос`, `Топ-10`, `Галерея`, `Облако в штанах` */
    .boxlet {
    background-color: #fff !important;
        border: 1px solid #440101;
        border-radius: 0;
    }

    /* Блок кода */

	
    .code.lc {
        background-color: #f5f5f5;
        border: 1px solid #000101 !important;
    }

    pre,
    code,
    .code {
        font-family: Courier New;
        color: #030303 !important;
    }

    span code {
        background-color: #fff !important;
        color: #500 !important;
        font-size: 0.9em;
    }

    /* Блок цитаты */
    blockquote {
        font-style: normal;
        color: #660101;
        background: #fff;
        border-left: 3px solid;
        border-right: 3px solid;
        border-bottom: 1px solid;
        border-top: 1px solid;
        border-bottom-left-radius: 0px;
        border-bottom-right-radius: 0px;
        border-top-left-radius: 0px;
        border-top-right-radius: 0px;
        border-color: #dcdcdccc;
    }

    /* Ссылки */
    a {
        font-family: 'Ubuntu Bold';
        color: #0d1f84 !important;
    }

    /* Посещенные ссылки */
    a.visited {
        color: #000000 !important;
    }

    /* Информационные блоки (`Активные теги`, `Мини-новости`) */
    .infoblock {
        background: #fff !important;
        border-left: 2px solid;
        border-top: 1px solid;
        border-radius: 0;
    }

    /* Блок сообщения (комментарий) */
    .messages .msg {
        background: none repeat scroll 0% 0% #fff;
        border: 1px solid #6c0404;
        border-radius: 0;
        border-left-width: 2px;
        border-right-width: 0;
        border-bottom-width: 0;
    }
    
    /* Запощенный комментарий */
    .messages .msg:target {
        background: #f2f0f0;
    }

    /* Нумерация страниц в тредах */
    .page-number {
        border: 2px solid;
        border-radius: 1em;
        background-color: #fff;
    }

    .nav-buttons a {
        color: #c2640e !important;
    }

    #bd .btn {
        border: 1px solid #6c0404;
        color: #860101;
    }

    #bd a.tag {
        background-color: #fff !important;
        color: #685de4 !important;
        font-weight: bold;
    }

    /* Кнопки фильтрации по категориям */
    #bd .btn-selected {
        background-color: #e8cccd;
        border-radius: 1em;
    }

    #bd .btn-default {
        background-color: #d9d2d2;
        border-radius: 1em;
    }

    #bd h1,
    #bd h2 {
        color: #1b307a;
    }

    /* `Трекер` */
    #bd .forum table th {
        background: #fff !important;
        /* цвет заголовка таблицы `Трекера` и что-то еще (заголовок, последнее сообщение) */
        color: #7e0000;
        border: 1px solid #6e0303;
        /* контур заголовка таблицы `Трекера` */
    }

    #bd .forum table thead,
    #bd .forum table tfoot {
        color: #ededed;
    }

    #bd .forum table tbody td {
        border: 0px solid #b1b1b1;
        /* контур таблицы `Трекера` */
    }

    /* теги в `Трекере` */
    .message-table .tag {
        background: #f7d7d7;
    }

    /* строки таблицы `Трекера` */
    .message-table tr {
        border: 0px solid;
    }

    /* Выделенная указателем мыши строка в таблице `Трекера` */
    .message-table tr:hover {
        background: #fffdfd36 !important;
        border: 1px solid #5a0000;
    }

    /* ссылки на разделы `Форума` (%юзернейм% в %разделе `Форума`%) */
    .message-table a.secondary {
        color: #9b2801 !important;
    }

    /* Логотип `LINUX.ORG.RU` */
    #sitetitle {
        color: #01772d !important;
    }

    /* Левая колонка таблицы в `Уведомлениях` */
    #bd .icon-reply-color,
    #bd .icon-tag-color,
    #bd .icon-user-color {
        color: #4d0000;
    }

    #whois_userpic {
        background: none repeat scroll 0% 0% #fff;
        border: 1px solid #0000004d;
        border-radius: 0.5em;
    }

    /* Кнопка, связанная с основным действием в блоке кнопок (например, `Поместить` по сравнению с `Предпросмотром` и `Отменить` в форме добавления сообщения (комментария)) */
    #bd .btn-primary {
        background-color: #fff;
        border: 1px solid #4b01017a !important;
        border-radius: 1em;
    }

    #bd .btn-danger {
        background-color: #fff;
        color: #042d38;
        border: 1px solid #0000004d;
        border-radius: 1em;
    }

    img {
        filter: contrast(100%) brightness(90%);
    }

    /* Звёзды! */
    .stars {
        color: #aa010b;
        background-color: #dedede;
        border: 0px solid #000;
        border-radius: 0.5em;
    }

    .group-label {
        background-color: #9e1c1c;
    }

    /* `Звезда` добавления `В избранное`, `око` добавления в `Отслеживать` топика */
    .fav-buttons .selected [class^="icon-"] {
        color: #ec2a05;
    }

    /* Комментарий к юзеру */
    .user-remark {
        color: #3a3a3a;
    }

    #loginGreating a {
        color: #710105 !important;
        text-decoration: none;
    }

    #loginGreating a:before,
    .head a[href*="people"]:before {
        content: "@";
        font-size: 1em;
    }

    /* Метка группы в `Похожих новостях` (`Новости`, `Галерея`, `Форум`, `Трекер`, `Уведомления`, `Поиск`) */
    span.group-label {
        background-color: #fff;
        color: #180000;
        border: 1px solid #000;
        border-radius: 1em;
    }
}
