css:
@-moz-document domain("rivalregions.com") {
/*
 * Global
 */

body
{
	background: url("/*[[background]]*/") no-repeat center center fixed;
	background-size: cover;
}

/* Regio overview*/
#region_scroll
{
	height: 85vh;
}

/* Remove green color from auction menu */
#auction_menu
{
	background-color: transparent;
}

/* Make scrollbox higher */
#my_messages,
.news_content,
.jspContainer
{
	height: 75vh !important;
}

/* Scrollbar if there is a textbox underneath */
.minwidth #chat_messages
{
	height: 65vh !important;
}

/* Chat window in parlement laws */
.offer_show_chat #chat_messages
{
	height: 35vh !important;
}

/* Longer list of partys */
#content:not(.konf_maker) #parliament_scroll
{
	height: 38vh !important;
	max-height: none !important;
}

/* Fix transparent background on home, work, storage, and news page */
#index_parliament_list,
#war_active_list,
.work_item,
.storage_item,
.quests_get,
.news_list_1,
.news_list_2,
.news_list_3
{
	background-color: #393939 !important;
}

/* Increase textbox height */
#message
{
	height: 12vh;
}

/* Make empty parlement seat transparent */
.par_seat_com:not(.ps) {
	background: rgba(40,40,40,0.1);
	border: 1px solid rgba(40,40,40,0.5) !important;
}

/*
 * Home
 */

#index_perks
{
	height: 35vh;
}

/* Fix scrollbox height */
#slide_output
{
	height: 75vh !important;
}

/*
 * Store 
 */

/* Display current of items in store bar*/
.store_bar
{
	background: #586e75 !important;
}

/*
 * Home
 */

#index_perks
{
	height: 35vh;
}

#index_perks_t
{
	height: 24vh;
}

#index_chat #chat_messages
{
	height: 31vh;
}

#chat_messages .jspContainer
{
	max-height: none !important;
}

#index_parliament_list
{
	height: auto !important;
	max-height: none !important;
}

#index_parliament_list .jspContainer
{
	max-height: none !important;
}

/*
 * Store
 */

.storage_sell_total,
.storage_sell_ammount_2
{
	margin-left: 0px;
	width: 200px;
}

.storage_sell_ammount,
.storage_sell_price,
.storage_ammount
{
	width: 100px;
}

#storage_market .float-left
{
	float: right;
}

.storage_sell_pcs
{
	display: none;
}

.storage_whose
{
	width: 150px;
}

.storage_buy_input
{
	width: 100px !important;
}
}

@-moz-document url-prefix("http://rivalregions.com/#map") {
/* Show sea background for map */
body
{
	background: url(https://upload.wikimedia.org/wikipedia/commons/b/bd/Water_texture_1380389_Nevit.jpg);
	background-size: 100%;
}
}
