css:
@-moz-document domain("www.postcrossing.com") {
body {
    background-color: #111;
    color: #ddd;
}

.default-page-layout #mainContentArea,
.traveling-postcard .postcard-details .box.split-box,
.profile-summary .box,
.box {
    background-color: #333;
    color: #ddd;
}

.traveling-postcard .postcard-details .box.split-box,
.profile-summary .box,
.profile-summary .actions,
.dotted-line {
	background-image: none;
}

h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6,
header .user-info a,
header .user-info h4,
header .user-info #user-links a, header .user-info #user-links button {
    color: #eee;
}

.box.with-pin img.pin {
    display: none;   
}

.traveling-postcard .address-details .address-container .address-image {
	-webkit-filter: invert(1);
   filter: invert(1);
}
}
