  border-color: var(--fg-2) }
.nav-tabs .nav-link.active, .page-item.disabled .page-link, .input-group-addon, .pagination > .disabled > a, .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus, .nav > li > a:hover {
  border-color: transparent }

noshadow {
  box-shadow: none }
baseshadow {
  box-shadow: 0 0 5px #000 }

body {
  text-shadow: 0.3px 0.3px 0.5px rgba(0, 0, 0, 0.5) }

success, success a, .alert-success, .alert-success .alert-link, .table-success, .table-success > th, .table-success > td {
  background-color: var(--bg-success);
