css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.dabblewriter.com/app") {

    body
    {
        color: #acacac !important;
        font-family: sans-serif;
        background-color: #333;
    }

    .app-header
    {
        background: #2a2a2a;
        border-top: 1px solid #0f0f0f;
        border-bottom: 1px solid #0f0f0f;
    }

    .btn
    {
        color: #999;
    }

    .app-header .btn
    {
        color: #acacac;
    }

    .dropdown-menu
    {
        background: #1a1a1a;
        color: #acacac;
    }

    .dropdown-item
    {
        color: #acacac;
    }

    .dropdown-menu .dropdown-item
    {
        color: #acacac
    }

    .app-header .btn:not([disabled]):hover, .app-header .btn:active, .app-header .btn.active
    {
        color: #ccc;
    }

    .dropdown-menu hr
    {
        border-top-color: #acacac;
    }

    .projects-header .logo
    {
        background: url('data:image/png;base64,') no-repeat center;
        background-size: 330px 90px;
        width: 324px;
        height: 78px;
        overflow: hidden;
        text-indent: -400px;
        margin: 40px auto 0;
    }

    .projects-header
    {
        background-color: #1a1a1a;
        border-bottom: 2px solid #333;
        outline: 3px solid #1a1a1a;
    }

    .projects-header .tagline
    {
        font-family: 'Quicksand', 'Ubuntu', serif;
    }

    #projects h3
    {
        font-family: sans-serif;
        font-weight: lighter;
        color: #acacac;
        text-shadow: 0 1px 1px rgba(0, 0, 0, .8);
    }

    .new-project
    {
        border: 1px dashed rgba(255, 255, 255, .3);
    }

    .icon.create-project
    {
        color: #666;
        text-shadow: 0 1px rgba(0, 0, 0, .8);
    }

    #projects .project::before, #projects .project::after, #projects .project-inner::before
    {
        background: #1a1a1a !important;
    }

    #projects .project h1
    {
        color: #acacac;
        font-family: 'Quicksand', 'Ubuntu', serif;
    }

    #projects .project h2
    {
        color: #acacac;
        font-family: 'Quicksand', 'Ubuntu', serif;
    }

    #projects .project .info
    {
        color: #acacac;
    }

    #projects .project h3
    {
        color: #acacac;
        font-family: 'Quicksand', 'Ubuntu', serif;
    }

    .icon.restore-project, .icon.trash-project
    {
        color: #333;
    }

    #projects-screen .projects-footer
    {
        box-shadow: 0 -1px #666, inset 0 1px #1a1a1a;
        color: #666;
        text-shadow: 0 1px rgba(0, 0, 0, .5);
    }

    .app-header .logo
    {
        background: url('data:image/png;base64,') no-repeat center;
        background-size: 110px 30px;
        width: 110px;
        height: 30px;
        overflow: hidden;
        text-indent: -400px;
        padding: 5px 10px;
        margin-left: -10px;
    }

    .side-nav
    {
        background: #2a2a2a;
        border-right: 1px solid #222;
    }

    .home-link
    {
        font-family: sans-serif;
        color: #acacac;
        border-bottom: 1px solid rgba(255, 255, 255, .1);
    }

    .home-link:hover
    {
        color: #bcbcbc;
    }

    .form-control
    {
        background-color: #2a2a2a;
        color: #acacac;
        border: 1px solid rgba(255, 255, 255, .15);
        box-shadow: inset 0 1px 1px rgba(255, 255, 255, 0.075);
    }

    .form-control:focus
    {
        background-color: #2a2a2a;
        color: #acacac;
        border: 1px solid rgba(255, 255, 255, .15);
        box-shadow: inset 0 1px 1px rgba(255, 255, 255, 0.075);
    }

    .nav-actions
    {
        color: #666;
        background-color: #3a3a3a;
    }

    .nav-actions .new-item
    {
        background-color: #3a3a3a;
    }

    .icon
    {
        color: #999;
    }

    nav-item header
    {
        color: #acacac;
        font-family: sans-serif;
    }

    #main-nav .novel_manuscript > header .title, #main-nav .novel_plots > header .title, #main-nav .novel_characters > header .title, #main-nav .novel_notes > header .title
    {
        font-weight: 600;
    }

    nav-item .toggle-arrow .icon
    {
        color: rgba(255, 255, 255, .3);
    }

    nav-item .toggle-arrow .icon:hover
    {
        color: rgba(255, 255, 255, .6);
    }

    nav-item .menu-opener::before
    {
        color: rgba(255, 255, 255, 0.5);
    }

    .menu
    {
        background-color: #1a1a1a;
    }

    .menu-item
    {
        color: #acacac;
    }

    .menu-separator
    {
        border-top: 1px solid #999;
    }

    .menu.right-down .menu-arrow::after
    {
        border-right-color: #1a1a1a;
    }

    nav-item.selected > header
    {
        background: #00688b;
    }

    body:not(.drag-drop) nav-item.selectable:not(.selected) > header:hover, body:not(.drag-drop) nav-item.selectable:not(.selected) > header.menu-open
    {
        background-color: rgba(255, 255, 255, .08);
        color: #acacac;
    }

    /*
    div.chapter-view.page
    {
        background-color: #1a1a1a !important;
        font-family: sans-serif !important;
    }
    */

    .page
    {
        background-color: #1a1a1a;
        font-family: sans-serif;
    }

    h1.title
    {
        font-family: 'Quicksand', 'Ubuntu', serif;
        text-shadow: none;
    }

    .side-nav.right
    {
        border-left: 1px solid #222;
    }

    .dabble-wordcounts
    {
        background: #2a2a2a;
        color: #acacac
    }

    button.icon
    {
        color: rgba(255, 255, 255, 0.5);
    }

    button.icon:not([disabled]):hover, button.icon:active, button.icon.active
    {
        color: rgba(255, 255, 255, 0.9);
    }

    body .side-nav h3, body .side-nav h4
    {
        font-family: sans-serif;
        font-weight: normal;
        text-transform: none;
    }

    .goals-sidebar .goal-progress-chart
    {
        background-color: #333;
    }

    body .side-nav h6
    {
        text-transform: none;
    }

    .wordcount-graph-background
    {
        background: #333;
    }

    .novel-card, body .modal-container > .novel-card
    {
        background: #1a1a1a;
        color: #acacac;
        font-family: sans-serif;
    }

    .novel-card .title
    {
        border-bottom: 1px solid #acacac;
        color: #acacac;
    }

    .novel-card .title h4
    {
        color: #acacac;
        font-family: sans-serif;
    }

    .novel-card .text
    {
        background-image: repeating-linear-gradient(to bottom, transparent, transparent 24px, #acacac 24px, #acacac 25px);
        color: #acacac;
    }

    div.grid-header, div.line-titles
    {
        background: #2a2a2a !important;
    }

    .plot-grid .grid-header
    {
        box-shadow: rgba(26, 26, 26, 0.6) 0px 2px 2px;
    }

    .plot-grid .plot-title h1
    {
        color: #acacac;
        text-shadow: none;
    }

    .plot-grid .line-titles editable-content.title h3
    {
        color: #acacac;
        text-shadow: none;
    }

    .project h1, .project h2, .project h3, .project h4, .project h5, .project h6, .project .page header
    {
        font-family: 'Quicksand', 'Ubuntu', serif;
    }

    .manuscript-book .book-title
    {
        text-shadow: none;
    }

    .novel-partition .title
    {
        text-shadow: none;
    }
    }
