css:
@-moz-document domain("howtogeek.com")
{
#content {
    min-width: 99%!important;
}
div #primary.content-area {
    width: 75%!important;
    max-width: none !important;
}
#secondary {
    display: inline !important;
}
body #content #primary .articles-left {
    width: 70% !important;
}
body #content #primary div.splashbox.img {
    display: none;
}
.adSlotEntry {
    display: none !important;
}
.articles-right,
.how-to-geek-follow,
.did-you-know,
.daily-tech-term,
.sidebar-glossary,
.homepage-bottom-ad,
.site-info {
    display: none !important;
}
.relatedside,
.menu-footer-containe {
    display: none !important;
}
.article-bottom {
    display: none !important;
}
.facebook,
.twitter,
.linkedin,
.g-plus,
.reddit,
.pocket,
.author-info-box {
    display: none !important;
}
#colophon {
    display: none !important;
}  
}
