css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://asbntby.sakura.ne.jp/sow/"),
url-prefix("http://melon-cirrus.sakura.ne.jp/sow/"),
url-prefix("http://www3.marimo.or.jp/~fgmaster/sow/"),
url-prefix("http://www3.marimo.or.jp/~fgmaster/ultimate/"),
url-prefix("http://s2.muryo-de.etowns.net/~fraw/sow/"),
url-prefix("http://hamyoron.s262.xrea.com/sow/"),
url-prefix("http://endlessloop.skr.jp/sow/"),
url-prefix("http://test-kunitori.ddo.jp/jinro/sow/"),
url-prefix("http://sebas.chips.jp/sow/"),
url-prefix("http://seileitoh.sakura.ne.jp/sow/"),
url-prefix("http://o8o8.o0o0.jp/wolf/"),
url-prefix("http://monooki.sakura.ne.jp/sow/"),
url-prefix("http://tanakatanaka.sakura.ne.jp/cgi-bin/sow/"),
url-prefix("http://sow.xrea.jp/"),
url-prefix("http://euros.sakura.ne.jp/real/") {
	.mes_text,
	.mes_text_report,
	.action_nom p,
	.action_grave p,
	.action_loveletter p,
	.action_bm p,
	.action_gravebm p,
	.action p,
	.infosp,
	.mes_text p,
	.mes_text_monospace,
	.mes_text_monospace p {
		word-wrap: break-word;
		overflow-x: auto;
		overflow-y: hidden;
	}
	.memo,
	.memo .mes_text,
	.memo .mes_text_monospace,
	.memo .mes_text_report {
		word-wrap: break-word;
		overflow-x: auto;
		overflow-y: hidden;
		table-layout: fixed;
	}
	form .mes_text,
	form .mes_text_report,
	form .mes_text_monospace,
	form .mes_text_monospace p,
	form .mes_text p,
	form .memo .mes_text,
	form .memo .mes_text_monospace,
	form .memo .mes_text_report {
		word-wrap: normal;
		overflow-x: inherit;
		overflow-y: inherit;
	}
}
@-moz-document url-prefix("http://melon-cirrus.sakura.ne.jp/sow/") {
	.mes_text td,
	.mes_text_monospace td {
		display: inline-block;
	}
	.mes_text td:first-child + td,
	.mes_text_monospace td:first-child + td {
		width: 14px !important;
	}
	form .mes_text td {
		display: table-cell;
	}
}
