css:
@-moz-document domain("www.journaldugeek.com")
{
    /* PAGE */
    body {
        color: #fff;
    }
    .main__content,
    .page__content,
    .page__content .container {
        background: rgb(30, 30, 40);
    }
    /* SIDE BAR */
    .page__sidebar .banner,
    .widget__area {
        border-left-color: transparent;
    }
    .widget__title,
    .widget__link p,
    .widget--hubs ul li a,
    .widget--related .entry__title {
        color: #fff;
    }
    /* PIED DE PAGE */
    .footer {
        background-color: #25253e;
    }
    /* ACCUEIL */
    .entry__grid .entry__item .entry__title,
    .entry__plus .entry__item .entry__title,
    .entry__category-bloc__list .entry__title,
    .entry__category-bloc__posts .entry__item .entry__title,
    .entry__list .entry__link .entry__caption .entry__title,
    .entry__list .entry__link .entry__caption p {
        color: #fff;
    }
    .content--gray {
        background: rgb(10, 10, 20);
    }
    /* LECTURE ARTICLE */
    /* Article */
    .widget__subtitle,
    .single__category,
    .single__summary ol li.current a,
    .single__summary__title,
    .single__summary ol li.current {
        color: #5f5ae0;
    }
    .comments__count,
    .single__share__alert > a,
    .read__title,
    .single__post a,
    .single__summary ol li a:active,
    .single__summary ol li a:focus,
    .single__summary ol li a:hover {
        color: #fff;
    }
    .read,
    .read--large {
        border-left-color: #555;
        background: rgba(255, 255, 255, .05);
    }
    .read__link {
        color: #e42675;
    }
    a,
    blockquote p {
        color: #ca1560;
    }
    blockquote p:after {
        background-color: #ca1560;
    }
    .single__shortcake {
        border-color: #444;
    }
    .single__opinion {
        background: rgb(20, 20, 30);
    }
    /* Tableau */
    .tablepress tfoot th,
    .tablepress thead th {
        background-color: rgba(255, 255, 255, .2);
        color: #ff0095;
    }
    .tablepress .even td {
        background-color: rgba(255, 255, 255, .05);
    }
    .tablepress .odd td {
        background-color: rgba(255, 255, 255, .1);
    }
    .tablepress tbody td,
    .tablepress tfoot th {
        border-color: rgba(255, 255, 255, .2);
    }
    .tablepress thead th {
        border-color: rgba(255, 255, 255, .3);
    }
    .tablepress .row-hover tr:hover td {
        background: rgba(255, 255, 255, .15);
    }
    /* Zone Partage */
    .single__share,
    .single__share__label,
    .single__share .button__share,
    .single__summary ol,
    .single__summary__title {
        border-color: rgba(255, 255, 255, .2);
    }
    .single__share .button__share:hover {
        background: rgba(0, 0, 0, .5);
    }
    .single__share .button__share .icon {
        filter: invert(85%);
    }
    .socialbar .single__share .button__share .icon {
        filter: initial;
    }
    /* Zone Tags */
    .single__tags ul li a {
        background-color: rgba(255, 255, 255, .1);
        color: #fff;
    }
    .single__tags ul li a:hover {
        background-color: #ca1560;
        color: #fff;
    }

    /* Avis */
    .single-cpt .single__mark-container {
        background: #4440a0;
    }
    .single-cpt .single__mark-container .single__label {
        color: #fff;
    }
    .note__star__half .icon {
        color: rgba(0, 0, 0, .3);
    }
    .note__star__half--active .icon {
        color: #fff;
    }
}

@-moz-document url-prefix('https://www.journaldugeek.com/articles/'), url-prefix('https://www.journaldugeek.com/dossiers/'), url-prefix('https://www.journaldugeek.com/hub/'), url-prefix('https://www.journaldugeek.com/category/'), url-prefix('https://www.journaldugeek.com/tag/'), url-prefix('https://www.journaldugeek.com/author/'), url-prefix('https://www.journaldugeek.com/tests/'), url-prefix('https://www.journaldugeek.com/critiques/'), url-prefix('https://www.journaldugeek.com/?s=') {
    /* Liste des articles/dossiers/etc... */

    .archive__list__caption .entry__title,
    .archive__list__caption .entry__excerpt {
        color: #fff;
    }
}

@-moz-document url-prefix('https://www.journaldugeek.com/articles/'), url-prefix('https://www.journaldugeek.com/dossiers/'), url-prefix('https://www.journaldugeek.com/category/'), url-prefix('https://www.journaldugeek.com/tag/'), url-prefix('https://www.journaldugeek.com/author/'), url-prefix('https://www.journaldugeek.com/tests/'), url-prefix('https://www.journaldugeek.com/critiques/') {
    /* Pagination des articles/dossiers/catégories/etc... */

    .wp-pagenavi {
        background: rgb(20, 20, 30);
        border-top: 1px solid #000;
        position: fixed;
        bottom: 0px;
        left: 0;
        right: 0;
        z-index: 1;
    }
    .wp-pagenavi .current,
    .wp-pagenavi .page,
    .wp-pagenavi .nextpostslink,
    .wp-pagenavi .previouspostslink {
        padding: 15px;
    }
    .wp-pagenavi .current {
        color: #fff;
    }
    .wp-pagenavi a {
        color: #da5f92;
    }
    .wp-pagenavi a:hover {
        background: rgba(255, 255, 255, .1);
        background: rgba(0, 0, 0, .7);
    }
    .footer {
        margin-bottom: 50px;
    }
}

@-moz-document url-prefix('https://www.journaldugeek.com/hub/') {
    /* Sous-titre de l'article */

    .single-hub .banner--subheader,
    .single-hub .page__content {
        background-color: transparent;
    }
    /* Catégories du hub */

    .single__content__nav ul li a {
        color: #fff;
    }
    .single__content__nav ul li a:active,
    .single__content__nav ul li a:focus,
    .single__content__nav ul li a:hover {
        color: #5f5ae0;
    }
    .single__content__nav {
        border-color: #000;
    }
    /* Pagination du hub */

    .single-hub__pagination {
        border-color: rgb(50, 50, 60);
    }
    .pagination button,
    .wp-pagenavi button {
        color: #da5f92;
    }
    .pagination .current,
    .wp-pagenavi .current {
        color: #fff;
    }
}

@-moz-document url-prefix('https://www.journaldugeek.com/dossiers/'), url-prefix('https://www.journaldugeek.com/category/'), url-prefix('https://www.journaldugeek.com/tag/') {
    /* Sommaire */

    .single-cpt .single__mark-container,
    .single__opinion {
        background: rgb(20, 20, 30);
    }
    .single-cpt .single__mark-container .single__label,
    .single__opinion__title,
    .single__summary__title,
    .single__summary ol li.current {
        color: #5f5ae0;
    }
    .single-cpt .single__note span {
        color: #fff;
    }
    .single__summary__title,
    .single__summary ol {
        border-color: rgb(10, 10, 20);
    }
    .single__summary ol li.current a,
    .single__summary ol li a:active,
    .single__summary ol li a:focus,
    .single__summary ol li a:hover {
        color: #fff;
    }
    /* Tableau */

    .tablepress tfoot th,
    .tablepress thead th {
        background: rgb(50, 50, 60);
        color: #ca1560;
    }
    .tablepress .even td {
        background: rgb(30, 30, 40);
    }
    .tablepress .odd td {
        background: rgb(25, 25, 35);
    }
    .tablepress tbody td,
    .tablepress tfoot th,
    .tablepress thead th {
        border-color: #333;
    }
    .tablepress .row-hover tr:hover td {
        background: rgb(40, 40, 50);
    }
    .single__type {
        color: rgba(255, 255, 255, .2);
    }
}
