css:
@-moz-document domain('myfigurecollection.net') {
	body {
		background-color: #262626 !important;
	}

	section > h1,
	section > h2,
	.tbx-target-WINDOW,
	.tbx-target-WINDOW > h2,
	.tbx-target-WINDOW > .window-actions,
	.tbx-target-WINDOW .actions {
		background-color: #212121 !important;
		border-color: #212121 !important;
	}

	section,
	div.item-object,
	div.section-content,
	.tbx-target-TAGS .object-tags,
	.listing .listing-item,
	.user-stats,
	.listing-noresults,
	.window-content,
	.entry-description,
	.entry-items,
	.entry-object,
	.picture-score,
	.nothing,
	.user-object,
	.item-information,
	.classified-content,
	.picture-awards,
	section > .users,
	.object-content,
	.blog-content,
	.home-about,
	.home-signup {
		background-color: #333333 !important;
		border-color: #333333 !important;
	}

	.entry-description,
	.item-information,
	.input *,
	.form-input,
	section h1,
	section h2,
	ul.menu > li > a,
	.form-input input,
	.form-input select,
	.form-input textarea,
	.picture-awards,
	.object-content,
	.blog-content,
	.home-about,
	.home-signup {
		color: #D2D2D2 !important;
	}

	.listing,
	.listing-title,
	.listing-filter {
		border-color: #333333 !important;
	}

	div.share,
	.comment > .user-expression .content,
	.activity-wrapper,
	.user-expression .expression .content,
	.tbx-bbcode,
	.bbcode-buttons,
	.form .form-field.small-text .form-input,
	.form .form-field.text .form-input,
	.form .form-field.long-text .form-input,
	.tbx-bbcode-pictures,
	.reply-content,
	.select.form-field > .form-input,
	.bigchar.form-field > .form-input,
	.varchar.form-field > .form-input,
	.date.form-field > .form-input,
	.date-chooser > .date,
	.form-input .option,
	.object-picker,
	.tbx-color,
	.tbx-color-palet > div > div,
	select > option,
	.form-input > label[for=fl-avatar],
	.listing-item-meta.collection-meta,
	.classified-contact-button,
	.blog-rating-box {
		background-color: #5C5C5C !important;
		border-color: #5C5C5C !important;
		color: #D2D2D2 !important;
	}

	.user-expression .avatar .inner-caret,
	.caret > .inner {
		border-color: #5C5C5C;
		border-top: 7px solid transparent;
		border-bottom: 7px solid transparent;
		border-right: 7px solid #5C5C5C !important;
	}

	.user-expression .avatar .outer-caret,
	.caret > .outer {
		border-right: 8px solid #5C5C5C !important;
	}

	.collection-meta-caret > .inner {
		border-bottom: 7px solid #5C5C5C !important;
	}

	.collection-meta-caret > .outer {
		border-bottom: 8px solid #5C5C5C !important;
	}

	.comment > .user-expression .content .quote,
	.tbx-menu > .menu,
	.tbx-menu > .menu > li,
	.reply-content .quote {
		background-color: #6B6B6B !important;
		border-color: #6B6B6B !important;
	}

	div.comment {
		border-bottom: 1px solid #5C5C5C !important;
	}

	a {
		color: #999 !important;
	}

	a:hover,
	.actions > a:hover,
	.actions > a > span:hover {
		color: #50BCB6 !important;
	}

	h1 > .actions,
	.actions > a,
	h2 .count,
	.subtab .selected,
	.subtab li a:hover,
	.input,
	.tbx-click,
	.criterion-preview,
	.home-signup > .field {
		background-color: #5C5C5C !important;
		border-color: #5C5C5C !important;
		color: #D2D2D2 !important;
	}

	.tiny-progress-bar {
		background-color: #50BCB6;
	}

	#window {
		background: rgba(0,0,0, 0.7);
	}

	#search {
		background: rgba(0,0,0, 0.6);
	}

	.item-object .item-collect-actions {
		border-bottom: none !important;
	}

	.object-stats,
	.object-meta,
	.object-mono-relation {
		border-top: none !important;
	}

	#navigator a.handle,
	a.item-category,
	a.item-category-2,
	.item-picture-categories > a,
	a.category {
		color: white !important;
	}

	a.category.picture-album {
		color: black !important;
	}

	ul.menu,
	ul.menu > li {
		background-color: #5C5C5C !important;
		border: none !important;
	}

	#menu li .menu li a:hover,
	.user-menu .menu li a:hover, .lang-menu .menu li a:hover {
		background: #6B6B6B;
	}

	.listing li.listing-toggles {
		border-bottom: 1px solid #5C5C5C !important;
	}

	input[type="button"]:hover,
	input[type="reset"]:hover,
	input[type="submit"]:hover,
	button:hover,
	.form-input > input[type=submit]:hover,
	.menu li a:hover {
		color: white !important;
		background-color: #50BCB6 !important;
	}

	.spotlighted {
		background: linear-gradient(transparent, #50BCB6) !important;
	}

	.spotlighted .expression > .meta {
		background: rgba(0,0,0, 0.6);
		padding: 0 0.5em;
		border-radius: 4px;
	}

	.spotlighted .comment-score-value {
		color: white !important;
	}
}
