css:
@-moz-document url(https://web.crowdfireapp.com/#/content/articles) {

.adaptiveMediaContainer {
	height: 135px;
	width: 100%;
	margin: 0 auto;
}

.adaptiveMediaContainer .adaptiveMedia {
	background-size: contain;
}

.listItemContainer {
	padding: 1em;
	width: 33%;
	box-sizing: border-box;
	align-items: stretch;
}
.listItemContainer:nth-child(6n+1),
.listItemContainer:nth-child(6n+2),
.listItemContainer:nth-child(6n+3) {
	background-color: #efefef;
}

.listItem__detail > * {
	flex: 1;
}

.listItem__detail .title {
	font-size: 1em;
	margin: 0;
	line-height: auto;
	display: block;
}

.listItem__detail .description {
	font-size: .9em;
	color: #333333;
}

.btn-share {
	height: 2em;
	padding: .25em;
	margin: 0;
}

.list {
	display: flex;
	flex-wrap: wrap;
	width: calc(100%) !important;
	margin: 0;
}

.listItem--post-share .listItem__detail__footer, .listItem--post-publish .listItem__detail__footer {
	margin: 0;
}

.listItem__separator {
	margin: 0;
	border: 0;
	display: none;
}

.listItem--post-share {
	margin: 0;
}
.listItem--post-share .listItem__detail__itemCollection, .listItem--post-publish .listItem__detail__itemCollection {
	display: block;
}

}
