css:
@-moz-document regexp("http://gen.lib.rus.ec/book/index.php?.*"), regexp("http://libgen.io/book/index.php?.*") {
tr {
        color: #444!important;
    }
    tr:nth-child(n+1):nth-child(-n+18) > td:nth-child(n+1):nth-child(-n+18) {
        border-right: 1px solid #eee!important;
        border-bottom: 1px solid #eee!important;
        padding:7px!important;
    }
    body > table:nth-child(6) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
        border-right:1px solid #fff!important;
        padding-right:2.5em!important;
    }
}

@-moz-document domain("gen.lib.rus.ec"), domain("libgen.io") {
html {
        background: #fff!important;
        /* width: 90%!important; */
        margin: auto!important;
    }
    body {
        margin: 0!important;
        background:#fff;
        min-width:800px!important;
        padding:5px!important;
    }
    * {
        font-family: Arial, Helvetica, sans-serif!important;
    }
    a {
        color: rgba(0, 100, 150, 0.8)!important;
		transition: color .4s ease-out!important;
    }
    a:hover {
        color: rgba(150,95,0, 0.8)!important;
		transition: color .4s ease-out!important;
    }
	font[color="#A00000"], a[href="/"] {
        color: #00B5FF!important;
		transition: color .4s ease-out!important;
    }
	font[color="#A00000"]:hover, a[href="/"]:hover {
        color: #0086BB!important;
		transition: color .4s ease-out!important;
	}
    
    /* book description */
    td[style="padding: 25px"] {
        text-align: justify!important;
    }
    
    a[href="http://genofond.org/viewtopic.php?t=6423"] {
        margin-right:25px!important;
        float:right!important;
    }
	
    /* search stuff */
    #searchform, input[type="edit"] {
        border: 1px solid #ccc!important;
        height: 14px!important;
        padding: 4px!important;
        margin-top: 5px!important;
    }
    input#searchform, input[type="edit"] {
        border: 1px solid #ccc!important;
        margin-bottom: 5px!important;
    }
    input[type="edit"] {
        margin-right:5px!important;
    }
    select {
        border:1px solid #ccc!important;
        width:225px!important;
        height:24px!important;
        color:#444!important;
    }
    select, input:not([type="submit"]) {
        box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
        border-radius:3px!important;
    }

	body > table:nth-child(6) {
		margin-bottom:20px!important;
	}
	
    /* submit button */
    input[type="submit"] {
        border: 1px solid #ccc!important;
        border-radius:3px!important;
        height: 24px!important;
    }
	
    /* 
    Fiction search
    */
    /*body > table:nth-child(9) td {
        padding:10px;
        background: #eee
    }*/
    /* DL links */
    body > table:nth-child(9) td[width="280"] {
        display: flex;
        justify-content: flex-end;
    }
    body > table:nth-child(10) td[width="280"] {
        padding:10px;
    }
    #mlink {
        float:right;
        line-height: 20px;
        margin-left:5px;
    }
    body > table:nth-child(10) td[width="150"], /* Series */
    body > table:nth-child(10) td[width="200"], /* Author(s) */
    body > table:nth-child(10) td[width="400"], /* Title */
    body > table:nth-child(10) td[width="70"]   /* Language */
    {
        padding:10px;
        border-right:1px solid #ddd;
    }
    body > table:nth-child(10) tbody tr {
        border-color:#ddd!important;
    }
    body > table:nth-child(10) tbody tr:hover {
        background-color: #eee!important;
        transition: background-color .8s!important;
    }
    
    /* paging stuff */
    #paginator_example_top,
    #paginator_example_bottom {
        margin-top: 25px!important;
        margin-bottom: 15px!important;
        float: none!important;
    }
    #paginator_example_bottom {
        margin-bottom: 25px!important;
    }
    /*
    body > table:nth-child(12) {
        display: none!important;
    }
    */
    .paginator span strong {
        background: #eee!important;
        color: #777!important;
    }
    body > table:nth-child(9) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > font:nth-child(1) > a:nth-child(1),
    body > table:nth-child(13) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > font:nth-child(1) > a:nth-child(1),
    
    body > table:nth-child(8) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > font:nth-child(1) > a:nth-child(1),
    body > table:nth-child(8) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > font:nth-child(2) > a:nth-child(1) {
        display: none!important;
    }
	
    /* fix search fields */
    body > table:nth-child(6) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > b:nth-child(10),
    body > table:nth-child(6) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > font:nth-child(21) > b:nth-child(1),
    body > table:nth-child(6) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > b:nth-child(15),
    body > table:nth-child(6) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > b:nth-child(13),
    body > table:nth-child(6) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > b:nth-child(19),
    body > table:nth-child(6) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > font:nth-child(25) > b:nth-child(1),
    body > table:nth-child(6) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > label:nth-child(10) > b:nth-child(1),
    body > table:nth-child(6) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > label:nth-child(7) > b:nth-child(1),
    /* libgen.io */
    body > table:nth-child(5) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > b:nth-child(13),
    body > table:nth-child(5) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > b:nth-child(22),
    body > table:nth-child(5) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > font:nth-child(28),
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > label:nth-child(7),
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > label:nth-child(9),
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > b:nth-child(12),
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > b:nth-child(17),
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > font:nth-child(23) > b:nth-child(1),
    body > table:nth-child(5) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > label:nth-child(10) > b:nth-child(1),
    body > table:nth-child(5) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > label:nth-child(19) > b:nth-child(1) {
        display: block;
        margin-bottom: 5px!important;
        margin-top: 5px!important;
    }
    body > table:nth-child(6) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > select:nth-child(11),
    body > table:nth-child(5) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > select:nth-child(20),
    body > table:nth-child(5) > tbody:nth-child(4) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > select:nth-child(11) {
        margin-bottom: 5px!important;
        margin-top: 5px!important;
    }

    body > table:nth-child(6) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > br:nth-child(1) {
        display: none!important;  /* hide this br */
    }
	
	/* current_page_mark */
	.current_page_mark {
		background-color: #00B5FF!important;
	}
	
    /* result */
    td[bgcolor="brown"] {
        display: none!important;
    }
    body > font:nth-child(8) {
        font-size:13px!important;
        font-weight: bold!important;
        margin-top:20px!important;
        display: flex;
        justify-content: center
    }
    /* fiction search */
    body > font:nth-child(9) {
        display: none  
    }
    body > table:nth-child(5) {
        margin-bottom:20px!important;
    }
    
    /* top menu stuff */
    #menu {
        background: #555!important;
        margin: auto!important;
        padding: 0!important;
        border: 0!important;
        border-radius: 0!important;
        box-shadow: none!important;
        width: 100%!important;
        border-bottom: 2px solid #00B5FF!important;
        display: flex;
        justify-content: center;
    }
    #menu > li > .drop {
        color:#fff!important;
    }
    #menu li .drop {
        background:url(data:image/png;base64,) no-repeat right 7px!important;
    }
    #menu li:hover {
        border: 0!important;
        background: #1B84BF!important;
        padding: 4px 10px 4px 10px!important;
    }
    #menu li .greybox li:hover {
	    background:#fff!important;
	    padding:4px 6px 4px 6px!important;
        border:1px solid #ddd!important;
    }
    #menu li .drop {
        padding-bottom: 5px!important;
    }
    .dropdown_3lettercolumn,
    .dropdown_1column,
    .dropdown_2columns,
    .dropdown_3columns,
    .dropdown_4columns,
    .dropdown_5columns,
    .dropdown_6columns {
        border: 1px solid #fff!important;
        border-radius: 0!important;
        background: #fcfcfc!important;
        box-shadow: 0 3px 5px 0 #eee!important;
    }
    #menu li:hover a, #menu h3 {
        text-shadow: none!important;
    }
    a[href="http://gen.lib.rus.ec/donate/"],
    a[href*="setlang.php?lang=ru"],
    a[href*="setlang.php?lang=en"] {
        color:#ccc!important;
    }
    #menu li .greybox li {
	    background:#f9f9f9!important;
    }
    
    tr {
        border-color: #ddd!important;
        line-height: 17px!important;
    }
	.c font i {
        font-style: normal!important;
        color: #555!important;
    }
    
    /* results hover */
    .c tbody tr:hover {
        background-color: #ddd!important;
        transition: background-color .4s ease-out!important;
    }
	
    /* results sorting */
    .c tbody:nth-child(1) tr:nth-child(1) {
        background-color: #d5d5d5!important;
        border-bottom:1px solid #fff!important;
    }
    /* center certain results cells */
    .c tbody tr td:nth-child(n+5):nth-child(-n+11),
    .c > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(n+1):nth-child(-n+4),
    .c > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(n+12):nth-child(-n+14) {
        text-align: center!important;
    }
    /* ID and Edit tables; not needed for normal users */
    /*.c > tbody:nth-child(1) > tr > td:nth-child(1),
    .c > tbody:nth-child(1) > tr > td:nth-child(14), .c > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(11) {
        display: none;
    }*/
    	
	/* even results background color */
    .c tr[bgcolor=""] {
        background-color: #f6f6f6!important;
        transition: background-color 1s ease-out!important;
    }
    /* odd results background color */
    .c tr[bgcolor="#C6DEFF"] {
        background-color: #eee!important;
        transition: background-color 1s ease-out!important;
    }
    .c tbody tr td {
        border-left: 1px solid #fff!important;
        padding-left: 5px!important;
        padding-right: 5px!important;
    }
    
    /* bottom of the page stuff */
    body > hr:nth-child(8) {
        display: none!important;
    }
    
    table:nth-child(4) {
        margin-bottom: 15px!important;
        background-color: #f5f5f5!important;
        border-radius: 10px!important;
        padding: 10px!important;
    }
}
