css:
@-moz-document domain(twitter.com) {

* {
    border-radius: 0 !important;
}
    
    .front-image{
        display:none !important;
    }
    
body {
    background: #090909 !important;
}
[dir="ltr"].logged-in{
    background-position: center !important;
       background-image:  url(/*[[mypic]]*/) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
}
    
.logged-out {
    background-position: center !important;
        background-image:  url(/*[[mypic]]*/) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
}    
    
    
.ProfilePage .AppContent {
    background-position: center !important;
        background-image:  url(/*[[mypic]]*/) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
}
    
    .front-bg{
    background-position: center !important;
        background-image:  url(/*[[mypic]]*/) !important;
    background-size: cover !important;
    background-attachment: fixed !important;

}

.topbar > div.global-nav > div.global-nav-inner{
    background: rgba(255,255,255,0.7) !important;
    }
    
    .PageDownload-info{
        background: rgba(255,255,255,0.7) !important;
        padding:10px !important;
        margin-top:210px !important;
        border-radius:3px !important;
    }

    .stream-item{
        background: rgba(255,255,255,0.9) !important;
    }
    
.dashboard.dashboard-left > div.Trends.module.trends{
        background: rgba(255,255,255,0.9) !important;    
    }    

    .timeline-tweet-box > div.home-tweet-box.tweet-box.component.tweet-user{
        background: rgba(255,255,255,0.5) !important;            
        
    }
    
#tweet-box-home-timeline.tweet-box.rich-editor.is-showPlaceholder{
    background: rgba(255,255,255,0.5) !important;   
    color:#333 !important;
    }    
    
.tweet-content > div.RichEditor{
    background: rgba(255,255,255,0.5) !important;   
    color:#333 !important;    
    }    
    
.dashboard-right{
        display:none !important;
    }    
    
    a:link {
        color:#a51b7b !important;
    }
    
    
    .u-bgUserColor{
        background: rgba(165,27,123, 0.7) !important;
        background: transparent !important;        
    }
    
    
.AdaptiveFiltersBar{
        background: rgba(255,230,247, 0.8) !important;
    }
    
    
#page-container.AppContent > div.AppContainer > div > div.Grid.Grid--withGutter > div > div > div.Grid-cell{
        background: rgba(255,230,247, 0.8) !important;    
    }    
    
    
#doc.route-profile > div#page-outer > div#page-container.AppContent > div > div.ProfileCanopy-inner > div{
        background: rgba(255,230,247, 0.8) !important;            
    }    
    
.logged-out > div#doc > div#page-outer > div#page-container.AppContent.wrapper.wrapper-login > div.page-canvas{
    margin-top:250px !important;
        background: rgba(255,230,247, 0.8) !important;                
    }    
    
    
.front-card{
    background: rgba(0,0,0, 0.3) !important;                    
    padding:15px !important;
    }    
    
.logged-out.ms-windows.front-page-photo-set.front-page.no-nav-banners > div#doc > div#page-outer > div#page-container.AppContent.wrapper-front > div#front-container.front-container.front-container-full-signup > div.footer.inline-list > ul{
        background: rgba(255,230,247, 0.8) !important;      
    }
    
.logged-out > div#doc > div#page-outer > div#page-container.AppContent.wrapper-front > div#front-container.front-container.front-container-full-signup > div.front-card > div.front-welcome > div.background-companion-tweets{
        margin-top:-30px !important;
    }
    

.logged-out > div#doc > div#page-outer > div#page-container.AppContent.wrapper-front > div#front-container.front-container.front-container-full-signup > div.front-card > div.front-welcome > div.background-companion-tweets{
    display:none !important;    
    }    
    

    
.logged-out > div#doc > div#page-outer > div#page-container.AppContent.wrapper-front > div#front-container.front-container.front-container-full-signup > div.front-card > div.front-welcome > div.front-welcome-text{
    padding-top:20px;
    padding-left:20px;    
    }    
    
    
.logged-out > div#doc > div#page-outer > div#page-container.AppContent.wrapper-front > div#front-container.front-container.front-container-full-signup > div.front-card > div.front-signin.js-front-signin  {
            background: rgba(255,230,247, 0.8) !important;    
    }  
    
.logged-out > div#doc > div#page-outer > div#page-container.AppContent.wrapper-front > div#front-container.front-container.front-container-full-signup > div.front-card > div.front-signup.js-front-signup{
            background: rgba(255,230,247, 0.8) !important;    
    }    
    
    
    
    
    
    
    
}
