@-moz-document url-prefix("https://docs.oracle.com") {
    

pre {
    background-color: #000;
    color: #15ff00;
}
.note {
    background-color: #222222;
}
.codeblock {
    color: #15ff00;
}
header {
    background-color: #222222 !important;
    opacity: 1.0;
}
body {
    background-color: rgb(35, 33, 33);
    color: rgb(170, 170, 185);
}
h1,
h2,
h3,
h4,
h5 {
    color: #CD9AB1;
}
*::selection {
    background: #F8981D;
    color: black;
}
*::-webkit-scrollbar {
    width: 0.9em;
}
*::-webkit-scrollbar-corner,
*::-webkit-resizer {
    background-color: #111;
}
*::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    background-color: #1D1D1D;
}
*::-webkit-scrollbar-thumb {
    background-color: #474747;
    outline: 1px solid #737373;
}
#TopBar_left {
    color: rgba(174, 195, 229, 1);
}
#CONTENT #NAV {
    background-color: #222222;
}
th {
    background-color: #111111;
}
#pthdr2container {
    background-color: #222222 !important;
    background: linear-gradient(to bottom, rgb(8, 9, 9) 0%, rgb(100, 100, 100) 100%);
}
#pthnavcontainer {
    background-color: #222222 !important;
    background: linear-gradient(to bottom, rgb(8, 9, 9) 0%, rgb(100, 100, 100) 100%);
}
.PTPAGELETHEADER {
    background-color: #222222 !important;
    background: linear-gradient(to bottom, rgb(8, 9, 9) 0%, rgb(100, 100, 100) 100%);
}
.portletBody {
    background-color: #222222 !important;
}
.tabContent {
    background-color: #222222 !important;
}
#TopBar {
    min-width: 0px;
    border-bottom: 1px solid rgba( 0, 30, 255, 1);
    border-radius: 3px;
    background: linear-gradient( to bottom, rgba( 0, 0, 0, 1) 0%, rgba( 0, 69, 122, 1) 100%) repeat scroll 0% 0% transparent;
}
.IND div > pre {
    background-color: #000 !important;
    color: #15ff00;
}
.row {
    background-color: #222222 !important;
}
.infobox-note {
    background-color: #111111;
}
.infoboxnotealso {
    background-color: #111111 !important;
}
.leftsidebar {
    background-color: rgba(20, 20, 20, 20);
}
.header-container {
    background-color: rgba( 0, 0, 0, 1);
    border-bottom: 1px solid rgba( 0, 143, 255, 1);
    box-shadow: 0px 2px 2px rgba( 0, 66, 255, 0.15);
}
#logocover {
    width: 0px;
}
.nolinkLESSON,
.nolinkAHEAD,
.nolinkBHEAD,
.nolinkCHEAD,
.nolinkQUESTIONS {
    color: rgb(68, 153, 168);
}
#Contents a,
.NavBit a,
#TagNotes a {
    color: rgb(66, 178, 228);
}
#BreadCrumbs a {
    color: rgb(205, 180, 155);
}
a:link {
    text-decoration: none;
    color: #76B1E6 !important;
}
a:hover {
    color: #deecf9 !important;
}
a:visited,
#Contents a:visited,
.NavBit a:visited,
#TagNotes a:visited {
    color: #94D5FF;
}
#productName {
    color: rgb(227, 168, 108);
}
code {
    color: rgb(186, 210, 255);
}
code.visited {
    color: rgb(196, 220, 255);
}
code {
    background-color: #222222;
    color: #15ff00;
}
.TutorialLink {
    color: #5AA3E8;
}
#homeGetStarted a:link,
#homeGetStarted a:active,
#homeGetStarted a:visited,
.threecolumns a:link,
.threecolumns a:active,
.threecolumns a:visited {
    color: #739BC0;
}
.button {
    text-align: center;
    display: inline-block;
    background-color: #525252;
    border: none;
    box-shadow: none;
    border-radius: 2px;
    padding: 2px 5px;
    margin-left: 5px;
}
.button:hover {
    background-color: #626262;
    border: none;
    box-shadow: none;
}
.treeview li,
.treeview li.submenu {
    background-color: rgb(35, 33, 33);
}
.booktitle {
    background: none;
    border-bottom: 1px solid #898989;
    border-radius: 0;
}
#breadcrumb a:link,
#breadcrumb a:visited {
    color: #76B1E6;
}
.footer-container,
.footer-containerbook {
    background: none;
    border-top: 1px solid #656565;
    border-radius: 0;
}
footer,
#Footer .footertext {
    color: rgb(170, 170, 185);
}
.SourceLink {
    border-bottom: 1px solid rgb(186, 190, 255);
}
div.block {
    color: rgb(170, 170, 185);
    font-family: sans-serif;
}
.title {
    color: #93B2C8;
}
.contentContainer .description dl dt,
.contentContainer .details dl dt,
.serializedFormContainer dl dt {
    color: #9E9E9E;
}
ul.blockList ul.blockList li.blockList,
ul.blockList ul.blockListLast li.blockList {
    border: 1px solid #444444;
    background-color: #303030;
}
.overviewSummary,
.memberSummary,
.typeSummary,
.useSummary,
.constantsSummary,
.deprecatedSummary {
    border-left: 1px solid #737373;
    border-right: 1px solid #737373;
    border-bottom: 1px solid #737373;
}
.overviewSummary caption,
.memberSummary caption {
    border-bottom: 1px solid #737373;
}
th.colFirst,
th.colSecond,
th.colLast,
th.colOne,
.constantsSummary th {
    background: #232323;
    color: #CCC;
}
.altColor,
.altColor th {
    background-color: #171717;
}
.rowColor,
.rowColor th {
    background-color: #232323;
}
ul.blockList ul.blockList ul.blockList li.blockList,
ul.blockList ul.blockList ul.blockListLast li.blockList {
    background-color: #1D1D1D;
}
ul.blockList ul.blockList ul.blockList li.blockList h3 {
    border: 1px solid #292929;
    background-color: #333333;
}
div.details ul.blockList ul.blockList ul.blockList li.blockList h4,
div.details ul.blockList ul.blockList ul.blockListLast li.blockList h4 {
    background-color: #2D2D2D;
    border: 1px solid #4B4B4B;
}
.topNav {
    background-color: #494949;
}
.subNav {
    background-color: #4D4D4D;
}
.overviewSummary caption span,
.memberSummary caption span,
.typeSummary caption span,
.useSummary caption span,
.constantsSummary caption span,
.deprecatedSummary caption span {
    background-color: #474747;
    color: rgb(227, 168, 108);
    border-left: 1px solid #737373;
    border-right: 1px solid #737373;
    border-top: 1px solid #737373;
}
.memberSummary caption span.activeTableTab span {
    background-color: #272727;
    color: rgb(227, 168, 108);
    border-left: 1px solid #737373;
    border-right: 1px solid #737373;
    border-top: 1px solid #737373;
}
.memberSummary caption span.tableTab span {
    background-color: #363636;
}
.memberSummary caption span.tableTab,
.memberSummary caption span.activeTableTab {
    opacity: 0;
}
dd,
h3 {
    font-family: sans-serif;
}
.NAV div > pre,
.NAV dd > pre,
.NAV li > pre {
    background-color: rgba(238, 238, 238, 0);
}
.IND div > pre,
.IND dd > pre,
.IND li > pre {
    background-color: rgba(238, 238, 238, 0);
}
div.syntaxhighlighter {
    background-color: rgba(238, 238, 238, 0) !important;
}
div.syntaxhighlighter .line.alt1,
div.syntaxhighlighter .line.alt2 {
    background-color: rgba(238, 238, 238, 0) !important;
}
.syntaxhighlighter code.comments,
.syntaxhighlighter code.comments a,
.syntaxhighlighter code.keyword,
.syntaxhighlighter code.plain,
.syntaxhighlighter code.plain a,
.syntaxhighlighter code.string,
.syntaxhighlighter code.string a {
    color: rgb(170, 170, 185) !important;
}
.TableRowColor {
    background-color: #434343;
}
.TableSubHeadingColor {
    background-color: #5A5A5A;
}
.TableHeadingColor {
    background: #525252;
}
body,
th,
td,
p,
h5,
h5 a,
h6,
h6 a,
h5 span,
h6 span,
h1,
h2,
h3,
h1 span,
h2 span,
h3 span,
h1 a,
h2 a,
h3 a,
h4,
h4 span,
h4 a {
    color: #aaaab9;
}
div.maincontent pre,
div.maincontent code,
div.maincontent tt {
    color: #aec3e5;
}
.important {
    background-color: #333333;
}
div.bottomNav {
    background-color: #222222;
}
li.navBarCell1Rev {
    background-color: #E95;
}
div.subNav {
    background-color: #333333;
}
.indexNav {
    background-color: #333333;
}
/* Search */

#search {
    color: #FFFFFF;
    border-width: 1px;
    border-color: #a8a8a8;
    padding: 3px;
    padding-left: 22px;
    background-position: 4px 4px;
    background-color: #494949;
}
#reset {
    background-color: #494949;
    left: -5px;
    cursor: pointer;
}
.ui-widget::-webkit-scrollbar {
    width: 0.35em;
    height: 0.3em;
}
.ui-widget-content {
    background: #232121;
}
.ui-state-focus {
    background: #494949!important;
    border-color: #494949!important;
}
.ui-state-focus a {
    color: #FFFFFF;
}
    
}
