css:
@-moz-document domain("news.ycombinator.com") {
/* based on Dark Hacker News - Solarized https://userstyles.org/styles/92693/dark-hacker-news-solarized */
:root {
    --nord0: #2E3440;
    --nord1: #3B4252;
    --nord2: #434C5E;
    --nord3: #4C566A;
    --nord4: #D8DEE9;
    --nord5: #E5E9F0;
    --nord6: #ECEFF4;
    --nord7: #8FBCBB;
    --nord8: #88C0D0;
    --nord9: #81A1C1;
    --nord10: #5E81AC;
    --nord11: #BF616A;
    --nord12: #D08770;
    --nord13: #EBCB8B;
    --nord14: #A3BE8C;
    --nord15: #B48EAD;
}
body, html {
    background-color: var(--nord1) !important;
}
* {
    font-size: 11pt;
}
span.sitestr {
    font-size: 9pt;
}
font[color="#3c963c"] {
    color: var(--nord14);
}
td[bgcolor="#000000"] {
	background-color: #000000 !important;
}
table.hnmain, tbody, tr, td, th, table {
  color: var(--nord4) !important;
  background-color: var(--nord0) !important;
}
td[bgcolor="#dfaf8f"] * {
    background-color: var(--nord10) !important;
}
/*body > center > table > tbody > tr:first-child * {
  background-color: var(--nord10) !important;
}*/
body > center > table > tbody > tr:first-child * a:hover {
  background: var(--nord0) !important;
}
code, pre, input, textarea {
  background: var(--nord2) !important;
  color: var(--nord4);
}
a {
  color: var(--nord8) !important;
}
.subtext a {
  color: var(--nord4) !important;
}
a:visited, a:visited span {
  color: var(--nord13) !important;
}
a:hover, a:hover span {
  background: var(--nord2) !important;
}
.c00, .c00 > p, .c5a, .c5a > p {
    color: var(--nord5);
}
.c73, .c73 > p, .c82, .c82 > p {
    color: var(--nord6);
}
.cdd, .cdd > p {
    color: var(--nord3) !important;
}
}
