css:
@-moz-document url-prefix("https://forums.eagle.ru/") {
body {
    background: #16171a;
    color: #fff;
    font-family: Helvetica, sans-serif;
}

div {
    border-top: 0px !important;
}

a:visited, body_avisited, a:link, body_alink {
    color: #00aeef;
    text-decoration: none !important;
}

a:hover, a:active, body_ahover, .sidebox a:hover, .tfoot a:hover, .sidebox a:active, .tfoot a:active {
    color: #fff !important;
}

legend {
    color: #8899A6;
}

#nav {
    background-color: #16171a;
    border-bottom: 0px;
    height: 40px;
}

.vbmenu_control {
    background: #16171a;
    padding: 0px 16px;
    height: 40px;
    font-size: 12px;
    color: #8899A6;
}

.vbmenu_control a:visited, .vbmenu_control_avisited, .vbmenu_control a:link, .vbmenu_control_alink {
    color: #fff;
}

.vbmenu_control a:hover, .vbmenu_control a:active {
    color: #00aeef !important;
}

#nav td {
    border-left: 0px;
    border-right: 0px;
}

.futer {
    background-color: #fff;
    border-top: 0px;
    height: 40px;
}

.tfoot {
    background: #16171a;
    color: #8899A6;
}

.wrapper {
    width: 88% !important;
    margin: 0 auto;
}

.smallfont {
    color: #8899A6;
}

.sidebox {
    background: #16171a;
    border: 0px;
    padding: 0px;
}

.sidebox .smallfont {
    color: #8899A6;
}

.tfoot a:visited, .tfoot_avisited, .tfoot a:link, .tfoot_alink {
    color: #00aeef;
}

.tcat2 {
    background: #111214 !important;
    background-image: none !important;
    color: #fff;
	line-height: 32px;
    font-size: 16px;
    font-weight: normal;
    padding-left: 16px;
}

.tcat2 a:hover {
    color: #00aeef !important;
}

/*.tcat2 img {
    display: none;
}*/

.alt1, .alt1Active {
    background: #16171a;
    color: #8899A6;
    border-right: 0px solid #EFF6FF;
    border-left: 0px solid #8e9fba;
    border-top: 0px solid #EFF6FF;
    border-bottom: 0px solid #232429;
    padding: 12px 8px;
}

.alt2, .alt2Active {
    background: #16171a;
    color: #8899A6;
    border-right: 0px solid #EFF6FF;
    border-left: 0px solid #8e9fba;
    border-top: 0px solid #EFF6FF;
	border-bottom: 0px solid #232429;
    padding: 12px 8px;
}

.thead {
    background: #232429;
    color: #8899A6;
    border-top: 0px;
    padding: 4px 8px !important;
    font-weight: normal;
    font-size: 11px;
}

.tcat {
    background: #f6b832;
    color: #16171a;
    font-weight: normal;
    font-size: 13px;
    /*border-left: 4px solid #66757f;*/
	padding: 8px;
}

.tcat .smallfont {
    color: #fff;
}

.tcat a {
    color: #16171a !important;
}

.tcat a:hover {
    color: #303030 !important;
}

.tcat span {
    color: #fff;
}

.tcat a:visited, .tcat_avisited, .tcat a:link, .tcat_alink,
.tcat2 a:visited, .tcat_avisited, .tcat2 a:link, .tcat_alink {
    color: #fff;
    text-decoration: none;
}

#breadcrumbs {
    background-color: transparent;
    color: #8899A6;
    font-size: 16px;
    padding: 8px;
}

#breadcrumbs a:link, #breadcrumbs a:visited {
    color: #fff;
}

#breadcrumbs a:hover, #breadcrumbs a:active {
    color: #00aeef !important;
}

.pagenav td {
    background-color: #16171a;
    color: #8899A6;
    padding: 4px 6px;
    border-bottom: 0px !important;
}

.pagenav td.alt2 {
    background-color: #232429;
}

.pagenav td.alt2 .smallfont {
    color: #fff;
    font-size: 12px;
}

.pagenav td.alt1 a:link,.pagenav td.alt1 a:visited {
    color: #fff;
    font-size: 12px;
}

.pagenav td.alt1 a:hover, .pagenav td.alt1 a:active {
    color: #00aeef !important;
    font-size: 12px;
}

#pb div {
    background-color: transparent;
    border: 0px;
}

.alt1 hr {
    background-color: #16171a !important;
    display: block;
    height: 1px;
    border: 0;
    border-top: 1px solid #232429;
    margin: 1em 0;
    padding: 0; 
}

.alt1 .fieldset, .panelsurround .panel .fieldset {
    border: 1px solid #232429;
}

#posts .alt1 {
    color: #fff;
}

.bordered {
    border-top: 0px;
    border-right: 0px;
    border-left: 1px solid #16171a;
    background-color: #f6b832;
}

.bordered a {
    color: #16171a !important;
}

.bordered a:hover {
    color: #303030 !important;
}

.panelsurround, .panelsurround .panel {
    background-image: none;
    background-color: #16171a;
}

.panelsurround .panel {
    color: #fff;
    border: 0px;
    
}

.tborder {
    background: #16171a;
}

.darkBlue {
    background-color: #16171a;
    border-bottom-color: #00aeef;
    color: #fff;
    line-height: 24px;
    font-size: 16px;
    font-weight: normal;
    padding-left: 16px;
}
}
