css:
@-moz-document domain("boards.4chan.org") {

	/* COLORS */

	/** DESCRIPTION : set the colors **/
	:root {
		--body-bg: #17181A !important;
		--border: #000 !important;
		--border-own: #2C82C9 !important;
		--border-you: rgba(255, 100, 0, 1) !important;
		--catalog-hover-bg: #242528 !important;
		--dropdown-bg: #EBEBEB !important;
		--header-bg: #242528 !important;
		--input-bg: #242528 !important;
		--post-bg: #383942 !important;
		--table-bg: #383942 !important;
		--thumb-bg: #242528 !important;
		--watch-thread-bg: #008891 !important;
		--catalog-hover-color: #7E7E7E !important;
		--catalog-message-color: #FFF !important;
		--catalog-stat-color: #FFF !important;
		--catalog-subject-color: #EBEBEB !important;
		--current-color: #60646D !important;
		--dead-color: #D33257 !important;
		--info-color: #BBB !important;
		--link-color: #008891 !important;
		--menu-color: #EBEBEB !important;
		--name-color: #A34443 !important;
		--num-color: #897399 !important;
		--post-color: #EBEBEB !important;
		--quote-color: #2CC990 !important;
		--quotelink-color: #897399 !important;
		--subject-color: #987D3E !important;
		--you-color: #EB9532 !important;
		--dropdown-url: url('https://i.imgur.com/AN6BKJT.png');
	}

	/* GENERAL */

	/** DESCRIPTION : set the main font and change others elements **/
	* {
		font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
		font-size: 12pt;
		text-decoration: none !important;
	}

	*::-moz-focus-inner {
		border: 0;
	}

	input::-moz-placeholder,
	.filed::-moz-placeholder {
		color: #F0F1F5 !important;
		font-family: "Roboto" !important;
		font-size: 15pt !important;
	}

	html {
		overflow-x: hidden !important;
	}

	body {
		background: var(--body-bg) !important;
		margin: 0px !important;
	}

	.fixed body {
		padding: 0px !important;
	}

	.middlead.center {
		display: none !important;
	}

	#trigger-btn {
		background: no-repeat url("https://i.imgur.com/fkIEJ1T.png") center !important;
		background-color: transparent;
		background-size: cover !important;
		border: none !important;
		color: transparent;
		height: 30px !important;
		width: 30px !important;
		position: fixed !important;
		top: 20px !important;
		left: 20px !important;
		filter: brightness(0) invert(1) !important;
		opacity: .8 !important;
		z-index: 999 !important;
	}

	#close-btn {
		background: url('https://imgur.com/4GlrMN7.png') no-repeat 50% 50%;
		background-size: cover !important;
		background-color: transparent !important;
		border: none !important;
		color: transparent !important;
		height: 30px !important;
		width: 30px !important;
		position: fixed !important;
		top: 20px !important;
		right: 20px !important;
		filter: brightness(0) invert(1) !important;
		opacity: .8 !important;
	}

	a[href="#top"] {
		position: fixed !important;
		right: 30px !important;
		bottom: 20px !important;
		visibility: hidden;
	}

	a[href="#top"]:hover:before {
		background-color: #E26E67 !important;
		transition: all .3s ease-in-out !important;
	}

	a[href="#top"]::before {
		background-color: #D6523C !important;
		background-image: url("https://i.imgur.com/iUcfZuU.png") !important;
		background-position: center 40% !important;
		background-repeat: no-repeat !important;
		background-size: 28px auto !important;
		border-radius: 50% !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		color: transparent !important;
		content: "dd" !important;
		height: 40px !important;
		width: 40px !important;
		position: absolute !important;
		bottom: 0px !important;
		margin-left: -2px !important;
		transition: all .3s ease-in-out !important;
		visibility: visible !important;
	}

	:root.rounded-corners .reply,
	:root.rounded-corners.op-background .postContainer.opContainer,
	:root.rounded-corners .dialog:not(#header-bar),
	:root.rounded-corners .inline,
	:root.rounded-corners #thread-stats:root.rounded-corners #updater,
	:root.rounded-corners #menu,
	:root.rounded-corners .thumb,
	:root.rounded-corners .fileThumb img:not(.full-image),
	:root.rounded-corners .catalog-thumb,
	:root.rounded-corners .dd-menu ul,
	:root.rounded-corners.werkTyme .catalog-thread:not(:hover),
	:root.rounded-corners.werkTyme:not(.catalog-hover-expand) .catalog-thread,
	:root.rounded-corners.catalog-hover-expand .catalog-container:hover > .post,:root.rounded-corners.catalog-hover-expand .catalog-container:hover .catalog-reply {
		border-radius: 1px !important;
	}

	.archivedIcon.retina,
	#bannerCnt,
	#blotter,
	#boardNavDesktopFoot,
	.bottomCtrl.desktop,
	.exlinksOptionsLink.entry,
	.gal-buttons,
	.hide-thread-button,
	.hide-reply-button,
	.identityIcon.retina,
	img[title="Closed"],
	img[title="Sticky"],
	.is_arclist .center,
	.is_thread .postInfo .riceCheck,
	.pagelist.json-index,
	.party-hat,
	.postInfo .riceCheck,
	.qr-link-container,
	:root.banner-opacity #bannerCnt,
	#togglePostFormLink {
		display: none !important;
	}

	#mascot img {
		position: fixed !important;
		left: 0px !important;
	}

	strong[style*="red"] {
		color: #BEBEBE !important;
		font-size: 14pt !important;
		font-weight: normal !important;
		text-transform: lowercase !important;
	}

	/* HEADER */

	/** DESCRIPTION : change the elements of the header **/
	#header-bar,
	:root.header-gradient #header-bar {

		/*** HEADER CONTAINER ***/
		background: transparent !important;
		box-shadow: none !important;
		position: absolute !important;
	}

	#header-bar #board-list {

		/*** HEADER BOARD LIST ***/
		background: rgba(0, 0, 0, .9) !important;
		color: transparent !important;
		height: 100vh !important;
		width: 100% !important;
		text-align: center !important;
		position: fixed !important;
		top: 0px !important;
		right: 0px !important;
		bottom: 0px !important;
		left: 0px !important;
		margin: auto !important;
		z-index: 9999 !important;
		display: none;
	}

	#header-bar #board-list #custom-board-list {

		/*** HEADER CUSTOM BOARD LIST ***/
		font-size: 0px !important;
		width: 70% !important;
		position: absolute !important;
		top: 40% !important;
		right: 0px !important;
		bottom: 0px !important;
		left: 0px !important;
		margin: auto !important;
		column-count: 3 !important;
		column-gap: 0em !important;
	}

	#header-bar #board-list #custom-board-list a:not(.show-board-list-button) {
		font-size: 18pt !important;
		font-stretch: condensed !important;
		width: auto !important;
		text-align: left !important;
		display: block;
		transition: all .3s ease-in-out !important;
	}

	#header-bar #board-list #custom-board-list a {
		color: #FFF !important;
	}

	#header-bar #board-list #custom-board-list a:hover {
		color: #F29B34 !important;
		transition: all .3s ease-in-out !important;
	}

	#header-bar #board-list #custom-board-list .current {
		border-bottom: none !important;
		color: #F29B34 !important;
		font-weight: normal !important;
	}

	#header-bar #custom-board-list::before {
		content: "Custom Board List";
		color: #7E7E7E !important;
		font-family: "Roboto" !important;
		font-size: 25pt !important;
		font-stretch: condensed;
		position: absolute !important;
		top: -60px !important;
		left: 0px !important;
	}

	#header-bar #full-board-list .boardList {

		/*** HEADER FULL BOARD LIST ***/
		color: transparent !important;
		width: 90% !important;
		position: absolute !important;
		top: 20% !important;
		right: 0px !important;
		bottom: 0px !important;
		left: 0px !important;
		margin: auto !important;
		column-count: 5 !important;
		column-gap: 1em !important;
	}

	#header-bar #full-board-list .boardList::before {
		content: "Full Board List";
		color: #7E7E7E !important;
		font-size: 25pt !important;
		font-stretch: condensed !important;
		position: absolute !important;
		top: -60px !important;
		left: 0px !important;
	}

	#header-bar #full-board-list .boardList span {
		display: none !important;
	}

	.show-board-list-button {
		position: absolute !important;
	}

	#header-bar #full-board-list a {
		color: #FFF !important;
		font-family: "Roboto" !important;
		font-size: 15pt !important;
		font-stretch: condensed;
		width: 200px !important;
		text-align: left !important;
		transition: all .3s ease-in-out !important;
		display: block !important;
	}

	#header-bar #full-board-list .a:hover {
		color: #F29B34 !important;
		transition: all .3s ease-in-out !important;
	}

	#header-bar #full-board-list .current {
		color: #F29B34 !important;
		font-weight: normal !important;
	}

	#header-bar .show-board-list-button,
	#header-bar .hide-board-list-button {
		visibility: hidden;
	}

	#header-bar .show-board-list-button::after,
	#header-bar .hide-board-list-button::after {
		border-radius: 50% !important;
		content: "" !important;
		padding: 40px 0px 0px 40px !important;
		position: fixed !important;
		right: 20px !important;
		bottom: 20px !important;
		visibility: visible !important;
	}

	#header-bar .show-board-list-button::after {
		background-color: #47B39D !important;
		background-image: url("https://i.imgur.com/JNGrOL0.png") !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
		background-size: 22px auto !important;
	}

	#header-bar .hide-board-list-button::after {
		background-color: #B05F6D !important;
		background-image: url("https://i.imgur.com/ZXTSKPA.png") !important;
		background-position: center !important;
		background-repeat: no-repeat !important;
		background-size: 22px auto !important;
	}

	#header-bar #shortcuts,
	:root.fixed:not(.centered-links) #shortcuts {
		width: 100% !important;
		text-align: center !important;
		position: absolute !important;
		right: 0px !important;
		top: 122px !important;
		left: 0px !important;
		display: inline-block !important;
		float: none !important;
	}

	#header-bar #shortcuts #shortcut-watcher #watcher-link,
	#header-bar #shortcuts .expand-all-shortcut,
	#header-bar #shortcuts #shortcut-gallery a,
	#header-bar #shortcuts #shortcut-qr a,
	#header-bar #shortcuts #shortcut-index-refresh a,
	#header-bar #shortcuts #shortcut-settings a,
	#header-bar #shortcuts #OneeChanLink {
		color: #FFF !important;
		text-shadow: 0px 1px 2px rgba(0, 0, 0, .5) !important;
	}

	#header-bar #shortcuts #shortcut-watcher .replies-quoting-you::before,
	#thread-watcher #watched-threads .replies-quoting-you span {
		color: #47D2E9 !important;
	}

	#thread-watcher #watched-threads .dead-thread {
		opacity: 1 !important;
	}

	#thread-watcher #watched-threads .dead-thread * {
		color: #FF6766 !important;
	}

	#header-bar .fa-compress::before {
		color: #2CC990 !important;
	}

	#header-bar #shortcut-menu > .menu-button {
		background: no-repeat url("https://i.imgur.com/AN6BKJT.png") center !important;
		background-size: 15px auto !important;
		height: 9px !important;
		width: 15px !important;
	}

	#header-bar #shortcut-menu .menu-button i {
		display: none !important;
	}

	.shortcut #menu,
	.shortcut .submenu {
		background: var(--post-bg) !important;
		color: var(--post-color) !important;
		border: 1px solid var(--border) !important;
		border-radius: 1px !important;
		padding: 20px 15px !important;
		min-width: 100px !important;
	}

	.shortcut #menu a {
		color: #2CC990 !important;
	}

	.shortcut #menu .entry,
	.shortcut #menu * {
		color: var(--menu-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		text-transform: lowercase !important;
	}

	.shortcut .focused.entry {
		background: transparent !important;
		color: #BEBEBE !important;
	}

	.shortcut .field,
	.shortcut .field.focused {
		background: var(--input-bg) !important;
		border: 1px solid var(--border) !important;
	}

	#thread-watcher {

		/*** HEADER THREAD WATCHER ***/
		background: var(--post-bg) !important;
		border: 1px solid var(--border) !important;
		border-radius: 1px !important;
		box-shadow: none !important;
		color: #1BA39C !important;
		padding: 20px !important;
	}

	#thread-watcher > .move {
		background: var(--header-bg) !important;
		border-bottom: 1px solid var(--border) !important;
		font-size: 15pt !important;
		font-stretch: condensed !important;
		padding: 12px 10px 14px 20px !important;
		margin: -20px -20px 20px -20px !important;
		text-transform: lowercase !important;
	}

	#thread-watcher .refresh {
		color: #0F6177 !important;
	}

	#thread-watcher .fa.fa-angle-down {
		color: var(--post-color) !important;
	}

	#thread-watcher #menu,
	#thread-watcher .submenu {
		background: var(--post-bg) !important;
		border: 1px solid var(--border) !important;
		border-radius: 1px !important;
		box-shadow: none !important;
		padding: 20px 15px !important;
		min-width: 100px !important;
	}

	#thread-watcher #menu a {
		color: #000 !important;
	}

	#thread-watcher #menu .entry,
	#thread-watcher #menu * {
		color: var(--menu-color) !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		text-transform: lowercase !important;
	}

	#thread-watcher .focused.entry {
		background: transparent !important;
		color: var(--menu-color) !important;
	}

	.has-submenu::after {
		background: no-repeat var(--dropdown-url) center !important;
		background-size: cover !important;
		border: none !important;
		padding: 10px 0px 0px 10px !important;
		margin-top: 9px !important;
	}

	#thread-watcher .move .close {
		color: var(--post-color) !important;
		font-size: 16pt !important;
		padding: 0px 4px;
		top: 10px !important;
		right: 15px !important;
	}

	#thread-watcher .fa.fa-times {
		color: #2CC990 !important;
	}

	#thread-watcher .watcher-link span {
		color: #1BBC9B;
		font-size: 14pt !important;
		font-stretch: condensed !important;
	}

	#thread-watcher .current span {
		color: var(--current-color) !important;
		font-weight: normal !important;
	}

	#thread-watcher .fa::before {
		font-size: 13pt !important;
	}

	#fourchanx-settings,
	#oneechan-options {
		background: var(--post-bg) !important;
		border: 1px solid var(--border) !important;
	}

	#fourchanx-settings *,
	#oneechan-options * {
		color: var(--post-color);
		font-size: 14pt;
		font-stretch: condensed !important;
	}

	#fourchanx-settings a,
	#oneechan-options a {
		color: #2CC990 !important;
	}

	#fourchanx-settings nav {
		background: var(--header-bg) !important;
		border-bottom: 1px solid var(--border) !important;
		padding: 14px 20px 28px 20px !important;
		margin: -3px !important;
	}

	#fourchanx-settings .sections-list,
	#fourchanx-settings .credits {
		color: transparent;
	}

	#fourchanx-settings .close:before {
		font-size: 13pt !important;
	}

	.suboption-list > div::before,
	.suboption-list::before {
		border-color: #242424 !important;
	}

	#fourchanx-settings .sections-list a:not(.tab-selected),#fourchanx-settings .credits a {
		color: var(--post-color) !important;
		font-size: 14pt !important;
	}

	#fourchanx-settings .tab-selected {
		color: var(--post-color) !important;
		font-size: 14pt !important;
		font-weight: normal !important;
		opacity: .5;
	}

	#fourchanx-settings fieldset {
		border-radius: 1px !important;
		border-color: var(--border) !important;
	}

	#fourchanx-settings .section-container {
		top: 4.5em;
		z-index: -1 !important;
	}

	#fourchanx-settings legend,
	#oneechan-options .option.header .option-title {
		border: none !important;
		color: var(--post-color) !important;
		font-size: 15pt !important;
		font-weight: normal !important;
	}

	#fourchanx-settings .suboption-list > div:last-of-type {
		background: transparent !important;
	}

	#options-container:not(.yui-skin-sam) {
		padding: 0px !important;
	}

	#options-container select {
		min-height: 30px !important;
		margin-top: -4px !important;
	}

	#oneechan-options #options-tabs {
		margin-left: -1px !important;
	}

	#oneechan-options .tab-item * {
		background: var(--post-bg) !important;
		color: var(--post-color) !important;
		font-family: "Roboto" !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: normal !important;
		line-height: 12px !important;
		text-transform: lowercase !important;
	}

	#oneechan-options #main-section > .option:nth-of-type(2n),
	#oneechan-options #main-section > .option:nth-of-type(2n+1) {
		background: var(--post-bg) !important;
		border: 1px solid var(--border) !important;
		padding: 5px 8px !important;
		margin-bottom: -1px !important;
	}

	#oneechan-options .options-button {
		background: var(--input-bg) !important;
		border: 1px solid var(--border) !important;
		border-radius: 2px !important;
		color: var(--post-color) !important;
		font-family: "Roboto" !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		line-height: 20px !important;
		padding-bottom: 5px !important;
		text-transform: lowercase !important;
	}

	#import-link {
		line-height: 20px !important;
		padding-bottom: 5px !important;
	}

	.options-close {
		margin-bottom: 5px !important;
	}

	#oneechan-options select,
	#oneechan-options input,
	#fourchanx-settings .field,
	#fourchanx-settings select {
		background: var(--input-bg) !important;
		border: 1px solid var(--border) !important;
		border-radius: 2px !important;
		color: var(--post-color) !important;
		font-size: 14pt !important;
		font-family: "Roboto" !important;
	}

	#fourchanx-settings .field {
		padding: 10px;
	}

	#fourchanx-settings .section-advanced #archive-board-select {
		padding: 2px !important;
	}

	#oneechan-options .riceCheck {
		position: relative !important;
		top: 3px !important;
	}

	.riceCheck,
	.riceCheck:hover {
		background: #FFF !important;
		border: 1px solid var(--border) !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
	}

	input[type="checkbox"]:checked::before,
	input[type="checkbox"]:checked + .riceCheck::before {
		filter: brightness(0) !important;
	}

	#add-mascot,
	#add-theme {
		border: 1px solid var(--border) !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		padding: 20px 15px 30px 15px !important;
	}

	#add-mascot label,
	#add-theme label {
		color: var(--post-color) !important;
		font-stretch: condensed;
	}

	.add-mascot-label input,
	#add-theme input {
		background: var(--input-bg) !important;
		border: 1px solid var(--border) !important;
		border-radius: 2px !important;
		color: var(--post-color) !important;
		font-size: 11pt !important;
		font-stretch: condensed !important;
	}

	#mascot-buttons-container a,
	#add-theme .options-button {
		background: var(--input-bg) !important;
		border: 1px solid var(--border) !important;
		border-radius: 2px !important;
		color: var(--post-color) !important;
		font-stretch: condensed !important;
		font-size: 11pt !important;
		font-family: "Roboto" !important;
		padding-bottom: 4px !important;
		margin-top: -1px !important;
	}

	.section-main button,
	#update-archives,
	#apply-css {
		background: var(--body-bg);
		border: 1px solid var(--border);
		color: var(--post-color);
		padding: 5px 10px !important;
		margin-top: 10px;
	}

	.section-keybinds kbd,
	code {
		background: var(--body-bg) !important;
		border: none;
		border-radius: 2px;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
		color: var(--post-color) !important;
		padding: 2px 6px;
	}

	#header-bar .notification {

		/*** HEADER NOTIFICATIONS ***/
		font-stretch: condensed !important;
		text-shadow: none !important;
		padding: 10px 10px 15px 10px !important;
	}

	#header-bar .notification * {
		font-size: 14pt !important;
		font-weight: normal !important;
	}

	/* BANNER */

	/** DESCRIPTION : change the elements of the banner **/:root {
		--banner: url('https://i.imgur.com/BZIzz1k.jpg');
		--banner-a: url("https://i.imgur.com/9bA5Jl6.jpg");
		--banner-c: url("https://i.imgur.com/4c5m2KO.jpg");
		--banner-ck: url("https://i.imgur.com/hJ7lLp2.jpg");
		--banner-e: url("https://i.imgur.com/5pjIJyp.jpg");
