css:
@-moz-document url-prefix("https://ndla.no/") {
body {
    color: #ffffff;
    background-color: #1E2026;
}
.subject_link {
    color: #ffffff !important;
}
#navigation,
.block-tab,
#footer {
    color: #ffffff;
    background-color: #2D313D !important;
}
a {
    color: #7588FF !important;
}
.resourcemap-button {
    background-color: #F14165 !important;
    color: #FFffff !important;
}
#header .logo img {
    width: 189px;
    height: 60px;
    content: url(https://tonystr.net/img/ndla_red.png);
}
.left-menu-content {
    background-color: #2D313D;
}
#left-menu-menu-tab,
.ndla2010-tab-center {
    background-color: #2D313D !important;
}
.tray-tab:hover,
.tray-tab:hover a {
    background-color: #F14165 !important;
    color: #ffffff !important;
}
#left-menu-menu-tab:hover,
#left-menu-menu-tab:hover a {
    background-color: #F14165 !important;
    color: #ffffff !important;
}
#grep {
    background-color: #F14165 !important;
    color: #ffffff !important;
}
#subjects.coursebrowser {
    background-color: #1E2026;
    box-shadow: 0 2px 14px #000000;
}
li.tab_title.active > .block-tab > a {
    background-color: #1E2026 !important;
}
li.tab_title.active > .block-tab > a > center {
    color: #F14165 !important;
}
.feed-content {
    background-color: #1E2026;
}
#ndla_feeds {
    outline: 2px dashed #2D313D !important;
}
.menu li:hover {
    color: #000000;
}
.menu li.open {
    background-color: #F14165 !important;
    color: #ffffff !important;
}
.menu li.open:hover {
    color: #000000 !important;
}
.menu ul {
    background-color: #1E2026;
    color: #ffffff !important;
}
.menu ul:hover {
    background-color: #1E2026;
}
.blue-background {
    background-color: #F14165;
}
}
