css:
@-moz-document domain("twitter.com") {
/* uncomment below if you want a better font */
/* @import url('https://fonts.googleapis.com/css?family=Quicksand'); */

body.ms-windows 
{
    font-family: Quicksand, "Segoe UI", Arial, sans-serif;
}

/* orange */
.twitter-atreply > s, 
.twitter-atreply > b,
.twitter-hashtag > s, 
.twitter-hashtag > b,
.u-linkComplex-target.trend-name
{
    color: #EB821C !important;
    transition: all 0.3s;
}

/* slighty darker orange */
.twitter-atreply > s:hover, 
.twitter-atreply > b:hover,
.twitter-hashtag > s:hover, 
.twitter-hashtag > b:hover,
.u-linkComplex-target.trend-name:hover
{
    color: #AE6015 !important;
    text-decoration: none !important;
}


/* cyan */
.username.u-dir.u-textTruncate,
.ProfileHeaderCard-screennameLink, 
.ProfileHeaderCard-screennameLink:hover,
.AdaptiveNewsHeadlineDetails-user:hover,
.QuoteTweet-fullname:hover,
.TwitterCard .SummaryCard-destination:hover,
.TwitterCard .MomentCard-subtitle,
.btn-link.translate-button:hover,
.btn-link.back-to-top.hidden:hover,
.typeahead button.clear-recent-searches:hover,
.ProfileCard .pretty-link:hover
{
    color: #00fff6 !important;
    transition: all 0.3s;
    text-decoration: none;
}

/* pink */
.ProfileHeading-toggleItem.is-active, 
.ProfileHeading-toggleItem.is-active:hover, 
.ProfileHeading-toggleItem.is-active:focus,
.ProfileAvatarEditing-addAvatarHelp, 
.ProfileAvatarEditing-changeAvatarHelp, 
.ProfileAvatarEditing-dropAvatarHelp, 
.ProfileAvatarEditing-iframeSavingHelp,
.ProfileHeaderEditing-addHeaderHelp, 
.ProfileHeaderEditing-changeHeaderHelp, 
.ProfileHeaderEditing-dropHeaderHelp, 
.ProfileHeaderEditing-iframeSavingHelp,
.AdaptiveNewsHeadlineDetails-user, 
.AdaptiveNewsHeadlineDetails-user:focus,
.QuoteTweet-fullname,
.TwitterCard .SummaryCard-destination,
.TwitterCard .MomentCard-badge,
.TwitterCard-container--clickable,
.tweet .context .pretty-link s, 
.tweet .context .pretty-link b,
.ListCreationModule-title,
.modal-header .modal-title,
.module .active .list-link,
.stats a,
.stats .stat-count strong,
.permalink .stream-end .Icon--logo,
.btn-link.translate-button,
.btn-link.translate-button .Icon--translator,
.UserActions .user-dropdown-icon,
.stream-end, 
.stream-loading, 
.stream-placeholder, 
.content-placeholder, 
.back-to-top, 
.btn-link.back-to-top,
.Icon--small,
h3,
.find-friends-list .source,
.app > .content > div > strong,
.FacebookConnect-title,
.typeahead a, 
.typeahead .fullname,
.typeahead button.clear-recent-searches,
.ProfileCard .pretty-link
{
    color: #ff007e;
    transition: all 0.3s;
}

/* dark grey */
.LiveVideoHomePageModule-title,
.LiveVideoHomePageModule-host,
.LiveVideoHomePageModule-eventTitle,
.ProfileTweet-actionButton, 
.ProfileTweet-actionButtonUndo,
.ProfileTweet-actionCount,
.ProfileNav-label,
.ProfileNav-value,
.ProfileNav-item.is-active .ProfileNav-label, 
.ProfileNav-item.is-active:hover .ProfileNav-label,
.ProfileHeading-toggleItem.is-active,
.ProfileHeading-title,
.ProfileEmptyTimeline-prefilledTweetsHeading,
.ProfileHeaderCard-bio,
.ProfileHeaderCard-birthdateText, 
.ProfileHeaderCard-joinDateText, 
.ProfileHeaderCard-locationText, 
.ProfileHeaderCard-onlineHoursText, 
.ProfileHeaderCard-responsivenessLevelText, 
.ProfileHeaderCard-urlText, 
.ProfileHeaderCard-vineProfileText, 
.ProfileHeaderCard-periscopeProfileText,
.ProfileHeaderCard-birthdate, 
.ProfileHeaderCard-joinDate, 
.ProfileHeaderCard-location, 
.ProfileHeaderCard-onlineHours, 
.ProfileHeaderCard-responsivenessLevel, 
.ProfileHeaderCard-url, 
.ProfileHeaderCard-vineProfile, 
.ProfileHeaderCard-periscopeProfile,
.ProfileEmptyTimeline-tweetBoxHeading,
.ProfilePrefilledTweet .tweet .tweet-text,
.ProfileTweetbox .tweet-box,
.tweet-box.rich-editor:focus,
.VisibilitySettings-dropdownIcon, 
.VisibilitySettings-dropdownCaret, 
.VisibilitySettings-optionIcon,
.ProfileHeaderCardEditing-name .ProfileHeaderCardEditing-editableField,
#user_description,
.ProfilePage .ProfileHeaderCardEditing-editableField.is-showPlaceholder::before,
#user_url::placeholder,
.ProfileHeaderCardEditing-name .ProfileHeaderCardEditing-editableField,
.BirthdateSelect-label,
.BirthdateSelect-button.is-notSet,
.message .message-text,
.ProfileHeaderCardEditing-item .ProfileHeaderCardEditing-editableField, 
.ProfileHeaderCardEditing-item .ProfileHeaderCardEditing-editableField:focus, 
.ProfileHeaderCardEditing-item .ProfileHeaderCardEditing-editableField.fake-focus, 
.ProfileHeaderCardEditing-item .ProfileHeaderCardEditing-measuringField,
.ProfileHeaderCardEditing-item .ProfileHeaderCardEditing-editableField, 
.ProfileHeaderCardEditing-item .ProfileHeaderCardEditing-editableField:focus, 
.ProfileHeaderCardEditing-item .ProfileHeaderCardEditing-editableField.fake-focus, 
.ProfileHeaderCardEditing-item .ProfileHeaderCardEditing-measuringField,
#search-query::placeholder,
.AdaptiveSearchPage-moduleTitle,
.AdaptiveNewsLargeImageHeadline-title,
.AdaptiveFiltersBar-target,
.QuoteTweet .tweet-content, 
.QuoteTweet-text a, 
.QuoteTweet-text a:hover, 
.QuoteTweet-text a:focus, 
.QuoteTweet-text a:active, 
.QuoteTweet-text .pretty-link b, 
.QuoteTweet-text .pretty-link s, 
.QuoteTweet-text .pretty-link:hover b, 
.QuoteTweet-text .pretty-link:hover s, 
.QuoteTweet-text .pretty-link:focus b, 
.QuoteTweet-text .pretty-link:focus s, 
.QuoteTweet-text .pretty-link:active b, 
.QuoteTweet-text .pretty-link:active s,
.TwitterCard .MomentCard-description p,
.tweet > .context .with-icn .Icon,
.GridTimeline-emptyText,
.ListCreationModule-description,
.t1-legend,
.list-editor .help-text,
.list-editor .help-textinput:focus, 
textarea:focus, 
div[contenteditable="true"]:focus, 
div[contenteditable="true"].fake-focus, 
div[contenteditable="plaintext-only"]:focus, 
div[contenteditable="plaintext-only"].fake-focus,
.list-editor .options label, 
.list-editor .options input,
input:focus, textarea:focus, 
div[contenteditable="true"]:focus, 
div[contenteditable="true"].fake-focus, 
div[contenteditable="plaintext-only"]:focus, 
div[contenteditable="plaintext-only"].fake-focus,
.Trends .context-trend-item .trend-item-stats,
.modal-body p,
.module .list-link,
.metadata, 
.metadata button.btn-link, 
.metadata a,
.tweet-form .reply-users,
.tweet-form.condensed .tweet-box,
.tweet-form.condensed div.rich-editor.is-showPlaceholder::before, 
.tweet-form.condensed div.rich-editor:empty::before,
.UserSmallListItem-contextIcon .Icon--follower,
.DMActivity-title,
.DMInboxItem-snippet,
.DMActivity-header,
.content-header .subheader,
h4.find-friends-top-subheader, 
h4.find-friends-bottom-subheader,
.fine-print,
.MutedKeywordsFooter-emptyMessage,
.app .description,
.facebook-metadata,
.FacebookConnect-text,
.muted,
.MomentCapsuleSubtitle-publish[data-visibility-mode="private"],
.MomentDialog-text,
.typeahead strong,
.hovercard.profile-card .profile-card-listName
{
    color: #999;
    transition: all 0.3s;
}

/* slightly darker grey */
.ReplyingToContextBelowAuthor,
.with-icn,
.list-editor .options label b,
.import-prompt p > span,
.self-thread-context,
.ActivityItem-activityTimestamp,
.DMInboxItem-timestamp,
.app .creator, .app .creator a,
#user_data_dashboard_auth_password::placeholder,
.Footer-item, 
.Footer-link,
.ThreadedConversation-unavailableText
{
    color: #666;
    transition: all 0.3s;
}

/* button */
.EdgeButton, 
EdgeButton:visited
{
    border-radius: 2px;
}
.EdgeButton--tertiary,
.EdgeButton--primary,
.EdgeButton--primary:focus,
.EdgeButton--secondary, 
.EdgeButton--secondary:focus, 
.EdgeButton--secondary:active
{
    border-color: #ff007e;
    color: #ff007e;
    border-radius: 2px ;
    background-color: transparent;
    box-shadow: none;
    transition: all 0.3s;
}

/* button hover */
.EdgeButton--tertiary:hover,
.EdgeButton--secondary:hover,
.EdgeButton--primary:hover,
.EdgeButton--danger:hover
{
    border-color: #00fff6 !important;
    color: #00fff6 !important;
    background: transparent !important;
    box-shadow: 0px 0px 10px #00fff6, 0px 0px 10px #00fff6 inset;
    border-radius: 2px;
}

/* images, gifs, videos  */
.ProfileAvatar-image, 
.ProfileAvatar-placeholderImage
{
    border-radius: 2px;
}
.ProfileAvatar-image,
.Avatar
{
    border: 1px solid #ff007e;
}
.Avatar
{
    border-radius: 2px;
}

/* brighter greyish cards */
.TwitterCard .MomentCard,
.permalink .inline-reply-tweetbox,
.ThreadedConversation-moreReplies
{
    background-color: #121212;
}

/* boxes/cards */
.SidebarFilterModule,
.AdaptiveRelatedSearches,
.AdaptiveNewsRelatedHeadlines,
.AdaptiveNewsLargeImageHeadline-body,
.AdaptiveFiltersBar,
.ProfileListsTimeline .GridTimeline-end:not(.has-items),
.ListCreationModule,
.modal-content,
.modal-header,
.ProfileUserMomentsTimeline .MomentCapsuleSummary,
#global-tweet-dialog .modal-tweet-form-container, 
#Tweetstorm-dialog .modal-body,
.dropdown-menu,
.message,
.module .list-link:hover, 
.module .active .list-link,
.module .list-link
.permalink-tweet:hover, 
.permalink-tweet.focus,
.permalink-tweet,
#permalink-overlay-body > .permalink-container > .permalink,
.ThreadedConversation-showMoreThreads,
.permalink .inline-reply-tweetbox-container,
.PermalinkOverlay-spinnerContainer,
.stream-end-inner,
.NotificationsHeadingContent,
.DMActivity-header,
.DMActivity-body,
.module .list-link,
.MomentDialog-header, 
.MomentDialog-footer,
.permalink,
#global-tweet-dialog .modal-tweet .tweet
{
    background-color: #0b0b0b;
    border: 0px;
    transition: all 0.3s; 
}

/* tweet hover */
.conversation-module .conversation-tweet-item .tweet:hover,
.ActivityItem.has-clickAction:hover,
.DMInboxItem:hover,
.find-friends-list li:hover,
.ThreadedConversation-unavailableTweet,
#stream-items-id,
.permalink .stream-end-inner,
.hovercard .profile-social-proof
{
    background-color: #0e0e0e !important;
}
/* retweet, like and reply button */
.ProfileTweet-action--reply:hover .ProfileTweet-actionButton, 
.ProfileTweet-action--reply:hover .ProfileTweet-actionCount, 
.ProfileTweet-action--reply .ProfileTweet-actionButton:focus, 
.ProfileTweet-action--reply .ProfileTweet-actionButton:focus .ProfileTweet-actionCount
{
    color: #00fff6 !important;
    text-shadow: 0px 0px 10px #00fff6;
}

.ProfileTweet-action--retweet:hover .ProfileTweet-actionButton, 
.ProfileTweet-action--retweet:hover .ProfileTweet-actionCount, 
.ProfileTweet-action--retweet .ProfileTweet-actionButton:focus, 
.ProfileTweet-action--retweet .ProfileTweet-actionButton:focus .ProfileTweet-actionCount, 
.retweeted .ProfileTweet-action--retweet .Icon--retweet, 
.retweeted .ProfileTweet-action--retweet .ProfileTweet-actionButtonUndo
{
    color: #EB821C !important;
	text-shadow: 0px 0px 10px #EB821C;
}

.ProfileTweet-action--favorite:hover .ProfileTweet-actionButton, 
.ProfileTweet-action--favorite:hover .ProfileTweet-actionCount, 
.ProfileTweet-action--favorite .ProfileTweet-actionButton:focus, 
.ProfileTweet-action--favorite .ProfileTweet-actionButton:focus .ProfileTweet-actionCount, 
.favorited .ProfileTweet-action--favorite .Icon--heart, 
.favorited .ProfileTweet-action--favorite .ProfileTweet-actionButtonUndo
{
    color: #ff007e !important;
    text-shadow: 0px 0px 10px #ff007e;
}

/* arrow on dropdown window */
.dropdown-caret .caret-inner
{
    border-bottom-color: #0b0b0b;
}

/* box shadow */
.dropdown-menu,
.ProfileCanopy-navBar,
.global-nav-inner
{
    box-shadow: 0px 0px 10px black;
}

/* no nasty ass borders */
#global-tweet-dialog .modal-header, 
#Tweetstorm-dialog .modal-header,
.modal-footer,
.module li:first-child .list-link,
hr,
.content-inner.no-stream-end,
.permalink .in-reply-to .tweet, .permalink .replies-to .tweet, 
.permalink .replies-to .ThreadedConversation--loneTweet .stream-tombstone-item,
.ThreadedConversation, 
.ThreadedConversation-moreReplies::after,
.permalink.has-replies .inline-reply-tweetbox,
.permalink .inline-reply-tweetbox,
.tweet .stats,
.permalink .stats .avatar-row a:first-child,
.btn.focus:hover, 
.btn:focus:hover,
.stream-end-item, 
.stream-end, 
.stream-loading, 
.stream-placeholder,
.ActivityItem,
.DMInboxItem,
.content-header, 
.content-no-header,
.find-friends-list li,
.account, 
.tweet, 
.app, 
.RtbAd-container,
.btn:focus,
#notifications-optout-all-top,
.input-prepend .add-on, .input-append .add-on,
.typeahead .dropdown-inner > .has-results ~ .has-results, .typeahead .dropdown-inner > .has-items ~ .has-items
{
    border: 0px !important;
}
}

@-moz-document domain("twitter.com") {
/* body */
body 
{
    background-color: #121212;
    color: #999;
}

/* navbar */
.nav > li /* navbar text */
{
    color: #999;
}
.nav .session .dropdown-toggle
{
    border-radius: 2px;
}
.bird-topbar-etched /* logo */
{
    color: #ff007e;
}
.bird-topbar-etched:hover /* logo Hover*/
{
    color: #00fff6;
}
.global-dm-nav .dm-new, /* notification count */
.global-nav .people .count 
{
    background-color: #ff007e;
    border-radius: 0px;
    color: #ff007e;
}
.global-nav .people .count .count-inner /* notification count */
{
    background: #0e0e0e;
    border-radius: 0px;
}
.global-nav-inner /* bakcground */
{
    background: #0b0b0b;
}
.global-nav .search-input /* search bar */
{
    background-color: #090909;
    border-radius: 0px;
    border: 1px solid transparent;
    color: #ff007e;
    height: 46px;
    font-size: 16px;
    transition: all 0.3s;
    margin-top: 1px;
}
.global-nav .form-search /* position of search bar */
{
    margin: 0 15px 0 0;
}
.global-nav .search-icon /* search icon */
{
    display: none;
}
.typeahead-accounts.social-context .typeahead-account-item .typeahead-user-item-info 
{
    line-height: 40px;
    height: 40px;
}
.typeahead .fullname 
{
    margin-left: 15px;
}
.typeahead .social-context .avatar
{
    margin-top: 10px;
}
.typeahead button.clear-recent-searches:hover
{
    text-shadow: 0px 0px 10px #00fff6;
}
.dropdown-menu .typeahead-items:not(.typeahead-accounts) li > a:focus, 
.dropdown-menu .typeahead-items:not(.typeahead-accounts) li > a:hover, 
.dropdown-menu .typeahead-items:not(.typeahead-accounts) .selected a, 
.dropdown-menu .typeahead-accounts a:focus .fullname, 
.dropdown-menu .typeahead-accounts a:hover .fullname, 
.dropdown-menu .typeahead-accounts .selected .fullname, 
.dropdown-menu .typeahead-accounts .typeahead-accounts-shortcut a:focus, 
.dropdown-menu .typeahead-accounts .typeahead-accounts-shortcut a:hover, 
.dropdown-menu .typeahead-accounts .typeahead-accounts-shortcut.selected a
{
    text-decoration: none;
}
.dropdown-menu .typeahead-items li > a:focus, 
.dropdown-menu .typeahead-items li > a:hover, /* Search Result Hover */
.dropdown-menu .typeahead-items .selected, 
.dropdown-menu .typeahead-items .selected a,
.typeahead a:hover, .typeahead a:hover strong, 
.typeahead a:hover .fullname,
.typeahead .selected a, 
.typeahead .selected strong, 
.typeahead .selected .fullname, 
.typeahead .selected .Icon--close
{
    background-color: transparent !important;
    color: #00fff6 !important;
    text-decoration: none !important;
    text-shadow: 0px 0px 10px #00fff6;
}
.global-nav .search-input:focus, .global-nav .search-input.focus /* search bar focused */
{
    border: 1px solid transparent;
    background-color: #060606;
    color: #ff007e;
}
#global-actions > li:hover > a, /* navbar text */
#global-actions > li > a:focus, 
#global-actions > li.active .text, 
.DashUserDropdown.dropdown-menu .nightmode-toggle .Icon, 
.nav.right-actions > li > a:hover, 
.nav.right-actions > li > a:focus,
.u-textUserColor, 
.u-textUserColorHover:hover, 
.u-textUserColorHover:hover .ProfileTweet-actionCount, 
.u-textUserColorHover:focus
{
    color: #ff007e !important;
}
#global-actions > li > a
{
    border-color: #ff007e !important;
}

/* main page */
.dashboard-left 
{
    margin-top: 2px;
}
.LiveVideoHomePageModule-toggleClose 
{
    font-size: 24px;
    color: #ff007e;
    transition: all 0.3s;
    margin-left: 110px;
}
.LiveVideoHomePageModule-toggleClose:hover 
{
    color: #00fff6 !important;
    text-decoration: none !important;
}
.LiveVideoHomePageModule-toggleOpen 
{
    font-size: 24px;
    margin-left: 104px;
}
.ProfileCard 
{
    background-color: #0e0e0e;
    border: 0px;
}
.ProfileCard-bio 
{
    color: #999;
}
.ProfileCard .ProfileCard-screennameLink,  
.ProfileCard .ProfileCard-screennameLink:focus 
{
    color: #00fff6;
    font-size: 12px;
    font-weight: normal;
    transition: all 0.3s;
}
.ProfileCard .ProfileCard-screennameLink:hover
{
    color: #ff007e;
    text-decoration: none;
    font-size: 12px;
}
#profile-hover-container
{
    margin-top: 0px;
}
.tooltip
{
    margin-top: 0px !important;
}
.WtfLargeCarouselStreamItem-title,
.recap-header
{
    color: #999;
}
.recap-header
{
    border: 1px solid transparent;
}
.home-tweet-box, /* tweet box */
.LiveVideo-tweetBox, 
.RetweetDialog-commentBox 
{
    background-color: #0b0b0b;
}
.top-timeline-tweetbox .timeline-tweet-box /* tweet box border */
{
    border: 1px solid #0b0b0b;
}
#tweet-box-home-timeline /* tweet text box */
{
    background: #090909;
    transition: all 0.3s;
    color: #999;
}
#tweet-box-home-timeline:focus /* tweet text box focus */
{
    background: #060606;
}
.RichEditor, .TweetBoxAttachments 
{
    border-color: #090909;
}
.RichEditor 
{
    background: #090909;
    border-radius: 2px;
}
.RichEditor.RichEditor--emojiPicker.is-fakeFocus /* tweet text box */
{
    box-shadow: none;
    border: 1px solid #090909;
}
.RadialCounter-progressUnderlay 
{
    stroke: #999;
}
.EdgeButton.EdgeButton--icon 
{
    border-radius: 2px;
}
.EdgeButton--secondary, 
.EdgeButton--secondary:hover, 
.EdgeButton--secondary:focus, 
.EdgeButton--secondary:active 
{
    border-color: #ff007e;
    color: #ff007e;
}
.EdgeButton--secondary.disabled, 
.EdgeButton--secondary[disabled], 
.EdgeButton--secondary[disabled]:hover, 
.EdgeButton--secondary[aria-disabled="true"] 
{
    background-color: transparent;
}
.EmojiPicker-trigger,
.RadialCounter-progressUnderlay,
.tweet-form div.rich-editor.is-showPlaceholder::before, 
.tweet-form div.rich-editor:empty::before
{
    color: #999;
}
/* avatar */
.DMAvatar
{
    border-radius: 2px;
    border: 1px solid #ff007e;
}
.ProfileUserList .Avatar
{
    border-radius: 2px;
}
.avatar,
.avatar--circular,
.ProfileCard-avatarImage
{
    border-radius: 2px;
    border: 1px solid #ff007e;
}
.ProfileCard-avatarLink
{
    background-color: transparent !important;
}
.new-tweets-bar /* new tweets */
{
    background-color: #0b0b0b;
    border: 0px;
    color: #ff007e;
}
.new-tweets-bar:hover /* new tweet hover */
{
    background-color: #0b0b0b;
    border: none;
    color: #00fff6;
}
.stream-item + .stream-item.separated-module:not(.no-header-background-module), 
.stream-item.separated-module + .stream-item:not(.no-header-background-module), 
.PromptbirdPrompt-streamItem.separated-module + .stream-item:not(.no-header-background-module) 
{
    border-top: 1px solid transparent;
}
.DashboardProfileCard,	/* Profile Card */
.Trends,				/* Trending Card */
.module .flex-module,	/* who to follow card */
.stream-item:not(.no-header-background-module) /* main twitter cards */
{
    border: 0px;
    background: #0b0b0b;
    color: #999;
}
.wtf-module .UserSmallListItem, /* who to follow */
.wtf-module .import-prompt .cta
{
    border: 0px;
}
.EdgeButton.EdgeButton--secondary.EdgeButton--small.button-text.follow-text /* follow button */
{
    border-radius: 2px;
    border-color: #ff007e;
    color: #ff007e;
    background: transparent;
    transition: all 0.3s;
}
.EdgeButton.EdgeButton--secondary.EdgeButton--small.button-text.follow-text:hover /* follow button hover */
{
    border-color: #00fff6;
    color: #00fff6;
}
.EdgeButton--secondary:focus 
{
    box-shadow: none;
}
.DashboardProfileCard-avatarImage /* avatar in profile card */
{
    border-radius: 2px;
    border: 1px solid #ff007e;
}
.DashboardProfileCard-avatarLink 
{
    background-color: transparent;
}
.u-textInheritColor.js-nav, /* name on profile card */
.flex-module-header h3 /* title of card */
{
    color: #ff007e !important;
	transition: all 0.3s;
}
.u-textInheritColor.js-nav:hover /* name on profile card hover */
{
    color: #00fff6 !important;
    text-decoration: none;
}
.ProfileCardStats-statLabel 
{
    color: #999;
}
.account-group .username, .time, .time a, .created-via /* @ */
{
    color: #00fff6;
}
#global-tweet-dialog .modal-header, 
#Tweetstorm-dialog .modal-header,
.modal-footer
{
    border: 0px;
}
.tweet /* tweet */
{
    border: 0px;
}
.tweet:hover /* hover over tweet */
{
    background-color: #0e0e0e;
}
.fullname /* Name */
{
    color: #ff007e;
    transition: all 0.3s;
}
