css:
@-moz-document domain("github.com")
{
    .blob-code-inner {
        font-family: "Operator Mono", mono !important;
    }
    .blob-code-inner .pl-c,
    .blob-code-inner .pl-s {
        font-style: italic !important;
    }
    .CodeMirror-code .CodeMirror-line > span {
        font-family: "Operator Mono", mono !important;
    }
    .CodeMirror-code .CodeMirror-line > span .cm-comment,
    .CodeMirror-code .CodeMirror-line > span .cm-string {
        font-style: italic !important;
    }
}
