css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digg.com") {

* {
  min-width: 0px !important;
  max-width: none !important;
}

.news-body a:link{color:black; text-decoration: none}

.sidebar{display:none;}
.extra-nav{display:none;}
.sub-menu{display:none;}
.more{display:none;}
.news-details{display:none;}
.news-submitted{display:none;}

#footer, .top_ad_image, .vertical_ad_unit, .item_ad_image, .comments_ad_image, .rectangle_ad_image {
  display: none !important;
}


.main {
  margin-right: 0px !important;
margin-top: -50px;
}

.news-summary {
  width: auto !important;
}

.news-full, .comment {
  margin-right: 0px !important;
}

.extra-nav li {
  margin: 0px !important;
  padding-left: 0px !important;
  padding-right: 5px !important;
  width: auto !important; 
}
.extra-nav li a {
  margin: 0px !important;
  padding-left: 5px !important;
  padding-right: 1px !important;
  font-size: 1em !important;
  width: auto !important; 
}

.form-large * {
  width: auto !important;
  max-width: 150px !important;
}

}
