css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animeresistance.com") {
  
  body,.main, #main, #navigation .ui.inverted.menu,footer,#info #head > .overlay,.chapters select option,.chapters,.nav .container .pages-list select option,.page-wrapper
  {background: #111 !important;}
  
  .anime-video .ui.master.table .body > .row,.ui .news
  {background: transparent !important;}
  
  .chapter:nth-child(2n+1),.highlighted > .post-content,#watch > .ui.grid > .anime-video .options
  {background-color: transparent !important;}
  
  .ui.pagination.menu.master.simple .item:hover:not(.disabled),.ui.button:focus, .ui.button:hover
  {background-color: #52526a;}
  
  #navigation .ui.inverted.menu,.chapters
  {box-shadow: none !important;}
  
  #info #head > .content h1,.ui.button:focus, .ui.button:hover,.ui.anime.results .anime .info .year,table .date
  {color: #bcbcbc;}
  
  .ui.button.master.dropdown:focus, .ui.button.master:hover, .ui.header,a, body,.ui.inverted.menu .item,.chapters .container .block .title,#info #main #stats .ui.grid > .column.synopsis,.chapters select option,
  .nav .container .pages-list select option,.ui .news a,#info #main #details .ui.sections.list .item
  {color: #9898c0;}
  
  a:hover,.ui.inverted.menu a.item:hover,.chapters select option:hover,.nav .container .pages-list select option:hover,.ui .news a:hover,#info #main #details > .ui.info.list .item small,
  #info #main #details .ui.sections.list .item:hover
  {color: #aa33f0;}
  
  #info #main #details .ui.sections.list .item,.ui.grid .schedule.day .schedule.date span.split
  {border-bottom: 1px solid transparent;}
  
  .player__container
  {display: none;}
  
  
   
  

}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=animeresistance-com"),url-prefix("https://disqus.com/home/forums/animeresistance-com/"),url-prefix("https://disqus.com/by/") {
  
  .dropdown-menu,.tooltip,.tooltip-point::after, .tooltip-point::before,.home-drawer #container-main
  {background: #000 !important;}
  
  .cover
  {background: #111 !important;}
  
   .highlighted > .post-content,.drawer-footer,.btn-follow
  {background-color: #111 !important;}
  
  .button-fill:hover
  {background-color: #222 !important;}
  
  .button-fill
  {background-color: #333 !important;}
  
  .cover-nav-option.active > a::before
  {background-color: #9898c0 !important;}
  
  .upvoters-outer::after,.upvoters-outer::before
  {background-image: none !important;}
  
  .textarea-wrapper,.post-actions,.input--text,.dark .load-more__button,.home-drawer .viewport,.cover,.home-drawer #container-main > div,.card-wrap,.nav-tabs,.nav-tabs li.active a::after,.layout .more-wrapper,
  .textarea-outer-wrapper .textarea-wrapper
  {background: transparent !important;}
  
  .dropdown-menu .active > a, .dropdown-menu .active > a:hover, .dropdown-menu li > a:hover,.drawer-footer,.button-outline
  {background-color: transparent !important;}
  
  body,.textarea,.dark .tab-conversation.active > a,.dark .nav-tab > a,.card-summary-content,.dark .icon-cog, .dark .icon-export, .dark .label,.cover-profile-name,.card-reason,.card-wrap,.cover,
  .card-comment .comment-message
  {color: #bcbcbc !important;}
  
  .publisher-anchor-color a,.dark .nav-tab > a:hover,.dark .load-more__button,.link-gray-darker,.dark .label:hover,.dropdown-menu li > a:hover,.cover-profile-name:hover,
  .cover-nav-option.active > a, .no-touchevents .cover-nav-option > a:hover,a, button,.dropdown-cover-profile__title
  {color: #9898c0 !important;}
  
  .publisher-anchor-color a:hover,.dark .load-more__button:hover,.link-gray-darker:hover, .no-touch .cover-nav-option > a:hover,a:hover, button:hover,.dropdown__link:not([disabled]):hover .dropdown-cover-profile__title
  {color: #aa33f0 !important;}
  
  .textarea-wrapper,.input--text:focus,.input--text,.dropdown-menu,.textarea-outer-wrapper .textarea-wrapper
  {border: 2px solid #333 !important;}
  
  .button-outline,.no-touch .button-outline:hover, .no-touchevents .button-outline:hover
  {border-color: #484848;}
  
  .card-discussion .card-aside a.discussion-image
  {border: transparent !important;}
  
  .post-content .post-menu .dropdown-toggle,.button-fill
  {border-color: transparent !important;}
  
  .disqus-footer__wrapper,.post-actions,.drawer-footer,.cover-nav-wrapper,.drawer-footer
  {border-top: transparent !important;}
  
  .nav-primary,.home-drawer .card-profile,.home-drawer .cover-forum,.home-drawer .cover-profile, .home-drawer .notifs-fixed,.home-drawer .card-profile,.drawer-footer,.nav-tabs,
  .border-bottom, .border-bottom-dark,.nav-primary
  {border-bottom: transparent !important;}
  
  .post-votes .post-votes__separator
  {border-right: transparent !important;}
  
  .vote-down,.drawer-footer,.home-drawer #container-main
  {border-left: transparent !important;}
  
  .dropdown-menu::before
  {border-color: transparent transparent transparent;}
  
  .dark .nav-primary
  {border-bottom-color: transparent;}

}
