css:
@-moz-document 
domain("ebay.co.jp"),
domain("ebay.com"),
url("http://www.ebay.com/bhp/japan")
{


body
  {
    background: url(https://s20.postimg.cc/tjjz440el/star-wars-bgr.jpg) no-repeat !important;
    background-size: 100% !important;
    background-position: top center !important;
    background-attachment: fixed !important;
  }

  .main
  {
    background: rgba(255,255,255,0.7) !important;
  }

  .footer
  {
    background: transparent !important;
  }

  #glbfooter
  {
    background: rgba(255,255,255,0.9) !important;
  }

  #Left
  {
    float: left;
    width: 195px !important;
    background: rgba(255,255,255,0.9) !important;
    padding: 10px !important;
    margin-top: 10px !important;
  }

  #CenterPanel
  {
    background: rgba(255,255,255,0.9) !important;
    margin-top: 10px !important;
  }

  .LV #Body
  {
    background: rgba(255,255,255,0.8) !important;
  }

  #Body .vi-bc-topM, #Body #CenterPanelDF, #Body #BottomPanelDF, body .oly
  {
    font-family: Arial,Sans-Serif,Verdana;
    line-height: normal;
    font-size: 13px;
    line-height: 100%;
    background: rgba(255,255,255,0.8);
    padding: 10px;
  }
/*  #Body #gh, body #Body .gf-BIG-t
  {
    background: rgba(255,255,255,0.8) !important;
  }
*/
#CenterPanel {
    background: rgba(255,255,255,0.8) !important;
    margin-top: 10px !important;
}

div#LeftPanel > div.wrap{
    background: rgba(255,255,255,0.8);
	padding: 10px !important;
	margin-top: 10px !important;
}

#NavigationPanel .cptdtl, .mstkyhdr {
    min-height: 40px;
    position: relative;
    padding: 10px!important;
    background: rgba(255,255,255,0.9)!important;
}

.con-results, .c-std {
    border: 1px solid #ddd!important;
    background: rgba(255,255,255,0.9)!important;
}


.con-resultset .con-img, .veryspecific .con-img {
    float: left;
    width: 225px;
    height: 225px;
    left: 0;
    top: 0;
    border: 0px solid #ccc;
    background: transparent;
    position: relative;
}

.browse-widget .bw-item .item-box {
    background: rgba(255,255,255,0.9)!important;
}

.mfe-lex .mfe-card {
    position: relative;
    background-color: rgba(255,255,255,0.7) !important;
}

#ChildItems li a:link{
    color: #111!important;
}

 #ChildItems li a:visited {
    color: #555!important;   
}

#gh-la {
    display: block;
    overflow: hidden;
    position: relative;
    text-indent: -9999px;
    width: 117px;
    height: 48px;
    margin-left: 7px!important; 
}

#gh-as-a {
    font-size: 11px;
    display: block;
    line-height: 40px;
    padding: 0 10px;
    white-space: nowrap;
    color: #fff !important;
}


#gh-shop-a {
    color: #fff !important;
}
  
.b-breadcrumb {
    position: relative;
    background: rgba(255,255,255,0.8) !important;
    padding-left: 20px !important;
}


.b-sidenav {
    background: rgba(255,255,255,0.8) !important;
    padding: 10px !important;
}

.b-pageheader__text {
    font-size: 1.75rem;
    color: #fff !important;
    padding: 5px !important;
}
    


.content.w950{
        background: rgba(255,255,255,0.8) !important;
    	padding:10px !important;
}


.footer{
        background: rgba(255,255,255,0.8) !important;
}






}
