css:
@-moz-document url-prefix("https://mail.google.com/") {

/* Gmail Inbox alt Contrast */
/* Version 1.0 */
/* Author: nicholeknc */
/* 02/23/2018 */


    /* read */  
    .yO {
        background-color: rgba(220, 222, 228, 1.00) !important;
        color: rgba(0, 0, 0, 1.00) !important;
    }
    /* unread */
    .zE {
        background-color: rgba(250, 250, 250, 1.00) !important;
        color: rgba(0, 0, 0, 1.00) !important;
    }
    /* all */
    .zA:hover {
        background-color: rgba(147, 194, 224, 1.00) !important;
        color: rgba(254, 254, 254, 1.00) !important;
    }

}
