css:
@-moz-document domain("www.cavelis.net") {
body {
    background-color: #000000;/*上部背景色*/
    color: #8b7e60;/*文字色*/
}
#header_wrapper header #logged_menu>a:link, #header_wrapper header #login_menu a.signin:link, #header_wrapper header #login_menu a.mypage:link, #header_wrapper header #head_menu_tooltip_container .qtip .user_menu a:link, #menu_wrapper>div nav>a:link, #header_wrapper header #logged_menu>a:visited, #header_wrapper header #login_menu a.signin:visited, #header_wrapper header #login_menu a.mypage:visited, #header_wrapper header #head_menu_tooltip_container .qtip .user_menu a:visited, #menu_wrapper>div nav>a:visited{
    color: #00bfff;/*ユーザー名色*/
}

.qtip.qtip-default {
    background-color: #000000;/*マウスオーバーメニュー背景色*/
}

#footer_wrapper>div{
     background: #333333;/*下部背景色*/
}

#body_wrapper {
    background-color: #212121;/*元薄緑のところ*/
}
#body_wrapper a:link { 
    color: #00bfff; /*未訪問のリンク*/
}
#body_wrapper a:visited {
    color: #cd5c5c; /*訪問済みのリンク*/
}
#body_wrapper a:hover { 
    color: #ffffff;/*ポイント時のリンク*/
    background-color:#333333;/*ポイント時の背景*/
}

#body_wrapper #body>div #watch_area{
    background-color: #212121;/*プレイヤー枠色*/
}

#amazon_affiliate_area{
    display: none !important;/*アマゾンアフィリエイト非表示*/
}

#notify_area{
    background-color: #212121;/*来場者等表示エリア*/
}

