css:
@-moz-document regexp("|^.*:8080/tfs/.*$|") {
#navWrapper {background-color: #333 !important; }
.header-post11 .hub-groups-section li:not(.selected) a, .header-post11 .hubs li:not(.selected) a, .hub-pivot li:not(.selected) a, .pivot-filter .title {color: #ddd !important;}
.header-post11 .hub-groups-section li.selected a, .header-post11 .hubs li.selected a, .hub-content a.node-link, .recycle-bin .content, .hub-pivot li.selected a, .pivot-filter a.selected, .capacity-days-off, .workitem-info-bar a.caption {color: #7db7ff !important;}
.header-post11 .hub-groups-section li a:hover, .header-post11 .hubs li a:hover, .hub-pivot li a:hover {border: none !important; text-shadow: -1px 1px 2px #35a, 1px -1px 2px #35a, -1px -1px 2px #35a, 1px 1px 2px #35a, 0 0 4px #fff !important; filter: brightness(125%) !important;}
.header-search {filter: invert(100%) brightness(125%) !important;}
.content-section, .recycle-bin, .handleBar {background-color: #222 !important; color: #fff !important; border-color: #444 !important;}
.hub-content li.node.focus a.node-link:hover .node-content, .hub-content li.node.selected a.node-link .node-content, .menu-popup .menu-item .sub-menu .menu-item:hover, .grid-row-selected-blur, .grid-row:hover, .state {background-color: #004878 !important; color: #fff !important;}

.hub-content li.folder a[aria-level="1"] .node-content, .hub-title, .hub-title-right {color: #fff !important;}
.hub-title-right-secondary, label {color: #bbb !important;}
.burndown-chart, .hub-content .icon-only .icon, .hub-content .toggle-button .icon, .taskboard-expander .maximize, .icon-add, .add-panel-close-button, .capacity-expand-collapse, .add-icon-cell, .grid-tree-icon, .ui-icon-gripsmall-diagonal-se, .toolbar .icon, .ui-icon, span.drop, .query-folder-tree .icon, .node-img {filter: invert(100%) hue-rotate(180deg) brightness(250%) !important;}
.recycle-bin-icon, .richeditor-toolbar .icon, .menu-bar .icon, .icon-pin, .velocity-chart, .cumulative-flow-chart {filter: invert(100%) hue-rotate(180deg) !important;}

.menu-popup .menu-item  {background-color: #333 !important;}
.menu-popup .menu-item .menu {background-color: #222 !important; border: none !important;}
.menu-item .text {text-decoration: none !important;}
.dropdown.active {border-color: transparent !important; background-color: #444 !important;}
.hub-pivot-content {background-color: #222 !important;}
.taskboard-cell, .taskboard-expander {border-color: #444 !important;}
#taskboard-table-header {background-color: #444 !important;}
.taskboard-cell .witState, .tbTile .title, .ui-button-text {color: #fff !important;}
.taskboard-cell .witRemainingWork {color: #aaa !important;}
.work-item-count, .ellipsis:not(.title) {color: #7db7ff !important;}
.tbTile .tbTileContent, .tbTile.tile-dimmed .tbTileContent {background-color: #444 !important; border-color: #111 !important;}
.tbTile.tile-dimmed .tbTileContent {opacity: .5 !important;}
.tbTile.tile-dimmed:hover .tbTileContent {opacity: 1 !important;}
.add-new-item {background-color: #444 !important;}
.board-add-card {border-color: #111 !important; background-color: #444}
.taskboard .taskboardTableBodyScrollContainer #taskboard-table-body, .taskboard-content-row .taskboard-expander {background-color: #222 !important;}
.taskboard-row:hover {background-color: #333 !important;}
.taskboard-content-row .taskboard-parent, .grid-gutter-row-normal {background-color: transparent !important;}
.backlog-header {background-color: #333 !important;}
.toolbar {border-color: transparent !important;}
span.text, .grid-row {color: #fff !important;}
.backlog-header .menu-item.toggle-on, .add-panel {background-color: #444 !important;}
.backlog-header .menu-item:hover {background-color: #444 !important;}
.productbacklog-grid-results {background-color: #111 !important;}
.grid-header {background-color: #444 !important; color: #fff !important;}
.grid-header-column {background: transparent !important;}
.workitem-dialog.ui-dialog, .ui-dialog-buttonpane, .combo {background-color: #222 !important; border-color: #444 !important;}
input {background-color: #111 !important;color: #ddd !important;}
.ui-tabs .ui-tabs-nav {background-color: #222 !important;}
.ui-widget-content, .ui-tabs .ui-tabs-nav li.ui-tabs-active a, legend {color: #fff !important;}
.ui-tabs .ui-tabs-nav li:not(.ui-tabs-active) a, .transition-container {color: #aaa !important;}
.ui-state-disabled, .ui-widget-content .ui-state-disabled, .ui-widget-header .ui-state-disabled {background-color: #444 !important; border-color: #666 !important; opacity: .25 !important;}
button, button.ui-state-default, button.ui-button {background-color: #444 !important; border-color: #666 !important;}
.grid-gutter-row-normal {border-color: transparent !important;}
.agile-board .horizontal .horizontal-table > .row.content, .combo-drop-popup {background-color: #222 !important;}
.agile-board .horizontal .horizontal-table > .row > .cell {border-color: #444 !important;}
.agile-board .board-tile .board-tile-content-container {background-color: #333 !important;}
.agile-board .board-tile .board-tile-content-container .board-tile-content-bottom-border {border-color: #111 !important;}
.agile-header-container {background-color: #444 !important;}
.agile-header-container .header {color: #fff !important;}
iframe {background-color: #fff !important;}
li.selected {background-color: #35a !important}

}
