css:
@-moz-document domain("ect-ua.com") {
  html, body {
   color: #333;
   background-color: black;
  }

  .headerbar {
    background-color: black;
    margin-top: 45px;
  }

  .navbar-top {
    background-color: orange;
    font-weight: bold;
  }

  .header-avatar span {
    margin-right: 15px;
  }
  
  .navbar ul.linklist {
    box-shadow: 0px 0 20px black;
    background-color: orange;
    padding: 2px 10px;
    margin-top: -4px;
    width: 100%;
    margin-left: -10px;
  }
   
  #nav-main {
    position: fixed;
    width: 100%;
    background-color: orange;
    padding-top: 28px;
    margin-top: -3px;
    display: inline-block;
    text-align: center;
    z-index: 1;
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
    margin-left: -25px;
  }
  .site-description h1:hover {
    color: orange;
    text-shadow: 0px -5px 6px black;
  }
  .site-description h1 {
    color: #333;
  } 
  .icon {
    margin-left: 15px;
    color: brown;
   }
  .fa-pencil:after {
    margin-left: -72px;
  }

  .navbar-top a, .navbar-bottom a {
    text-decoration: none;
    text-shadow: none;
    color: brown;
  }

  .wrap {
    margin-top: 40px;
    box-shadow: 0px 0 45px white;
    background-color: orange;
  }

  .navbar {
    background-color: black;
  }

  .header-avatar span {
    color: black !important;
  }
   .site-description {
     width: 100%;
     background-color: aliceblue;
   }

  a.forumtitle {
    color: brown;
  }

  a.forumtitle:hover {
    text-decoration: none;
  }

  ul.topiclist dd {
    font-family: sans-serif;
  }

  ul.topiclist dl {
    box-shadow: 0px 0 20px black;
  }

  a {
    color: brown;
  }

  a.lastsubject:hover {
    text-decoration: underline;
    color: #1370b2;
  }
  ul.topiclist.two-columns dt .list-inner {
    margin-right: 80px;
    color: black;
  }
  .autowidth {
    width: 60% !important;
  }
   .panel-container .panel li.header dd, .panel-container .panel li.header dt {
      color: black;
    }
   .ul.topiclist .dl {
    box-shadow: none;
  }
  .navbar ul.linklist {
    box-shadow: 0px 0 20px black;
    background-color: black;
    padding: 2px 10px;
    margin-top: -4px;
    width: 100%;
    margin-left: -10px;
  }
  .button {
    border-color: white;
    background-color: gray;
  }
  .button .icon {
    color: black;
  }
  .search-box .inputbox, .search-box .inputbox:hover, .search-box .inputbox:focus {
    border-color: white;
  }
  .search-box .inputbox {
    background-color: lightgray;
  }
  #nav-footer {
    margin-bottom: 10px;
  }
  .button .button-search:hover {
    background-color: black;
  }
  a.button.button-icon-only[href*=report]:hover {
    filter: invert(100%) brightness(50%);
  }
  a.button.button-icon-only[href*=report]:after {
    visibility: visible;
    content: url(https://u.imageresize.org/ef0323e7-b214-4588-a123-ea5549ca04a1.png);
  }
  a.button.button-icon-only[href*=report] {
    visibility: hidden;
    margin-right: -16px;
  }
  .fa-pencil:before:hover {
    filter: invert(100%) brightness(50%);
  }
  .fa-pencil:after {
    visibility: visible;
    content: url(https://u.imageresize.org/c52bae9b-1962-4f69-8c85-2c434a39eb17.png);
    margin-right: -57px;
  }
  .fa-pencil {
    visibility: hidden;
  }
 i.icon.fa-quote-left.fa-fw:hover {
    filter: invert(100%) brightness(50%);
  }
  i.icon.fa-quote-left.fa-fw:after {
    visibility: visible;
    content: url(https://u.imageresize.org/7f36f8f2-7d8a-49c2-96e0-d3d5b8252955.png);
    margin-left: -45px;
  }
  i.icon.fa-quote-left.fa-fw {
    visibility: hidden;
  }
   i.icon.fa-smile-o.fa-fw:hover {
    filter: invert(100%) brightness(50%);
  }
  i.icon.fa-smile-o.fa-fw:after {
    visibility: visible;
    content: url(https://u.imageresize.org/00f4cb70-e99e-4731-8a6a-6132d951beea.png);
  }
  i.icon.fa-smile-o.fa-fw {
    visibility: hidden;
  }
  i.icon.fa-sort-down.fa-fw {
    visibility: hidden;
  }
 .post-buttons .button, .format-buttons .button {
    padding-left: 3px;
    padding-right: 3px;
    visibility: hidden;
    margin-right: 10px;
 }
 a.button.button-icon-only[href*=report]:after {
    margin-left: 0px;
 }
 .fa-pencil:after {
    margin-left: 0px;
 }
 i.icon.fa-quote-left.fa-fw:after {
    margin-left: 0px;
 }
 i.icon.fa-smile-o.fa-fw:after {
 }
 .dropdown .pointer-inner {
    border-color: black transparent;
    margin-left: -31px;
    margin-bottom: 0px;
}
 .contact-icons a:hover {
    background-color: white;
 }
 .dropdown .dropdown-contents {
    background: #f4a742;
    border-color: black;
    border: 2px solid;
 }
 .dropdown .pointer {
    border-color: transparent;
 }
 .dropdown {
    margin-top: 6px;
 }
 .dropdown .pointer-inner {
    border-color: black transparent;
    margin-left: -31px;
 }
 .fa-pencil:after {
    visibility: hidden;
 }
 .button span {
     margin-right: 0px;
 }
}
