css:
@-moz-document url-prefix(http://manga-town.net/s/){
* {
    color:white !important;
}
body, h1, h2, li {
background-color:#010101;
    border: 0px solid #000;
}
#side-menu li a {
    background: #fff3 none repeat scroll 0 0;
    color: #010101;
    display: block;
    font-size: 15px;
    line-height: 24.2px;
    padding: 10px 0 10px 10px;
    text-align: left;
    text-decoration: none;
}
  #side-menu li#detail img {
    margin: 0 auto;
    vertical-align: top;
    width: 177px;
}
#side-menu li#detail #manga-title {
    border:0px solid #000;
    color: #000;
    display: block;
    font-size: 14px;
    margin: 10px;
    text-align: center;
}
#side-menu li#logo img {
    height: 20px;
    vertical-align: top;
}
  input[type="range"]::-moz-range-track {
    background: #fff none repeat scroll 0 0;
    border: 1px solid #000;
    border-radius: 5px;
    box-shadow: 1px 1px 1px #000;
    cursor: pointer;
    height: 7.5px;
    width: 100%;
}
  input[type="range"]::-moz-range-thumb {
    background: #fff none repeat scroll 0 0;
    border: 1px solid #000;
    border-radius: 30px;
    box-shadow: 1px 1px 1px #000;
    cursor: pointer;
    height: 40px;
    width: 10px;
}
  a#menu-btn, a#close-btn {
    background: #000 none repeat scroll 0 0;
    border-radius: 50px;
    color: #000;
    font-size: 15px;
    height: 30px;
    line-height: 0;
    padding: 5px;
    position: fixed;
    text-align: center;
    top: 10px;
    width: 30px;
    z-index: 1;
}
    footer {
    background: #010101 none repeat scroll 0 0;
    border-top: 1px solid #eaeaea;
    padding: 20px 0;
    }
    #read {
    background: #000 none repeat scroll 0 0;
}
        img.lazy.manga{
	max-width: 1px;
	max-height: 1px;
			margin:0px 940px;
    }
}
