css:
@-moz-document url-prefix(http://manga-town.net){
 h1,h2 {
  font-size:21px;
 font-weight:700;
 text-align:center;
 margin:0 0 10px;
 border-top:1px solid #eaeaea;
 border-bottom:1px solid #eaeaea;
 background:#fafafa;
 padding:5px 10px
}
  h1 i,h2 i {
 color:black
}
  #read {
 min-width:238px;
 float:right;
 border:1px solid #ccc;
 border-radius:5px;
 padding:0
}
  #readtitle,#sharetitle {
 padding:5px;
 background:#f1f1f1;
 border-bottom:1px solid #ccc;
 border-radius:5px 5px 0 0;
 text-align:center;
 font-size:16px;
 font-weight:700;
 color:#000
}
  #thumbnail img,.thumbnails img {
 width:100%;
 margin:0;
 padding:0;
 vertical-align:bottom;
 border:1px solid #fff
}
  #data ul,#data li {
 margin:0;
 padding:0;
 font-size:16px
}
  #data li a {
 font-size:12px;
 padding:5px 10px;
 margin:5px;
 text-align:center;
 border:1px solid #fff0;
 border-radius:30px;
 font-weight:700;
 background:#bbb;
 color:#333;
 text-decoration:none;
 display:inline-block
}
  #data li a i {
 color:#000
}
  a.btn {
 display:block;
 margin:15px;
 border:1px solid #5D627B;
 border-radius:5px;
 background:#333;
 color:#FFF;
 text-decoration:none;
 text-align:center;
 padding:10px 0
}
  h1 img.dh {
 height:21px;
 vertical-align:text-top
}
  .c div.comment,div.links a {
 font-size:15px;
 padding:5px 0;
 margin:0 auto 5px;
 text-align:center;
 border:1px solid #fafafa;
 border-radius:3px;
 font-weight:700;
 background:#fafafa;
 color:#444;
 text-overflow:ellipsis;
 overflow:hidden;
 white-space:nowrap;
}
  div .links a i {
 color:#bb0000
}
  html.wf-fontawesome-n4-active.wf-active body div#content div#main-content h1 div.links a i.fa.fa-arrow-circle-right.fa-fw{
    color:#000000;
  }
  html.wf-fontawesome-n4-active.wf-active body div#content div#main-content h1 div.links span a i.fa.fa-th.fa-fw{
    color:#000000;
  }
  html.wf-fontawesome-n4-active.wf-active body div#content div#main-content h1 div.links a i.fa.fa-chevron-circle-up.fa-fw{
    color:#000000;
  }
  html.wf-fontawesome-n4-active.wf-active body div#content div#side-content div#updates p{
    visibility:hidden
  }
  #side-menu li a {
    background: #fff3 none repeat scroll 0 0;
    color: #fefefe;
    display: block;
    font-size: 15px;
    line-height: 24.2px;
    padding: 10px 0 10px 10px;
    text-align: left;
    text-decoration: none;
}
  #side-menu li#detail img {
    margin: 0 auto;
    vertical-align: top;
    width: 177px;
}
#side-menu li#detail #manga-title {
    color: #fff;
    display: block;
    font-size: 14px;
    margin: 10px;
    text-align: center;
}
#side-menu li#logo img {
    height: 20px;
    vertical-align: top;
}
  input[type="range"]::-moz-range-track {
    background: #fff none repeat scroll 0 0;
    border: 1px solid #000;
    border-radius: 5px;
    box-shadow: 1px 1px 1px #000;
    cursor: pointer;
    height: 7.5px;
    width: 100%;
}
  input[type="range"]::-moz-range-thumb {
    background: #fff none repeat scroll 0 0;
    border: 1px solid #000;
    border-radius: 30px;
    box-shadow: 1px 1px 1px #000;
    cursor: pointer;
    height: 40px;
    width: 10px;
}
}
