css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.hi-pda.com"){
#footer,.channelinfo,#modedby,#ad_text,tbody[id^="stickthread_"],h2 img,#visitedforums,.pageback,.pages a.prev{display:none !important}
body,#nav,.content,#header,#wrap,.pages a, .pageback a,.colplural, .colplural th, .colplural td, th.highlight, td.highlight,
#menu li,.mainbox td.postauthor,.editor_tb,#f_post .txtarea,#fastpostsubmit,#e_textarea,.txtarea,.newediter,input,select,button,.list td, .list th,.searchform,.editorrow,.forumcontrol table,.forumcontrol .modaction{background: #c5e0c8 !important;}
.threadtype,.postinfo,posterinfo,authorinfo,.postactions,#threadtitle,.list td, .list th,.threadtype a,.editor a,.searchform，.datalist .datatable, .datalist .datatable th, .datalist .datatable td,.list, .threadtype strong，.postinfo strong a,.postinfo strong a {border-color: #c5e0c8 !important;}
.adcontent,.threadad td.postauthor{background: #E3EDCD !important;}
#nav, #nav a,#footer, #footer a,#fjump_menu li a{color: #000 !important;}
#menu li.current a,.pages strong,.itemfilter .current a,#menu a:hover,.itemfilter .current span{background: #00B271 !important;}
.popupmenu_popup{background: #E8FFE8 !important;}
.userinfopanel{margin-left:0 !important}
.quote{background-color: #FAF9DE !important;}
.pstatus, .modact {background-color: #f0dad2 !important;}
.dropmenu{padding-right: 14px !important;}
#header .wrap h2 a{display: block !important;;height: 126px !important;width: 126px;background: url(https://img02.hi-pda.com/forum/attachments/day_150125/1501251334640622f9e5200fbc.png)  no-repeat scroll 0 0 rgba(0, 0, 0, 0) !important;}
#header .wrap {padding: 24px 0 22px 40px !important;}
#menu{right: 45px !important;}
#umenu {right: 45px !important;;top: 10px !important;}
.locked{background-color: #E8D098 !important;}
.popavatar{width:140px !important;padding-left:9px}
.postauthor .avatar,.popavatar img,.profile_side .avatar {margin: 10px 20px;position: relative;width: 120px;height: 120px;overflow: hidden;border-radius:50%}
.popavatar .profile_side{padding-left:10px}
#postlist{border-top: 2px solid #e3edcd !important;}
.popavatar img,.avatar img,.profile_side img{width:120px;height:120px;margin:0}
.forumcontrol #newspecial img,.forumcontrol #post_reply img,.pages_btns #newspecial img{visibility: hidden !important;}
.forumcontrol #newspecial a,.pages_btns #newspecial a {background: url(https://img02.hi-pda.com/forum/attachments/day_180225/18022513549bf1857c202be2c6.png) no-repeat 0 0;height: 31px;width:90px;display: inline-block;}
.forumcontrol #post_reply a{background: url(https://img02.hi-pda.com/forum/attachments/day_180225/1802251354450b4da541ec135a.png) no-repeat 0 0;height: 31px;width:90px;display: inline-block;}
.signatures a{color: #777 !important;}
.signatures{color: #777 !important;}
}
