css:
@-moz-document domain("whateleyacademy.net") {
body.site.fluid {
    background-color: #1A1A1A;
    color: #CCC;
}

/*************************/
/***    Page Header    ***/
/*************************/
.brand {
    color: #CCC;
}
.brand:hover {
    color: #85c2e0;
}

/*************************/
/***  Side Info Boxes  ***/
/*************************/
.well {
    background: #333;
    /* Old browsers */
    background: -moz-linear-gradient(top, #555 0px, #333 200px);
    /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #555 0px, #333 200px);
    /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #555 0px, #333 200px);
    /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    box-shadow: 0px 6px 12px #000;
    border: 2px solid #999;
}

/*************************/
/***       Links       ***/
/*************************/
a {
    color: #c4a37e;
}
a:hover {
    color: #6ba8c7;
}
.nav-tabs > .active > a {
    color: #CCC;
}
.nav-tabs > .active > a:hover {
    border-width: 3px;
}

/*************************/
/*** Story List Tables ***/
/*************************/
.table-striped tbody > tr:nth-child(odd) > td, .table-striped tbody > tr:nth-child(odd) > th {
    background-color: #2A2A2A;
}
.table-hover tbody tr:hover > td, .table-hover tbody tr:hover > th {
    background-color: #0a0a0a;
}

/*************************/
/***    Pagination     ***/
/*************************/
.pagination ul > li > a, .pagination ul > li > span {
    background-color: #2a2a2a;
}
.pagination ul > li > a:hover, .pagination ul > li > a:focus, .pagination ul > .active > a, .pagination ul > .active > span {
    background-color: #1a1a1a;
}
}
